const layoutProps = {

};
const MDXLayout = "wrapper"
export default function MDXContent({
  components,
  ...props
}) {
  return <MDXLayout {...layoutProps} {...props} components={components} mdxType="MDXLayout">
    <p>{`OAUTH AND PASSPORT FOR EXPRESS`}</p>
    <p>{`/////////////////////////////////////////////////////////`}</p>
    <p>{`/`}<strong parentName="p"><strong parentName="strong"><strong parentName="strong"><strong parentName="strong"><strong parentName="strong"><strong parentName="strong">{`****`}</strong></strong></strong></strong></strong></strong></p>
    <ul>
      <li parentName="ul"><pre parentName="li"><code parentName="pre" {...{}}>{`                    *
`}</code></pre><ol parentName="li">
          <li parentName="ol">{`What is OAuth?`}</li>
        </ol></li>
      <li parentName="ul"><pre parentName="li"><code parentName="pre" {...{}}>{`                    *
`}</code></pre><strong parentName="li"><strong parentName="strong"><strong parentName="strong"><strong parentName="strong"><strong parentName="strong"><strong parentName="strong">{`****`}</strong></strong></strong></strong></strong></strong>{`/`}</li>
    </ul>
    <p>{`It is authorization, not authentication.`}</p>
    <p>{`Authorization is used for what you can do.`}</p>
    <p>{`You usually get tokens...`}</p>
    <ul>
      <li parentName="ul">{`Access Token`}</li>
      <li parentName="ul">{`Refresh Token`}</li>
    </ul>
    <p>{`/`}<strong parentName="p"><strong parentName="strong"><strong parentName="strong"><strong parentName="strong"><strong parentName="strong"><strong parentName="strong">{`****`}</strong></strong></strong></strong></strong></strong></p>
    <ul>
      <li parentName="ul"><pre parentName="li"><code parentName="pre" {...{}}>{`                    *
`}</code></pre><ol parentName="li" {...{
          "start": 2
        }}>
          <li parentName="ol">{`Setting up Passport`}</li>
        </ol></li>
      <li parentName="ul"><pre parentName="li"><code parentName="pre" {...{}}>{`                    *
`}</code></pre><strong parentName="li"><strong parentName="strong"><strong parentName="strong"><strong parentName="strong"><strong parentName="strong"><strong parentName="strong">{`****`}</strong></strong></strong></strong></strong></strong>{`/`}</li>
    </ul>
    <p>{`// THE SETUP STRATERGY`}</p>
    <ol>
      <li parentName="ol">{`Install Strategy`}</li>
      <li parentName="ol">{`Create 'Application' on OAuth Provider`}</li>
      <li parentName="ol">{`Configure Strategy`}</li>
      <li parentName="ol">{`Create routes for the provider`}</li>
    </ol>
    <p>{`// installing Github auth`}</p>
    <p>{`npm install passport-github --save`}</p>
    <ul>
      <li parentName="ul">{`from here you can register the application on Github in settings`}</li>
      <li parentName="ul">{`grab tokens`}</li>
    </ul>
    <p>{`|||||||||||||||||||||||||||||||||||||||||||||`}</p>
    <p>{`npm install passport --save
npm install express-session --save`}</p>
    <p>{`We'll use persistent session store with mongoDB to keep the login persistent`}</p>
    <p>{`// in app.js`}</p>
    <p>{`var passport = require('passport');
var GitHubStrategy = require('passport-github').Strategy;
var FacebookStrategy = require('passport-facebook').Strategy;
var session = require('express-session');
var MongoStore = require('connect-mongo')(session);`}</p>
    <p>{`function generateOrFindUser (accessToken, refreshToken, profile, done) {
User.findOneAndUpdate({
email: profile.emails`}{`[0]`}{`.value
}, {
name: profile.displayName || profile.username,
email: profile.emails`}{`[0]`}{`.value,
photo: profile.photos`}{`[0]`}{`.value
}, {
upsert: true
},
done);
} else {
var noEmailError = new Error("Your email privacy settings prevent you from signing into bookworm!");
done(noEmailError, null);
}
}`}</p>
    <p>{`// config the strategy
passport.use(new GitHubStrategy({
// do not enter your actual ID and Key here!
clientID: process.env.GITHUB_CLIENT_ID,
clientSecret: process.env.GITHUB_CLIENT_SECRET,
callbackURL: "`}<a parentName="p" {...{
        "href": "http://localhost:3000/auth/github/return"
      }}>{`http://localhost:3000/auth/github/return`}</a>{`"
}, generateOrFindUser));`}</p>
    <p>{`passport.use(new FacebookStrategy({
// do not enter your actual ID and Key here!
clientID: process.env.FACEBOOK_APP_ID,
clientSecret: process.env.FACEBOOK_APP_SECRET,
callbackURL: "`}<a parentName="p" {...{
        "href": "http://localhost:3000/auth/facebook/return%22"
      }}>{`http://localhost:3000/auth/facebook/return"`}</a>{`,
profileFields: `}{`['id', 'displayName', 'photos', 'email']`}{`
}), generateOrFindUser);`}</p>
    <p>{`passport.serializeUser(function (user, done) {
done(null, user._id);
});`}</p>
    <p>{`passport.deserializeUser(function(userId, done) {
User.findById(userId, done); // done is a callback function
});`}</p>
    <p>{`...`}</p>
    <p>{`var auth = require('./routes/auth');`}</p>
    <p>{`...`}</p>
    <p>{`// after db connection
// Session config for Passport and MongoDB`}</p>
    <p>{`var sessionOptions = {
secret: "this is a super secret dadada",
resave: true,
saveUninitialized: true,
store: new MongoStore({
mongooseConnection: db
})
}`}</p>
    <p>{`app.use(session(sessionOptions));`}</p>
    <p>{`// Init Passport
app.use(passport.initialize());`}</p>
    <p>{`// Restore Session
app.use(passport.session());`}</p>
    <p>{`app.use('/auth', auth);`}</p>
    <p>{`// in routes > auth.js`}</p>
    <p>{`var express = require('express');
var router = express.Router();
var passport = require('passport');`}</p>
    <p>{`// Get /auth/login/github`}</p>
    <p>{`router.get('/login/github',
passport.authenticate('github'));`}</p>
    <p>{`// Get /auth/github/return`}</p>
    <p>{`router.get('/github/return',
passport.authenticate('github', {failedRedirect: '/'}),
function(req, res) {
//Succes Auth, redirect to page for now
res.redirect('/profile');
});`}</p>
    <p>{`// Get /auth/login/facebook`}</p>
    <p>{`router.get('/login/facebook',
passport.authenticate('facebook', { scope: `}{`["email"]`}{`}));`}</p>
    <p>{`// Get /auth/facebook/return`}</p>
    <p>{`router.get('/facebook/return',
passport.authenticate('facebook', {failedRedirect: '/'}),
function(req, res) {
//Succes Auth, redirect to page for now
res.redirect('/profile');
});`}</p>
    <p>{`// GET /auth/logout`}</p>
    <p>{`router.get('/logout', function(req, res) {
req.logout();
res.redirect('/');
})`}</p>
    <p>{`module.exports = router;`}</p>
    <p>{`// to use vars in command line...`}</p>
    <blockquote>
      <p parentName="blockquote">{`GITHUB_CLIENT_ID=123432 GITHUB_CLIENT_SECRET=892u3rw9u ./bin/www`}</p>
    </blockquote>
    </MDXLayout>;
}

MDXContent.isMDXComponent = true;
