    .select('body')
    .append('svg')
    .attr('id', 'chart')
    .attr('height', h)
    .attr('width', w);

function plot(params) {
    // creating the bars
    // vertical bar graph
    this.selectAll('.bar')
        .data(params.data)
        .enter() // enter phase
        .append('rect')
        .attr('class', 'bar') // for future selections
        .attr('x', 0)
        .attr('y', (d, i) => {
            return y(i);
        })
        .attr('width', (d, i) => {
            return x(d); // x() does the scaling
        })
        .attr('height', (d, i) => {
            return y(1) - 1;
