  import React from 'react'
  import { MDXTag } from '@mdx-js/tag'


export default class MDXContent extends React.Component {
  constructor(props) {
    super(props)
    this.layout = null
  }
  render() {
    const { components, ...props } = this.props

    return <MDXTag
             name="wrapper"

             components={components}><MDXTag name="h1" components={components} props={{"id":"puppeteer-recorder"}}>{`Puppeteer Recorder`}</MDXTag>
<MDXTag name="p" components={components}>{`Use this to convert a db.json events file into someting usable with puppeteer.`}</MDXTag>
<MDXTag name="h2" components={components} props={{"id":"install"}}>{`Install`}</MDXTag>
<MDXTag name="p" components={components}>{`Currently this is a lib file that needs to be installed using kratos.`}</MDXTag>
<MDXTag name="h2" components={components} props={{"id":"usage"}}>{`Usage`}</MDXTag>
<MDXTag name="p" components={components}>{`Use the `}<MDXTag name="inlineCode" components={components} parentName="p">{`Emitter`}</MDXTag>{` common JS class to handle this. Ensure you sub to the event `}<MDXTag name="inlineCode" components={components} parentName="p">{`action`}</MDXTag>{`.`}</MDXTag>
<MDXTag name="pre" components={components}><MDXTag name="code" components={components} parentName="pre" props={{"className":"language-javascript"}}>{`// Example of emission with type inferred
handlePrimaryClick = (e) => {
    Emitter.emit('action', {
        event: 'ComponentALLandingOne.handlePrimaryClick',
        e: e.target
    });

    const { router } = this.props;
    router.push('/testTwo');
};

// Example of emission with type manually specified
handleLink = (e) => {
    e.preventDefault();
    Emitter.emit('action', {
        event: 'ComponentALLandingOne.handleLink',
        type: 'link',
        e: e.target
    });

    const { router } = this.props;
    router.push('/testTwo');
};
`}</MDXTag></MDXTag>
<MDXTag name="h2" components={components} props={{"id":"generating-the-tests"}}>{`Generating the tests`}</MDXTag>
<MDXTag name="p" components={components}>{`The recorder relies on JSON DB and using the `}<MDXTag name="inlineCode" components={components} parentName="p">{`events`}</MDXTag>{` endpoint.`}</MDXTag>
<MDXTag name="pre" components={components}><MDXTag name="code" components={components} parentName="pre" props={{}}>{`# usage
node path/to/recorder
`}</MDXTag></MDXTag>
<MDXTag name="p" components={components}>{`The console log output for this guy can then be transferred to a test file to use with Puppeteer.`}</MDXTag>
           </MDXTag>
  }
}
