  import React from 'react'
  import { MDXTag } from '@mdx-js/tag'


export default class MDXContent extends React.Component {
  constructor(props) {
    super(props)
    this.layout = null
  }
  render() {
    const { components, ...props } = this.props

    return <MDXTag
             name="wrapper"

             components={components}><MDXTag name="hr" components={components}></MDXTag>
<MDXTag name="p" components={components}>{`Author: Dennis O'Keeffe`}</MDXTag>
<MDXTag name="h2" components={components} props={{"id":"section-testing"}}>{`Section: Testing`}</MDXTag>
<MDXTag name="h1" components={components} props={{"id":"mocha-and-chai"}}>{`Mocha and Chai`}</MDXTag>
<MDXTag name="h2" components={components} props={{"id":"resources"}}>{`Resources`}</MDXTag>
<MDXTag name="ul" components={components}>
<MDXTag name="li" components={components} parentName="ul"><MDXTag name="a" components={components} parentName="li" props={{"href":"https://mochajs.org/"}}>{`Mocha Website`}</MDXTag></MDXTag>
<MDXTag name="li" components={components} parentName="ul"><MDXTag name="a" components={components} parentName="li" props={{"href":"http://www.chaijs.com/api/"}}>{`Chai API`}</MDXTag></MDXTag>
<MDXTag name="li" components={components} parentName="ul"><MDXTag name="a" components={components} parentName="li" props={{"href":"http://airbnb.io/enzyme/docs/api/"}}>{`Enzyme API`}</MDXTag></MDXTag>
<MDXTag name="li" components={components} parentName="ul"><MDXTag name="a" components={components} parentName="li" props={{"href":"https://github.com/GoogleChrome/puppeteer/blob/master/docs/api.md"}}>{`Puppeteer API`}</MDXTag></MDXTag>
</MDXTag>
<MDXTag name="h2" components={components} props={{"id":"usage"}}>{`Usage`}</MDXTag>
<MDXTag name="p" components={components}>{`Testing is done using Mocha and Chai for evaluation and uses Enzyme for rendering out React components to evaulate state for the React app at any given time and Puppeteer to evaulate UI testing.`}</MDXTag>
<MDXTag name="p" components={components}>{`The app itself can be developed using both Behaviour Driven Development and Test Drive Development.`}</MDXTag>
<MDXTag name="p" components={components}>{`We use Mocha as the main framework to layout the test suites and test specs themselves, and to evaluate the individual test specs, we use the Chai library.`}</MDXTag>
<MDXTag name="p" components={components}>{`Basic example:`}</MDXTag>
<MDXTag name="pre" components={components}><MDXTag name="code" components={components} parentName="pre" props={{"className":"language-javascript"}}>{`// Example function to test
function add(x, y) {
    return x + y;
}

// Test suite
describe('JavaScript functionality for a file', () => {
    // Test spec
    it('expects true to be true', () => {
        expect(true).to.be.true; // evaluates to true - test passes
    });

    // Test spec
    it('expects 1 + 2 to equal 3', () => {
        const result = add(1, 2);
        expect(result).to.equal(3); // evaluates to true - test passes
    });
});
`}</MDXTag></MDXTag>
<MDXTag name="p" components={components}>{`For best practise, when testing classes and functions which comprise of straight Javascript (no React element ie js lib subfolders, utilities and helpers), just use Mocha and Chai to evaluate an expectation.`}</MDXTag>
<MDXTag name="p" components={components}>{`To incorporate BDD in this scenariom you can use Mocha's `}<MDXTag name="inlineCode" components={components} parentName="p">{`it`}</MDXTag>{` function to also scaffold the pending tests in advance that will result in "pass" or "fail" but be ready for when you start writing the evaluation and assertion expecations:`}</MDXTag>
<MDXTag name="pre" components={components}><MDXTag name="code" components={components} parentName="pre" props={{"className":"language-javascript"}}>{`// Test suite
describe('JavaScript functionality for a file', () => {
    // Test spec
    it('expects true to be true'); // Pending test - won't evaluate as a pass or fail
});
`}</MDXTag></MDXTag>
           </MDXTag>
  }
}
