/* @jsx mdx */
  import React from 'react'
  import { mdx } from '@mdx-js/react'
  /* @jsx mdx */


const makeShortcode = name => function MDXDefaultShortcode(props) {
  console.warn("Component " + name + " was not imported, exported, or provided by MDXProvider as global scope")
  return <div {...props}/>
};

const layoutProps = {

};
const MDXLayout = "wrapper"
export default function MDXContent({
  components,
  ...props
}) {
  return <MDXLayout {...layoutProps} {...props} components={components} mdxType="MDXLayout">
    <h2 {...{
      "id": "testing"
    }}>{`Testing`}</h2>
    <p>{`The test files are stored in the test folders and require the dev dependencies `}<inlineCode parentName="p">{`sinonjs, jsdom, mocha, chai`}</inlineCode>{`.`}</p>
    <p>{`Run `}<inlineCode parentName="p">{`npm test`}</inlineCode>{` to view the tests files that are written in that folder.`}</p>
    <p>{`Testing is still primitive and should be BDD (behaviour driven). Tests that are too specific will cause issues.`}</p>
    <p><strong parentName="p">{`mocha and chai`}</strong></p>
    <p>{`These two are used in tandem to assert/expect values. Check out the Chai documentation for relative examples.`}</p>
    <p>{`Tests consist of test suites and test specs. In order to recreate DOM elements if you are looking to test values etc. from functionality, you can use the `}<inlineCode parentName="p">{`jsdom`}</inlineCode>{` library and import `}<inlineCode parentName="p">{`jquery`}</inlineCode>{`.`}</p>
    <p>{`Below is a small example of a test set up.`}</p>
    <pre><code parentName="pre" {...{
        "className": "language-javascript"
      }}>{`import jsdom from 'jsdom-global';
import jQuery from '../bower_components/jquery/dist/jquery.min.js';

import ModuleToTest from 'path/to/module'l

// require sinon/chai
const sinon = require('sinon');
const expect = require('chai').expect;

// Test suite
describe('These tests are supposed to test some functionality', () => {

    let $;
    let ageGateFilters;
    jsdom();

    // this function will run before every Test Spec
    before(function () {
        $ = jQuery;
        const params = ['param1', 'param2', 'param3']

        // create instance
        const moduleToTest = new ModuleToTest(...params);
    });

    // Test Spec (unit test)
    it('This test should return ok', () => {
        expect(true).to.be.ok;  // returns ok
    });

    // Test Spec (unit test)
    it('This test should return true for the ModuleToTest function', () => {
        expect(moduleToTest.func()).to.equal(true); // returns ok if result is true
    });

});
`}</code></pre>
    <p><strong parentName="p">{`sinon.js`}</strong></p>
    <p>{`This library is used to essentially stub data for functions that require things like ajax etc. No tests currently use this effectively. Examples will be added later if used.`}</p>
    <p>{`For now, I will show an example of a `}<inlineCode parentName="p">{`spy`}</inlineCode>{` and a `}<inlineCode parentName="p">{`stub`}</inlineCode>{` (as this may the most relevant): `}</p>
    <p><em parentName="p">{`For the Spy`}</em></p>
    <pre><code parentName="pre" {...{}}>{`// in module Example

export default class Example {

    callout() {
        let a = 1;
        let b = 2;

        return target(a,b);
    }

}

// in a test file

import Example from 'Example';

// Test suite
describe('A test suite using sinon', () => {

    // this function will run before every Test Spec
    before(function () {
        $ = jQuery;
        const params = ['param1', 'param2', 'param3']

        // create instance
        const example = new Example(...params);
    });

    // Test spec (unit test)
    it('Should return that the async func was called', () => {

        let targetSpy = sinon.spy(example, 'target');

        // Now, any time we call the function, the spy logs information about it
        example.callout();

        assert(targetSpy.calledOnce); // returns true
    });
});
`}</code></pre>
    <p><em parentName="p">{`For the Stub`}</em></p>
    <pre><code parentName="pre" {...{}}>{`// in module Example

export default class Example {

    callout(param, callback) {
        $.ajax({
            // whatever it normally is
        }, callback);
    }

}

// in a test file

import Example from 'Example';

// Test suite
describe('A test suite using sinon', () => {

    // this function will run before every Test Spec
    before(function () {
        $ = jQuery;
        const params = ['param1', 'param2', 'param3']

        // create instance
        const example = new Example(...params);
    });

    // Test spec (unit test)
    it('Should call callback after saving', () => {

        //We'll stub $.post so a request is not sent
        const post = sinon.stub($, 'ajax');
        post.yields();

        //We can use a spy as the callback so it's easy to verify
        var callback = sinon.spy();

        example.callout(param, callback);

        post.restore();
        sinon.assert.calledOnce(callback); // returns true
    });
});
`}</code></pre>
    <p><strong parentName="p">{`jsdom`}</strong></p>
    <p>{`jsdom is a library that allows you to write a `}<inlineCode parentName="p">{`innerHTML`}</inlineCode>{` var for the test to use if you want to test out the jQuery values for a test.`}</p>
    <p>{`For an example, checkout yt `}<inlineCode parentName="p">{`test/agegate_test.js`}</inlineCode>{`.`}</p>
    </MDXLayout>;
}

MDXContent.isMDXComponent = true;
