  import React from 'react'
  import { MDXTag } from '@mdx-js/tag'


export default class MDXContent extends React.Component {
  constructor(props) {
    super(props)
    this.layout = null
  }
  render() {
    const { components, ...props } = this.props

    return <MDXTag
             name="wrapper"

             components={components}><MDXTag name="h1" components={components} props={{"id":"passkit"}}>{`PassKit`}</MDXTag>
{/* TOC */}
<MDXTag name="ul" components={components}>
<MDXTag name="li" components={components} parentName="ul"><MDXTag name="a" components={components} parentName="li" props={{"href":"#passkit"}}>{`PassKit`}</MDXTag><MDXTag name="ul" components={components} parentName="li">
<MDXTag name="li" components={components} parentName="ul"><MDXTag name="a" components={components} parentName="li" props={{"href":"#adding-the-certificate"}}>{`Adding the certificate`}</MDXTag></MDXTag>
<MDXTag name="li" components={components} parentName="ul"><MDXTag name="a" components={components} parentName="li" props={{"href":"#php-server"}}>{`PHP Server`}</MDXTag><MDXTag name="ul" components={components} parentName="li">
<MDXTag name="li" components={components} parentName="ul"><MDXTag name="a" components={components} parentName="li" props={{"href":"#plan-ticket-example"}}>{`Plan ticket example`}</MDXTag></MDXTag>
<MDXTag name="li" components={components} parentName="ul"><MDXTag name="a" components={components} parentName="li" props={{"href":"#store-card-example"}}>{`Store card example`}</MDXTag></MDXTag>
</MDXTag></MDXTag>
<MDXTag name="li" components={components} parentName="ul"><MDXTag name="a" components={components} parentName="li" props={{"href":"#more-info"}}>{`More info`}</MDXTag></MDXTag>
</MDXTag></MDXTag>
</MDXTag>
{/* /TOC */}
<MDXTag name="h2" components={components} props={{"id":"adding-the-certificate"}}>{`Adding the certificate`}</MDXTag>
<MDXTag name="p" components={components}>{`Head to the iOS dev portal and add the appropriate certificate.`}</MDXTag>
<MDXTag name="p" components={components}>{`You may need to open Keychain access to generate a new key using `}<MDXTag name="inlineCode" components={components} parentName="p">{`Keychain Access > Certificate Assistant > Req Cert from Cert Auth`}</MDXTag>{` and generate a new cert. This cert will be used to generate what you need to import into Keychain Access from the dev portal.`}</MDXTag>
<MDXTag name="p" components={components}>{`Once created, export it to a folder for the server.`}</MDXTag>
{/* Once created, download the certificate and export it as .p12 to the server which hosts `node-passbook`. You may need to global install as well `npm install -g passbook` to then use `node-passbook prepare-keys -p keys` to convert .p12 to .pem. Make sure you set the PEM passphrase too - it will be needed. */}
<MDXTag name="h2" components={components} props={{"id":"php-server"}}>{`PHP Server`}</MDXTag>
<MDXTag name="p" components={components}>{`Using `}<MDXTag name="inlineCode" components={components} parentName="p">{`composer`}</MDXTag>{`, you can build out a scaffold using the Slim Framework:`}</MDXTag>
<MDXTag name="p" components={components}><MDXTag name="inlineCode" components={components} parentName="p">{`composer create-project slim/slim-skeleton [project-name]`}</MDXTag>{`.`}</MDXTag>
<MDXTag name="p" components={components}>{`To install the PHP PassKit helper, run `}<MDXTag name="inlineCode" components={components} parentName="p">{`composer require pkpass/pkpass`}</MDXTag>{`.`}</MDXTag>
<MDXTag name="p" components={components}>{`Once built, you can then hit the routes and just require the pkpass and the routes file may end up looking like this:`}</MDXTag>
<MDXTag name="h3" components={components} props={{"id":"plan-ticket-example"}}>{`Plan ticket example`}</MDXTag>
<MDXTag name="pre" components={components}><MDXTag name="code" components={components} parentName="pre" props={{"className":"language-php"}}>{`<?php
// Routes
use PKPass\PKPass;

\$app->get('/', function (\$request, \$response, \$args) {
    // Sample log message
    \$this->logger->info("Slim-Skeleton '/' route");

    // Render index view
    return \$this->renderer->render(\$response, 'sample.phtml', \$args);
});

\$app->get('/pass', function (\$request, \$response, \$args) {
    // Sample log message
    \$this->logger->info("Slim-Skeleton '/pass' route");

    \$pass = new PKPass('<name>.p12', '<insert-password>');

    // Pass content
    \$data = [
        'description' => 'Demo pass',
        'formatVersion' => 1,
        'organizationName' => 'Flight Express',
        'passTypeIdentifier' => 'pass.com.<teamname>.<passname>', // Change this!
        'serialNumber' => '12345678',
        'teamIdentifier' => '<insert-team-id>', // Change this! Found on Apple Dev Portal
        'boardingPass' => [
            'primaryFields' => [
                [
                    'key' => 'origin',
                    'label' => 'San Francisco',
                    'value' => 'SFO',
                ],
                [
                    'key' => 'destination',
                    'label' => 'London',
                    'value' => 'LHR',
                ],
            ],
            'secondaryFields' => [
                [
                    'key' => 'gate',
                    'label' => 'Gate',
                    'value' => 'F12',
                ],
                [
                    'key' => 'date',
                    'label' => 'Departure date',
                    'value' => '07/11/2012 10:22',
                ],
            ],
            'backFields' => [
                [
                    'key' => 'passenger-name',
                    'label' => 'Passenger',
                    'value' => 'John Appleseed',
                ],
            ],
            'transitType' => 'PKTransitTypeAir',
        ],
        'barcode' => [
            'format' => 'PKBarcodeFormatQR',
            'message' => 'Flight-GateF12-ID6643679AH7B',
            'messageEncoding' => 'iso-8859-1',
        ],
        'backgroundColor' => 'rgb(32,110,247)',
        'logoText' => 'Flight info',
        'relevantDate' => date('Y-m-d\TH:i:sP')
    ];

    \$pass->setWWDRcertPath('<path to wwdr.pem>');
    \$pass->setData(\$data);

    // Add files to the pass package
    \$pass->addFile('public/icon.png');
    \$pass->addFile('public/icon@2x.png');
    \$pass->addFile('public/logo.png');

    if(\$pass->checkError(\$error) == true) {
        // echo \$error;
        // exit('An error occured: ' . \$error);
    }

    // echo 'Here';

    \$result = \$pass->create(true);
    if(\$result == false) {
        echo \$pass->getError();
    }
    // Render index view
    return \$result;
});
`}</MDXTag></MDXTag>
<MDXTag name="h3" components={components} props={{"id":"store-card-example"}}>{`Store card example`}</MDXTag>
