  import React from 'react'
  import { MDXTag } from '@mdx-js/tag'


export default class MDXContent extends React.Component {
  constructor(props) {
    super(props)
    this.layout = null
  }
  render() {
    const { components, ...props } = this.props

    return <MDXTag
             name="wrapper"

             components={components}><MDXTag name="h1" components={components} props={{"id":"animations-in-react"}}>{`Animations in React`}</MDXTag>
{/* TOC -->autoauto- [Animations in React](#animations-in-react)auto- [REACTAN-1: Installation](#reactan-1-installation)auto- [REACTAN-2: Using CSS to now run the Transitions and Transforms](#reactan-2-using-css-to-now-run-the-transitions-and-transforms)autoauto<!-- /TOC */}
<MDXTag name="h1" components={components} props={{"id":"reactan-1-installation"}}>{`REACTAN-1: Installation`}</MDXTag>
<MDXTag name="p" components={components}>{`React Transistions can be done use the `}<MDXTag name="inlineCode" components={components} parentName="p">{`ReactCSSTransitionGroup`}</MDXTag>{`.`}</MDXTag>
<MDXTag name="p" components={components}>{`Since React uses the virtual DOM, we don't get a chance to directly apply a CSS transition like you normally would with jQuery or JS.`}</MDXTag>
<MDXTag name="p" components={components}>{`To install: `}<MDXTag name="inlineCode" components={components} parentName="p">{`yarn add react-addons-css-transition-group`}</MDXTag></MDXTag>
<MDXTag name="p" components={components}>{`In the JS file:`}</MDXTag>
<MDXTag name="pre" components={components}><MDXTag name="code" components={components} parentName="pre" props={{"className":"language-javascript"}}>{`import ReactCSSTransitionGroup from 'react-addons-css-transition-group';

export default class Transition extends Component {
    render() {
        return {
            <ReactCSSTransitionGroup>
                { list }
            </ReactCSSTransitionGroup>
        }
    }
}
`}</MDXTag></MDXTag>
<MDXTag name="p" components={components}>{`By default, `}<MDXTag name="inlineCode" components={components} parentName="p">{`<ReactCSSTransitionGroup>`}</MDXTag>{` renders as a `}<MDXTag name="inlineCode" components={components} parentName="p">{`span`}</MDXTag>{`, but we can give it a `}<MDXTag name="inlineCode" components={components} parentName="p">{`component`}</MDXTag>{` property to change it! E.g. `}<MDXTag name="inlineCode" components={components} parentName="p">{`<ReactCSSTransitionGroup component="ul">`}</MDXTag></MDXTag>
<MDXTag name="p" components={components}>{`To access the property for animations, we need to set a property of `}<MDXTag name="inlineCode" components={components} parentName="p">{`transitionName`}</MDXTag>{`.`}</MDXTag>
<MDXTag name="p" components={components}>{`E.g. `}<MDXTag name="inlineCode" components={components} parentName="p">{`<ReactCSSTransitionGroup component="ul" transitionName="slide">`}</MDXTag></MDXTag>
<MDXTag name="p" components={components}>{`There are a couple of other noteworthy properties:`}</MDXTag>
<MDXTag name="pre" components={components}><MDXTag name="code" components={components} parentName="pre" props={{"className":"language-javascript"}}>{`<ReactCSSTransitionGroup
  component="ul"
  transitionName="slide"
  transitionEnterTimeout={500} // this is for the duration of the transition in ms
  transitionLeaveTimeout={500} // transition for leaving the DOM
  transitionAppear={true} // transition for the ReactCSSTransitionGroup component
  transitionAppearTimeout={500} // time for is
>
  {list}
</ReactCSSTransitionGroup>
`}</MDXTag></MDXTag>
<MDXTag name="h1" components={components} props={{"id":"reactan-2-using-css-to-now-run-the-transitions-and-transforms"}}>{`REACTAN-2: Using CSS to now run the Transitions and Transforms`}</MDXTag>
<MDXTag name="pre" components={components}><MDXTag name="code" components={components} parentName="pre" props={{}}>{`// in the css file

/* start enter state */
.slide-enter {
    transform: translateX(-100%);
}

/* end enter state */
.slide-enter.slide-enter-active {
    transform: translateX(0);
    transition: transform 0.5s ease-out;
}

/* start leave state */
.slide-leave {
    transform: translateX(0);
}

/* end leave state */
.slide-leave.slide-leave-active {
    transform: translateX(-100%);
    opacity: 0;
    transition: 0.5s ease-in;
}

/* initial mounting */
.slide-appear {
    opacity: 0;
}

.slide-appear.slide-appear-active {
    opacity: 1;
    transition: opacity 0.5s ease-in;
}
`}</MDXTag></MDXTag>
           </MDXTag>
  }
}
