/* @jsx mdx */
  import React from 'react'
  import { mdx } from '@mdx-js/react'
  /* @jsx mdx */


const makeShortcode = name => function MDXDefaultShortcode(props) {
  console.warn("Component " + name + " was not imported, exported, or provided by MDXProvider as global scope")
  return <div {...props}/>
};

const layoutProps = {

};
const MDXLayout = "wrapper"
export default function MDXContent({
  components,
  ...props
}) {
  return <MDXLayout {...layoutProps} {...props} components={components} mdxType="MDXLayout">
    <h1 {...{
      "id": "loonshots"
    }}>{`Loonshots`}</h1>
    <h2 {...{
      "id": "bush-vail-cartesian-plane"
    }}>{`Bush-Vail Cartesian Plane`}</h2>
    <ul>
      <li parentName="ul">{`y plane: Dynamic Equilibrium`}</li>
      <li parentName="ul">{`x plane: Phase Transition`}</li>
      <li parentName="ul">{`Bottom-left quadrant: Stagntion`}</li>
      <li parentName="ul">{`Bottom-right quadrant: Moses trap, PARC trap`}</li>
      <li parentName="ul">{`Top-left quadrant: Chaos`}</li>
      <li parentName="ul">{`Top-right quadrant: Bush-Vail Balance`}</li>
    </ul>
    <h2 {...{
      "id": "bush-vail-rules"
    }}>{`Bush-Vail Rules`}</h2>
    <h3 {...{
      "id": "1-separate-the-phases"
    }}>{`1. Separate the phases`}</h3>
    <ul>
      <li parentName="ul">{`Seperate your artists and soldiers`}</li>
      <li parentName="ul">{`Tailor the tools to the phase`}</li>
      <li parentName="ul">{`Watch your blind side: nurture both types of loonshots`}</li>
    </ul>
    <h3 {...{
      "id": "2-create-dynamic-equilibrium"
    }}>{`2. Create dynamic equilibrium`}</h3>
    <ul>
      <li parentName="ul">{`Love your artists and soldiers equally`}</li>
      <li parentName="ul">{`Manage the transfer, not the tech: be a gardener, not a Moses`}</li>
      <li parentName="ul">{`Appoint and train project champions to bridge the divide`}</li>
    </ul>
    <h3 {...{
      "id": "3-spread-a-system-mindset"
    }}>{`3. Spread a system mindset`}</h3>
    <ul>
      <li parentName="ul">{`Keep asking why the organisation made the choices that it did`}</li>
      <li parentName="ul">{`Keep asking how the decision-making process can be improved`}</li>
      <li parentName="ul">{`Identify teams with outcome mindset and help them adopt system mindset`}</li>
    </ul>
    <h3 {...{
      "id": "4-raise-the-magic-number"
    }}>{`4. Raise the magic number`}</h3>
    <ul>
      <li parentName="ul">{`Reduce return-on-politics`}</li>
      <li parentName="ul">{`Use soft equity (nonfinancial awards)`}</li>
      <li parentName="ul">{`Increase project-skill fit (scan for mismatches)`}</li>
      <li parentName="ul">{`Fix the middle (reduce perverse incentives for middle managers)`}</li>
      <li parentName="ul">{`Bring a gun to a knife fight (engage a chief incentives officer)`}</li>
      <li parentName="ul">{`Fine-tune the spans (wide for loonshots for groups; narrow for franchise)`}</li>
    </ul>
    <h3 {...{
      "id": "other-standard-thoughts"
    }}>{`Other standard thoughts`}</h3>
    <ul>
      <li parentName="ul">{`Mind the `}<inlineCode parentName="li">{`False Fail`}</inlineCode></li>
      <li parentName="ul">{`Listen to the `}<inlineCode parentName="li">{`Suck with Curiousity`}</inlineCode>{` (LSC)`}</li>
      <li parentName="ul">{`Apply system rather than outcome mindset`}</li>
      <li parentName="ul">{`Keep your eyes on SRT: spirit, relationships, time`}</li>
    </ul>
    <h2 {...{
      "id": "the-magic-number-150"
    }}>{`The Magic Number 150`}</h2>
    <h3 {...{
      "id": "design--fitness-parameters-g-s-e-f"
    }}>{`Design + Fitness Parameters (G, S, E, F)`}</h3>
    <table>
      <thead parentName="table">
        <tr parentName="thead">
          <th parentName="tr" {...{
            "align": null
          }}>{`Parameter`}</th>
          <th parentName="tr" {...{
            "align": null
          }}>{`Definition`}</th>
        </tr>
      </thead>
      <tbody parentName="table">
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": null
          }}>{`Growth Rate (G)`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`Salary-Step Up (12% means G is 12%)`}</td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": null
          }}>{`Management Span (S)`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`Manager : employee (S = 3)`}</td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": null
          }}>{`Equity Fraction (E)`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`Equity ties your pay directly to quality`}</td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": null
          }}>{`Fitness (F)`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`Project-Skill Fit + Return-On-Politics (F = 1 is equal)`}</td>
        </tr>
      </tbody>
    </table>
    <h3 {...{
      "id": "magic-number-equation"
    }}>{`Magic Number Equation`}</h3>
    <p>{`M (150) stands for the number that above which the balance flips from favoring project work to politics.`}</p>
    <p>{`Below the threshold, incentives encourage employees to unite around making loonshots successful. Above, career considerations become more important and politics suddently appear.`}</p>
    <pre><code parentName="pre" {...{
        "className": "language-typescript"
      }}>{`type Equity = number;
type GrowthRate = number;
type ManagementSpan = number;
type Fitness = number;
type MagicNumber = number;

// M = magic number
const M = (
  E: Equity,
  S: ManagementSpan,
  F: Fitness,
  G: GrowthRate
): MagicNumber => (E * (S * S) * F) / G;

// example one
M(0.5, 6, 1, 0.12); // = 150
M(0.5, 10, 1, 0.33); // = 150
`}</code></pre>
    </MDXLayout>;
}

MDXContent.isMDXComponent = true;
