/* @jsx mdx */
  import React from 'react'
  import { mdx } from '@mdx-js/react'
  /* @jsx mdx */


const makeShortcode = name => function MDXDefaultShortcode(props) {
  console.warn("Component " + name + " was not imported, exported, or provided by MDXProvider as global scope")
  return <div {...props}/>
};

const layoutProps = {

};
const MDXLayout = "wrapper"
export default function MDXContent({
  components,
  ...props
}) {
  return <MDXLayout {...layoutProps} {...props} components={components} mdxType="MDXLayout">
    <h1 {...{
      "id": "npm-help-sheet"
    }}>{`npm help sheet`}</h1>
    <h2 {...{
      "id": "install-npm-dependencies"
    }}>{`install npm dependencies`}</h2>
    <pre><code parentName="pre" {...{}}>{`npm install
`}</code></pre>
    <h2 {...{
      "id": "useful-npm-dependencies"
    }}>{`USEFUL NPM DEPENDENCIES`}</h2>
    <h4 {...{
      "id": "mongoose---database-helper-for-mongodb"
    }}>{`MONGOOSE - database helper for MongoDB`}</h4>
    <pre><code parentName="pre" {...{}}>{`npm install --save mongoose

mongod // starts the daemon
mongo // check out the documents
`}</code></pre>
    <pre><code parentName="pre" {...{
        "className": "language-javascript"
      }}>{`var User = mongoose.model('User', UserSchema);
module.exports = User;

// in app.js
var User = require('../models/user');
`}</code></pre>
    <h4 {...{
      "id": "bcrypt---used-for-hashing-and-salting"
    }}>{`BCRYPT - used for hashing and salting`}</h4>
    <pre><code parentName="pre" {...{}}>{`npm install --save bcrypt
`}</code></pre>
    <pre><code parentName="pre" {...{
        "className": "language-javascript"
      }}>{`// in User.js
var bcrypt = require('bcrypt');
UserSchema.pre('save', function(next) {
    var user = this;
    bcrypt.hash(user.password, 10, function(err, hash) {
        if (err) {
            return next(err);
        }
        user.password = hash;
        next();
    })
});

UserSchema.statics.authenticate = function(email, password, callback) {
    User.findOne({ email: email })
        .exec(function (error, user) {
            if (error) {
                return callback(error);
            } else if (!user) {
                var err = new Error('User not found.');
                err.status = 401;
                return callback(err);
            }
            bcrypt.compare(password, user.password, function (error, result) {
                if (result === true) {
                    return callback(null, user);
                } else {
                    return callback();
                }
            });
        });
}

// Example in app.js for Express

router.post('/login', function(req, res, next) {
    if (req.body.email && req.body.password) {
        User.authenticate(req.body.email, req.body.password, function (error, user) {
            if (error || !user) {
                var err = new Error('Wrong email or password.');
                err.status = 401;
                return next(err);
            } else {
                req.session.userId = user._id;
                return res.redirect('/profile');
            }
        });
    } else {
        var err = new Error('Email and password are required.');
        err.status = 401;
        return next(err);
    }
});
`}</code></pre>
    <h4 {...{
      "id": "express-session---save-sessions-to-use-throughout-the-website"
    }}>{`EXPRESS SESSION - save sessions to use throughout the website`}</h4>
    <pre><code parentName="pre" {...{}}>{`npm install express-session --save
`}</code></pre>
    <pre><code parentName="pre" {...{
        "className": "language-javascript"
      }}>{`// in app.js

var session = require('express-session');

// use sessions for tracking logins - check more in the readme
app.use(session({
    secret: 'treehouse loves you',
    resave: true,
    saveUninitialized: false
}));

// not useful when in production. check https://github.com/expressjs/session#compatible-session-stores
`}</code></pre>
    <h4 {...{
      "id": "connect-mongo---used-for-quick-middleware-access-to-mongo"
    }}>{`CONNECT-MONGO - used for quick middleware access to Mongo`}</h4>
    <p>{`connect-mongo is a middleware already written for us!`}</p>
    <pre><code parentName="pre" {...{}}>{`npm install connect-mongo --save
`}</code></pre>
    <pre><code parentName="pre" {...{
        "className": "language-javascript"
      }}>{`require MongoStore = require('connect-mongo')(session);

// in app.js

app.use(session({
    secret: 'treehouse loves you',
    resave: true,
    saveUninitialized: false
    store: new MongoStore({
        mongooseConnection: db
    })
}));
`}</code></pre>
    </MDXLayout>;
}

MDXContent.isMDXComponent = true;
