  import React from 'react'
  import { mdx } from '@mdx-js/react'
  /* @jsx mdx */


const makeShortcode = name => function MDXDefaultShortcode(props) {
  console.warn("Component " + name + " was not imported, exported, or provided by MDXProvider as global scope")
  return <div {...props}/>
};

const layoutProps = {

};
const MDXLayout = "wrapper"
export default function MDXContent({
  components,
  ...props
}) {
  return <MDXLayout {...layoutProps} {...props} components={components} mdxType="MDXLayout">
    <h1 {...{
      "id": "abstract-factory"
    }}>{`Abstract Factory`}</h1>
    <p>{`Here we will cover the `}<inlineCode parentName="p">{`creational`}</inlineCode>{` design pattern `}<inlineCode parentName="p">{`Abstract Factory`}</inlineCode>{`.`}</p>
    <p>{`Another creational pattern, the `}<inlineCode parentName="p">{`Factory`}</inlineCode>{` design pattern, differs from Abstract Factory in that the factory method is a single method, whereas an abstract factory is an object.`}</p>
    <blockquote>
      <p parentName="blockquote">{`... the Factory Method pattern uses inheritance and relies on a subclass to handle the desired object instantiation.`}</p>
    </blockquote>
    <p>{`This quote assumes the object is calling its own factory method, therefore the only thing that could change the return value would be a subclass.`}</p>
    <blockquote>
      <p parentName="blockquote">{`... with the Abstract Factory pattern, a class delegates the responsibility of object instantiation to another object via composition ...`}</p>
    </blockquote>
    <p>{`Here there is an object A who wants to make a Foo object. Instead of making the Foo object itself (like in the factory method), it's going to get a different object (the abstract factory) to create the Foo object.`}</p>
    <pre><code parentName="pre" {...{
        "className": "language-javascript"
      }}>{`// defining our abstract products
interface Processor {
    performOperation(): void;
}

interface HardDisk {
    storeData(): void;
}

interface Monitor {
    displayPicture(): void;
}

// defining our concrete products
class ExpensiveProcessor implements Processor {
    performOperation() {
        console.log('Operation will perform quickly!');
    }
}

class CheapProcessor implements Processor {
    performOperation() {
        console.log('Operation will perform slowly!');
    }
}

class ExpensiveHDD implements HardDisk {
    storeData() {
        console.log('Data will take less time to store');
    }
}

class CheapHDD implements HardDisk {
    storeData() {
        console.log('Data will take more time to store');
    }
}

class HighResMonitor implements Monitor {
    displayPicture() {
        console.log('High picture quality');
    }
}

class LowResMonitor implements Monitor {
    displayPicture() {
        console.log('Low picture quality');
    }
}

// defining the abstract factory
interface MachineAbstractFactory {
    // each factory needs to implement these methods
    getProcessor(): Processor;
    getHardDisk(): HardDisk;
    getMonitor(): Monitor;
}

// defining our concrete factories
class HighBudgetMachineFactory implements MachineAbstractFactory {
    getProcessor(): Processor {
        return new ExpensiveProcessor();
    }

    getHardDisk(): HardDisk {
        return new ExpensiveHDD();
    }

    getMonitor(): Monitor {
        return new HighResMonitor();
    }
}

class LowBudgetMachineFactory implements MachineAbstractFactory {
    getProcessor(): Processor {
        return new CheapProcessor();
    }

    getHardDisk(): HardDisk {
        return new CheapHDD();
    }

    getMonitor(): Monitor {
        return new LowResMonitor();
    }
}

// defining our client and final product
interface Machine {
    processor: Processor;
    hdd: HardDisk;
    monitor: Monitor;
}

class ComputerShop {
    // change access modifiers as suits
    public category: MachineAbstractFactory;

    // we'll pass a factory as category during instantiation
    constructor(category: MachineAbstractFactory) {
        this.category = category;
    }

    assembleMachine(): Machine {
        const processor = this.category.getProcessor();
        const hdd = this.category.getHardDisk();
        const monitor = this.category.getMonitor();

        // to be explicit
        const machine: Machine = {
            processor,
            hdd,
            monitor
        }
        return machine;
    }
}

// running the code in action!
const cheapFactory = new LowBudgetMachineFactory();
const expensiveFactory = new HighBudgetMachineFactory();

// ensure to pass the factory in during instantiation
// using let to show variability, in code these
// would likely be functions that take a factory
// arg and constants for shop/machine
let shop;
let machine;

shop = new ComputerShop(cheapFactory);
machine = shop.assembleMachine();
machine.hdd.storeData();
machine.processor.performOperation();
machine.monitor.displayPicture();

shop = new ComputerShop(expensiveFactory);
machine = shop.assembleMachine();
machine.hdd.storeData();
machine.processor.performOperation();
machine.monitor.displayPicture();
`}</code></pre>
    <h2 {...{
      "id": "references"
    }}>{`References`}</h2>
    <ul>
      <li parentName="ul"><a parentName="li" {...{
          "href": "https://stackoverflow.com/questions/5739611/what-are-the-differences-between-abstract-factory-and-factory-design-patterns"
        }}>{`Difference between factory and abstract factory`}</a></li>
      <li parentName="ul"><a parentName="li" {...{
          "href": "https://www.codeproject.com/Articles/716413/Factory-Method-Pattern-vs-Abstract-Factory-Pattern"
        }}>{`ASP.NET Abstract Factory vs Factory`}</a></li>
    </ul>
    </MDXLayout>;
}

