/* @jsx mdx */
  import React from 'react'
  import { mdx } from '@mdx-js/react'
  /* @jsx mdx */


const makeShortcode = name => function MDXDefaultShortcode(props) {
  console.warn("Component " + name + " was not imported, exported, or provided by MDXProvider as global scope")
  return <div {...props}/>
};

const layoutProps = {

};
const MDXLayout = "wrapper"
export default function MDXContent({
  components,
  ...props
}) {
  return <MDXLayout {...layoutProps} {...props} components={components} mdxType="MDXLayout">
    <h1 {...{
      "id": "sysops"
    }}>{`SysOps`}</h1>
    {
      /* TOC */
    }
    <ul>
      <li parentName="ul"><a parentName="li" {...{
          "href": "#sysops"
        }}>{`SysOps`}</a><ul parentName="li">
          <li parentName="ul"><a parentName="li" {...{
              "href": "#networking"
            }}>{`Networking`}</a><ul parentName="li">
              <li parentName="ul"><a parentName="li" {...{
                  "href": "#networking-bottlenecks"
                }}>{`Networking Bottlenecks`}</a></li>
              <li parentName="ul"><a parentName="li" {...{
                  "href": "#dns-101"
                }}>{`DNS 101`}</a><ul parentName="li">
                  <li parentName="ul"><a parentName="li" {...{
                      "href": "#top-level-domains"
                    }}>{`Top Level Domains`}</a></li>
                  <li parentName="ul"><a parentName="li" {...{
                      "href": "#domain-registrars"
                    }}>{`Domain Registrars`}</a></li>
                  <li parentName="ul"><a parentName="li" {...{
                      "href": "#soa-records"
                    }}>{`SOA Records`}</a></li>
                  <li parentName="ul"><a parentName="li" {...{
                      "href": "#ns-records"
                    }}>{`NS Records`}</a></li>
                  <li parentName="ul"><a parentName="li" {...{
                      "href": "#a-records"
                    }}>{`A Records`}</a></li>
                  <li parentName="ul"><a parentName="li" {...{
                      "href": "#ttl-record"
                    }}>{`TTL Record`}</a></li>
                  <li parentName="ul"><a parentName="li" {...{
                      "href": "#cnames"
                    }}>{`CNAMES`}</a></li>
                  <li parentName="ul"><a parentName="li" {...{
                      "href": "#alias-records"
                    }}>{`Alias records`}</a></li>
                  <li parentName="ul"><a parentName="li" {...{
                      "href": "#summary"
                    }}>{`Summary`}</a></li>
                </ul></li>
              <li parentName="ul"><a parentName="li" {...{
                  "href": "#registering-a-domain-name"
                }}>{`Registering a Domain Name`}</a></li>
            </ul></li>
          <li parentName="ul"><a parentName="li" {...{
              "href": "#definitions"
            }}>{`Definitions`}</a></li>
        </ul></li>
    </ul>
    {
      /* /TOC */
    }
    <h2 {...{
      "id": "networking"
    }}>{`Networking`}</h2>
    <h3 {...{
      "id": "networking-bottlenecks"
    }}>{`Networking Bottlenecks`}</h3>
    <p>{`Increase network performance = increase instance size.`}</p>
    <p>{`EBS Throughput != network throughput. This is writing to disk as opposed to EC2 talking to each other within the same subnet.`}</p>
    <h3 {...{
      "id": "dns-101"
    }}>{`DNS 101`}</h3>
    <p>{`DNS is used to convert human friendly domains to an `}<inlineCode parentName="p">{`Internet Protocol (IP)`}</inlineCode>{`.`}</p>
    <p>{`IPv4 is a 32 bit field and has 42 billion addresses.`}</p>
    <p>{`IPv6 has an address space of 128 bits. 340 undecillion addresses.`}</p>
    <p>{`The big issue at the moment is that everyone is still using IPv4 - this is driven by ISPs.`}</p>
    <p>{`Amazon previously did not have IPv6 support for backend systems. This changed around 2016.`}</p>
    <h4 {...{
      "id": "top-level-domains"
    }}>{`Top Level Domains`}</h4>
    <p>{`The last word in the domain name is the top level eg. `}<inlineCode parentName="p">{`.com`}</inlineCode>{`, `}<inlineCode parentName="p">{`.edu`}</inlineCode>{`, `}<inlineCode parentName="p">{`.gov`}</inlineCode>{` etc.`}</p>
    <p>{`These are controlled by the `}<inlineCode parentName="p">{`Internet Assigne Numbers Authority (IANA)`}</inlineCode>{` in a root zone database which is essentially a database of all available top level domains.`}</p>
    <h4 {...{
      "id": "domain-registrars"
    }}>{`Domain Registrars`}</h4>
    <p>{`As all of these need to be unique, the domain registrar is an authority that can assign domain names directly under one or more top-level domains. These domains are registered with InterNIC, a service of ICANN, which enforces the uniqueness of domain names across the internet.`}</p>
    <p>{`Each domain becomes registered in a central database known as the `}<inlineCode parentName="p">{`WhoIS database`}</inlineCode>{`.`}</p>
    <h4 {...{
      "id": "soa-records"
    }}>{`SOA Records`}</h4>
    <ul>
      <li parentName="ul"><inlineCode parentName="li">{`Start of Authority`}</inlineCode></li>
      <li parentName="ul">{`Name of the server that suppised the data for the zone.`}</li>
      <li parentName="ul">{`The administrator of the zone. (The owner)`}</li>
      <li parentName="ul">{`Current version of the data file`}</li>
      <li parentName="ul">{`Number of seconds a secondary name server should wait before checking for updates etc.`}</li>
      <li parentName="ul">{`Default TTL file on resource records`}</li>
    </ul>
    <h4 {...{
      "id": "ns-records"
    }}>{`NS Records`}</h4>
    <ul>
      <li parentName="ul"><inlineCode parentName="li">{`Name Server records`}</inlineCode>{` - used by `}<inlineCode parentName="li">{`Top Level Domain`}</inlineCode>{` servers to direct traffic to the Content DNS server which contains the authoritative DNS records.`}</li>
    </ul>
    <h4 {...{
      "id": "a-records"
    }}>{`A Records`}</h4>
    <p>{`The "A" record is the fundamental type of DNS record. It stands for "Address". The A record is use by a computer to translate the name of the domain to the IP address.`}</p>
    <h4 {...{
      "id": "ttl-record"
    }}>{`TTL Record`}</h4>
    <p><inlineCode parentName="p">{`Time to Live`}</inlineCode>{` - the length of time that the DNS server is cached either on the server or you PC.`}</p>
    <p>{`DNS records take time to propagate throughout the internet. Companies before a migration, they will drop the TTL down low. You may find that even two days later, at the time of the migration the DNS resolutions can head to the old or new server.`}</p>
    <h4 {...{
      "id": "cnames"
    }}>{`CNAMES`}</h4>
    <p>{`A `}<inlineCode parentName="p">{`Canonical Name (CNAME)`}</inlineCode>{` can be used to resolve one domain name to another. For example, you may have a mobile website with the domain name `}<inlineCode parentName="p">{`http://m.acloud.guru`}</inlineCode>{` that is used for when users browse to your domain name on their mobile devices. You may also want `}<inlineCode parentName="p">{`http://mobile.acloud.guru`}</inlineCode>{` to resolve to the same address.`}</p>
    <h4 {...{
      "id": "alias-records"
    }}>{`Alias records`}</h4>
    <p>{`AWS specific. It is used to map resource record sets in your hosted zone to things like ELBs, CF distributions or S3 buckets.`}</p>
    <p>{`The difference, a CNAME can't be used for naked domain names (zone apex).`}</p>
    <h4 {...{
      "id": "summary"
    }}>{`Summary`}</h4>
    <ul>
      <li parentName="ul">{`ELBs do not have a pre-defined IPv4 address - you need to resolve it using a DNS name.`}</li>
      <li parentName="ul">{`Understand the difference between an Alias Record and a CNAME.`}</li>
      <li parentName="ul">{`Given the choose, always choose an Alias Record over a CNAME. (No charge with Alias Record either)`}</li>
    </ul>
    <h3 {...{
      "id": "registering-a-domain-name"
    }}>{`Registering a Domain Name`}</h3>
    <p>{`Head to Route 53, select `}<inlineCode parentName="p">{`Register a Domain`}</inlineCode>{` and go through the process of adding the domain to the cart and continue.`}</p>
    <p>{`Follow the process to go through with details for the SOA record and confirm the purchase.`}</p>
    <h2 {...{
      "id": "definitions"
    }}>{`Definitions`}</h2>
    <table>
      <thead parentName="table">
        <tr parentName="thead">
          <th parentName="tr" {...{
            "align": null
          }}></th>
          <th parentName="tr" {...{
            "align": null
          }}></th>
        </tr>
      </thead>
      <tbody parentName="table">
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": null
          }}>{`ENI`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`Elastic Network Interface`}</td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": null
          }}>{`EBS`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`Elastic Block Store`}</td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": null
          }}>{`DNS`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`Domain Name System`}</td>
        </tr>
      </tbody>
    </table>
    </MDXLayout>;
}

MDXContent.isMDXComponent = true;
