  import React from 'react'
  import { MDXTag } from '@mdx-js/tag'


export default class MDXContent extends React.Component {
  constructor(props) {
    super(props)
    this.layout = null
  }
  render() {
    const { components, ...props } = this.props

    return <MDXTag
             name="wrapper"

             components={components}><MDXTag name="h1" components={components} props={{"id":"npm-help-sheet"}}>{`npm help sheet`}</MDXTag>
<MDXTag name="h2" components={components} props={{"id":"install-npm-dependencies"}}>{`install npm dependencies`}</MDXTag>
<MDXTag name="pre" components={components}><MDXTag name="code" components={components} parentName="pre" props={{}}>{`npm install
`}</MDXTag></MDXTag>
<MDXTag name="h2" components={components} props={{"id":"useful-npm-dependencies"}}>{`USEFUL NPM DEPENDENCIES`}</MDXTag>
<MDXTag name="h4" components={components} props={{"id":"mongoose---database-helper-for-mongodb"}}>{`MONGOOSE - database helper for MongoDB`}</MDXTag>
<MDXTag name="pre" components={components}><MDXTag name="code" components={components} parentName="pre" props={{}}>{`npm install --save mongoose

mongod // starts the daemon
mongo // check out the documents
`}</MDXTag></MDXTag>
<MDXTag name="pre" components={components}><MDXTag name="code" components={components} parentName="pre" props={{"className":"language-javascript"}}>{`var User = mongoose.model('User', UserSchema);
module.exports = User;

// in app.js
var User = require('../models/user');
`}</MDXTag></MDXTag>
<MDXTag name="h4" components={components} props={{"id":"bcrypt---used-for-hashing-and-salting"}}>{`BCRYPT - used for hashing and salting`}</MDXTag>
<MDXTag name="pre" components={components}><MDXTag name="code" components={components} parentName="pre" props={{}}>{`npm install --save bcrypt
`}</MDXTag></MDXTag>
<MDXTag name="pre" components={components}><MDXTag name="code" components={components} parentName="pre" props={{"className":"language-javascript"}}>{`// in User.js
var bcrypt = require('bcrypt');
UserSchema.pre('save', function(next) {
    var user = this;
    bcrypt.hash(user.password, 10, function(err, hash) {
        if (err) {
            return next(err);
        }
        user.password = hash;
        next();
    })
});

UserSchema.statics.authenticate = function(email, password, callback) {
    User.findOne({ email: email })
        .exec(function (error, user) {
            if (error) {
                return callback(error);
            } else if (!user) {
                var err = new Error('User not found.');
                err.status = 401;
                return callback(err);
            }
            bcrypt.compare(password, user.password, function (error, result) {
                if (result === true) {
                    return callback(null, user);
                } else {
                    return callback();
                }
            });
        });
}

// Example in app.js for Express

router.post('/login', function(req, res, next) {
    if (req.body.email && req.body.password) {
        User.authenticate(req.body.email, req.body.password, function (error, user) {
            if (error || !user) {
                var err = new Error('Wrong email or password.');
                err.status = 401;
                return next(err);
            } else {
                req.session.userId = user._id;
                return res.redirect('/profile');
            }
        });
    } else {
        var err = new Error('Email and password are required.');
        err.status = 401;
        return next(err);
    }
});
`}</MDXTag></MDXTag>
<MDXTag name="h4" components={components} props={{"id":"express-session---save-sessions-to-use-throughout-the-website"}}>{`EXPRESS SESSION - save sessions to use throughout the website`}</MDXTag>
<MDXTag name="pre" components={components}><MDXTag name="code" components={components} parentName="pre" props={{}}>{`npm install express-session --save
`}</MDXTag></MDXTag>
<MDXTag name="pre" components={components}><MDXTag name="code" components={components} parentName="pre" props={{"className":"language-javascript"}}>{`// in app.js

var session = require('express-session');

// use sessions for tracking logins - check more in the readme
app.use(session({
    secret: 'treehouse loves you',
    resave: true,
    saveUninitialized: false
}));

// not useful when in production. check https://github.com/expressjs/session#compatible-session-stores
`}</MDXTag></MDXTag>
<MDXTag name="h4" components={components} props={{"id":"connect-mongo---used-for-quick-middleware-access-to-mongo"}}>{`CONNECT-MONGO - used for quick middleware access to Mongo`}</MDXTag>
<MDXTag name="p" components={components}>{`connect-mongo is a middleware already written for us!`}</MDXTag>
<MDXTag name="pre" components={components}><MDXTag name="code" components={components} parentName="pre" props={{}}>{`npm install connect-mongo --save
`}</MDXTag></MDXTag>
<MDXTag name="pre" components={components}><MDXTag name="code" components={components} parentName="pre" props={{"className":"language-javascript"}}>{`require MongoStore = require('connect-mongo')(session);

// in app.js

app.use(session({
    secret: 'treehouse loves you',
    resave: true,
    saveUninitialized: false
    store: new MongoStore({
        mongooseConnection: db
    })
}));
`}</MDXTag></MDXTag>
