  import React from 'react'
  import { MDXTag } from '@mdx-js/tag'


export default class MDXContent extends React.Component {
  constructor(props) {
    super(props)
    this.layout = null
  }
  render() {
    const { components, ...props } = this.props

    return <MDXTag
             name="wrapper"

             components={components}><MDXTag name="h1" components={components} props={{"id":"create-react-app-server-side-bootstrapping"}}>{`create-react-app Server Side bootstrapping`}</MDXTag>
<MDXTag name="ol" components={components}>
<MDXTag name="li" components={components} parentName="ol">{`npm run eject`}</MDXTag>
<MDXTag name="li" components={components} parentName="ol">{`Update config/paths.js`}</MDXTag>
</MDXTag>
<MDXTag name="pre" components={components}><MDXTag name="code" components={components} parentName="pre" props={{"className":"language-javascript"}}>{`// config/paths.js

module.exports = {
    ...
    appServer: resolveApp('server'),
    serverIndexJs: resolveApp('src/server.js'),
    ...
}
`}</MDXTag></MDXTag>
<MDXTag name="ol" components={components} props={{"start":3}}>
<MDXTag name="li" components={components} parentName="ol">{`Create a `}<MDXTag name="inlineCode" components={components} parentName="li">{`webpack.config.server.js`}</MDXTag>{` file.`}</MDXTag>
<MDXTag name="li" components={components} parentName="ol">{`Install `}<MDXTag name="inlineCode" components={components} parentName="li">{`webpack-node-externals`}</MDXTag>{`.`}</MDXTag>
</MDXTag>
<MDXTag name="p" components={components}>{`As described on the github:`}</MDXTag>
<MDXTag name="p" components={components}><MDXTag name="em" components={components} parentName="p">{`Webpack allows you to define externals - modules that should not be bundled.`}</MDXTag></MDXTag>
<MDXTag name="p" components={components}><MDXTag name="em" components={components} parentName="p">{`When bundling with Webpack for the backend - you usually don't want to bundle its node_modules dependencies. This library creates an externals function that ignores node_modules when bundling in Webpack.`}</MDXTag></MDXTag>
<MDXTag name="pre" components={components}><MDXTag name="code" components={components} parentName="pre" props={{}}>{`const path = require('path');
const paths = require('./paths');
const webpackNodeExternals = require('webpack-node-externals');

module.exports = {
    // Target Nodehs
    target: 'node',
    // Looking for the root of server app
    entry: paths.serverIndexJs,
    output: {
        // The build folder.
        path: paths.appServer,
        filename: 'server.js'
    },
    // Run Babel on every file
    module: {
        rules: [
            {
                test: /\.js?\$/,
                loader: 'babel-loader',
                exclude: /node_modules/,
                options: {
                    presets: [
                        'react',
                        'stage-0',
                        ['env', { targets: { browsers: ['last 2 versions']}}]
                    ]
                }
            }
        ]
    },
    externals: [webpackNodeExternals()]
}
`}</MDXTag></MDXTag>
           </MDXTag>
  }
}
