  console.warn("Component " + name + " was not imported, exported, or provided by MDXProvider as global scope")
  return <div {...props}/>
};

const layoutProps = {

};
const MDXLayout = "wrapper"
export default function MDXContent({
  components,
  ...props
}) {
  return <MDXLayout {...layoutProps} {...props} components={components} mdxType="MDXLayout">
    <h1 {...{
      "id": "swift-3-oop"
    }}>{`Swift 3 OOP`}</h1>
    {
      /* TOC -->autoauto- [Swift 3 OOP](#swift-3-oop)auto  - [Structs](#structs)auto  - [Instances of Objects](#instances-of-objects)auto  - [Methods](#methods)auto  - [Initialisers and Self](#initialisers-and-self)auto  - [Class](#class)auto  - [Inheritance](#inheritance)auto  - [Structs vs Classes](#structs-vs-classes)auto  - [Value type vs Reference type](#value-type-vs-reference-type)autoauto<!-- /TOC */
    }
    <hr></hr>
    <h2 {...{
      "id": "structs"
    }}>{`Structs`}</h2>
    <pre><code parentName="pre" {...{}}>{`let coordinate1: (x: Int, y: Int) = (1,0) //tuple
coordinate1.x

// structs are the blueprints
struct Point {
  let x: Int
  let y: Int
}

// when defining structs, define the params
let p1 = Point(x: 1, y: 0)
p1
`}</code></pre>
    <hr></hr>
    <h2 {...{
      "id": "instances-of-objects"
    }}>{`Instances of Objects`}</h2>
    <pre><code parentName="pre" {...{}}>{`// when defining structs, define the params
let p1 = Point(x: 1, y: 0)
p1
p1.x
p1.y

struct User {
  let username: String
  let password: String
}

let Login = User(username: "example@mail.com", password: "123pass")
Login.username
Login.password
`}</code></pre>
    <hr></hr>
    <h2 {...{
      "id": "methods"
    }}>{`Methods`}</h2>
    <ul>
      <li parentName="ul">{`Declaring empty arrays that infer a type `}<inlineCode parentName="li">{`var results: [Point] = []`}</inlineCode></li>
      <li parentName="ul">{`Declaring it the preferred way `}<inlineCode parentName="li">{`var results = [Point]()`}</inlineCode></li>
    </ul>
    <pre><code parentName="pre" {...{}}>{`struct PointTwo {
  let x: Int
  let y: Int

  // three slashes helps with definitions

  /// Returns the surrounding points in range of
  /// the current one
  func points(inRange range: Int = 1) -> [PointTwo] {
    var results = [PointTwo]()

    let lowerBoundOfXRange = x - range
    let upperBoundOfXRange = x + range

    let lowerBoundOfYRange = y - range
    let upperBoundOfYRange = y + range

    for xCoordinate in lowerBoundOfXRange...upperBoundOfXRange {
      for yCoordinate in lowerBoundOfYRange...upperBoundOfYRange {
        let coordinatePoint = PointTwo(x: xCoordinate, y: yCoordinate)
        results.append(coordinatePoint)
      }
    }

    return results
  }
}

let p2 = PointTwo(x: 1, y: 0)
p2.x
p2.y

let rangeReturn = p2.points(inRange: 3)
rangeReturn[0].x
rangeReturn[3].y

struct Person {
    let firstName: String
    let lastName: String

    func fullName() -> String {
      return firstName + " " + lastName
    }
}

let aPerson = Person(firstName: "Billy", lastName: "Bob")
let myFullName = aPerson.fullName()
`}</code></pre>
    <hr></hr>
    <h2 {...{
      "id": "initialisers-and-self"
    }}>{`Initialisers and Self`}</h2>
    <p>{`Self is generally only used in Swift in the init method or when differentiating`}</p>
    <pre><code parentName="pre" {...{}}>{`struct Point {
  let x: Int
  let y: Int

  init(x: Int, y: Int) {
    self.x = x;
    self.y = x;
  }
}

struct RGBColor {
  let red: Double
  let green: Double
  let blue: Double
  let alpha: Double

  let description: String

  // Add your code below
  init(red: Double, green: Double, blue: Double, alpha: Double) {
    self.red = red
    self.green = green
    self.blue = blue
    self.alpha = alpha

    self.description = "red: \\(self.red), green: \\(self.green), blue: \\(self.blue), alpha: \\(self.alpha)"
  }
}

let test = RGBColor(red: 16.0, green: 5.0, blue: 4.3, alpha: 3.0)
test.description
`}</code></pre>
    <hr></hr>
    <h2 {...{
      "id": "class"
    }}>{`Class`}</h2>
    <pre><code parentName="pre" {...{
        "className": "language-swift"
      }}>{`class Enemy {
  var life: Int = 2
  let position: Point

  init(x: Int, y: Int) {
    self.position = Point(x: x, y: y)
  }

  func decreaseLife(by factor: Int) {
    life -= factor
  }

}

struct Location {
  let latitude: Double
  let longitude: Double
}

class Business {
  var name: String
  var location: Location

  init(name: String, location: Location) {
    self.name = name
    self.location = location
  }
}

let someBusiness = Business(name: "Quiry", location: Location(latitude: 341, longitude: 82))
`}</code></pre>
    <hr></hr>
    <h2 {...{
      "id": "inheritance"
    }}>{`Inheritance`}</h2>
    <pre><code parentName="pre" {...{}}>{`class SuperEnemy: Enemy {
  let isSuper: Bool = true

  override init(x: Int, y: Int) {
    super.init(x: x, y: y)
    self.life = 50
  }
}
`}</code></pre>
    <pre><code parentName="pre" {...{}}>{`class Vehicle {
  var numberOfDoors: Int
  var numberOfWheels: Int

  init(withDoors doors: Int, andWheels wheels: Int) {
      self.numberOfDoors = doors
      self.numberOfWheels = wheels
  }
}

class Car: Vehicle {
  var numberOfSeats: Int = 4

  override init(withDoors doors: Int, andWheels wheels: Int) {
    super.init(withDoors: doors, andWheels: wheels)
  }

}

let someCar = Car(withDoors: 4, andWheels: 4)
`}</code></pre>
    <pre><code parentName="pre" {...{}}>{`class Person {
  let firstName: String
  let lastName: String

  init(firstName: String, lastName: String) {
    self.firstName = firstName
    self.lastName = lastName
  }

  func fullName() -> String {
    return "\\(firstName) \\(lastName)"
  }
}

// Enter your code below
class Doctor: Person {

  override init(firstName: String, lastName: String) {
    super.init(firstName: firstName, lastName: lastName)
  }

  override func fullName() -> String {
    return "Dr. \\(lastName)"
  }
}

let someDoctor = Doctor(firstName: "Sam", lastName: "Smith")
`}</code></pre>
    <hr></hr>
    <h2 {...{
      "id": "structs-vs-classes"
    }}>{`Structs vs Classes`}</h2>
    <p>{`Distinct line in the sand.`}</p>
    <pre><code parentName="pre" {...{
        "className": "language-swift"
      }}>{`import UIKit

var str = "Hello, playground"


struct User {
  var fullName: String
  var email: String
  var age: Int
}

var someUser = User(fullName: "Denis O'Keeffe", email: "test@test", age: 24)
var anotherUser = someUser

someUser.email = "newemail@email"

// remains as test@test
anotherUser.email

class Person {
  var fullName: String
  var email: String
  var age: Int

  init(name: String, email: String, age: Int) {
    self.fullName = name
    self.email = email
    self.age = age
  }
}

var somePerson = Person(name: "Tim Cook", email: "tc@apple.com", age: 54)

var anotherPerson = somePerson

somePerson.email = "newemail@email"

// newemail@email -> points to the same reference
anotherPerson.email
`}</code></pre>
    <h2 {...{
      "id": "value-type-vs-reference-type"
    }}>{`Value type vs Reference type`}</h2>
    <p>{`Values are copied across, references are not. All `}<inlineCode parentName="p">{`structs`}</inlineCode>{` are value types.`}</p>
    </MDXLayout>;
}

