  import React from 'react'
  import { MDXTag } from '@mdx-js/tag'


export default class MDXContent extends React.Component {
  constructor(props) {
    super(props)
    this.layout = null
  }
  render() {
    const { components, ...props } = this.props

    return <MDXTag
             name="wrapper"

             components={components}><MDXTag name="h1" components={components} props={{"id":"aws-node-sdk"}}>{`AWS Node SDK`}</MDXTag>
{/* TOC */}
<MDXTag name="ul" components={components}>
<MDXTag name="li" components={components} parentName="ul"><MDXTag name="a" components={components} parentName="li" props={{"href":"#aws-node-sdk"}}>{`AWS Node SDK`}</MDXTag><MDXTag name="ul" components={components} parentName="li">
<MDXTag name="li" components={components} parentName="ul"><MDXTag name="a" components={components} parentName="li" props={{"href":"#loading-credentials-in-nodejs-from-environment-variables"}}>{`Loading Credentials in Node.js from Environment Variables`}</MDXTag></MDXTag>
<MDXTag name="li" components={components} parentName="ul"><MDXTag name="a" components={components} parentName="li" props={{"href":"#example-use-in-a-task-runner---get-ec2-details-back"}}>{`Example use in a task runner - get EC2 Details back`}</MDXTag></MDXTag>
</MDXTag></MDXTag>
</MDXTag>
{/* /TOC */}
<MDXTag name="h2" components={components} props={{"id":"loading-credentials-in-nodejs-from-environment-variables"}}>{`Loading Credentials in Node.js from Environment Variables`}</MDXTag>
<MDXTag name="ul" components={components}>
<MDXTag name="li" components={components} parentName="ul">{`AWS_ACCESS_KEY_ID`}</MDXTag>
<MDXTag name="li" components={components} parentName="ul">{`AWS_SECRET_ACCESS_KEY`}</MDXTag>
<MDXTag name="li" components={components} parentName="ul">{`AWS_SESSION_TOKEN (optional)`}</MDXTag>
</MDXTag>
<MDXTag name="p" components={components}>{`These can be set into your ENV variables.`}</MDXTag>
<MDXTag name="h2" components={components} props={{"id":"example-use-in-a-task-runner---get-ec2-details-back"}}>{`Example use in a task runner - get EC2 Details back`}</MDXTag>
<MDXTag name="pre" components={components}><MDXTag name="code" components={components} parentName="pre" props={{"className":"language-javascript"}}>{`var fs = require('fs');
const util = require('util');
var dotenv = require('dotenv');
var envConfig = dotenv.parse(fs.readFileSync('.env'));
for (var k in envConfig) {
    process.env[k] = envConfig[k];
}

var gulp = require('gulp');

// Load the SDK for JavaScript
var AWS = require('aws-sdk');

var params = {
    InstanceIds: [process.env.EC2_INSTANCE_ID]
};

/* Get EC2 Details */
gulp.task('info', function() {
    AWS.config = {
        accessKeyId: process.env.AWS_ACCESS_KEY,
        secretAccessKey: process.env.AWS_SECRET_KEY,
        region: process.env.AWS_REGION
    };

    // Create EC2 service object
    ec2 = new AWS.EC2({ apiVersion: '2016-11-15' });

    // Call EC2 to retrieve the policy for selected bucket
    ec2.describeInstances(params, function(err, data) {
        if (err) {
            console.log('Error', err.stack);
        } else {
            console.log('Success', util.inspect(data, { depth: 6 }));
        }
    });
});
`}</MDXTag></MDXTag>
           </MDXTag>
  }
}
