<MDXTag name="p" components={components}>{`The other option sometimes would be to instead of using the constructor, we could us onChange={ () => this.onInputChange }`}</MDXTag>
<MDXTag name="p" components={components}><MDXTag name="strong" components={components} parentName="p">{`In containers > SearchBar.js`}</MDXTag></MDXTag>
<MDXTag name="pre" components={components}><MDXTag name="code" components={components} parentName="pre" props={{"className":"language-javascript"}}>{`import React, { Component } from 'react'

export default class SearchBar extends Component {

    contructor(props) {
        super(props);

        this.state = { term: '' };

        this.onInputChage = this.onInputChange.bind(this);
    }

    onInputChange(event) {
        console.log(event.target.value);
        this.setState({ term: event.target.value });
    }

    render() {
        return (
            <form className="input-group">
                <input
                    placeholder="Get a 5-day forecast in your favourite cities"
                    className="form-control"
                    value={this.state.term}
                    onChange={this.onInputChange}
                />
                <span className="input-group-btn">
                    <button type="submit" className="btn btn-secondary">Submit</button>
                </span>
            </form>
        )
    }
}
`}</MDXTag></MDXTag>
           </MDXTag>
  }
}
