/* @jsx mdx */
  import React from 'react'
  import { mdx } from '@mdx-js/react'
  /* @jsx mdx */


const makeShortcode = name => function MDXDefaultShortcode(props) {
  console.warn("Component " + name + " was not imported, exported, or provided by MDXProvider as global scope")
  return <div {...props}/>
};

const layoutProps = {

};
const MDXLayout = "wrapper"
export default function MDXContent({
  components,
  ...props
}) {
  return <MDXLayout {...layoutProps} {...props} components={components} mdxType="MDXLayout">
    <h1 {...{
      "id": "terraform"
    }}>{`Terraform`}</h1>
    <h2 {...{
      "id": "course-content"
    }}>{`Course Content`}</h2>
    <pre><code parentName="pre" {...{
        "className": "language-bash"
      }}>{`# Use this to get the content
$ git clone https://github.com/wardviaene/terraform-course
`}</code></pre>
    <h2 {...{
      "id": "useful-terraform-commands"
    }}>{`Useful Terraform Commands`}</h2>
    <pre><code parentName="pre" {...{
        "className": "language-bash"
      }}>{`$ terraform plan # plan
$ terraform apply # shortcut for plan and apply - avoid in production
$ terraform plan -out out.terraform # terraform and write the plan to out file
$ terraform apply out.terraform # apply terraform plan using out file
$ terraform show # show current state
$ cat terraform.tfstate
`}</code></pre>
    <h2 {...{
      "id": "what-is-terraform"
    }}>{`What is Terraform?`}</h2>
    <ul>
      <li parentName="ul">{`Infrastructure as `}<strong parentName="li">{`code`}</strong></li>
      <li parentName="ul"><strong parentName="li">{`Automation`}</strong>{` of infrastructure`}</li>
      <li parentName="ul">{`Keep our infrastructure in a certain state (compliant)`}<ul parentName="li">
          <li parentName="ul">{`e.g. 2 web instances with 2 volumes + 1 load balancer`}</li>
        </ul></li>
      <li parentName="ul">{`Make infrastructure `}<strong parentName="li">{`auditable`}</strong><ul parentName="li">
          <li parentName="ul">{`You can keep your infrastructure change history in a `}<strong parentName="li">{`version control`}</strong>{` system like GIT`}</li>
        </ul></li>
      <li parentName="ul">{`Terraform can automate provisioning of the `}<strong parentName="li">{`infrastructure itself`}</strong>{`. `}</li>
    </ul>
    <h2 {...{
      "id": "installation-of-terraform"
    }}>{`Installation of Terraform`}</h2>
    <p>{`Head onto the Terraform website, download the .zip file and then move the binary after unzipping into your path to access it from the CLI.`}</p>
    <h2 {...{
      "id": "hello-terraform"
    }}>{`Hello Terraform!`}</h2>
    <p>{`Example hello using Terraform to spin up an EC2 micro instance.`}</p>
    <pre><code parentName="pre" {...{
        "className": "language-kotlin"
      }}>{`provider "aws" {
  access_key = "ACCESS_KEY_HERE"
  secret_key = "SECRET_KEY_HERE"
  region     = "ap-southeast-1"
}

resource "aws_instance" "example" {
  ami           = "ami-0d729a60"
  instance_type = "t2.micro"
}
`}</code></pre>
    <p>{`To initialise and spin up the instance, run the following:`}</p>
    <pre><code parentName="pre" {...{
        "className": "language-bash"
      }}>{`$ terraform init
$ terraform plan -out file.terraform
$ terraform apply file.terraform
$ terraform destory # tear down the instance
`}</code></pre>
    <h2 {...{
      "id": "variables"
    }}>{`Variables`}</h2>
    <p>{`To create variables, we create two files `}<inlineCode parentName="p">{`provider.tf`}</inlineCode>{` and `}<inlineCode parentName="p">{`vars.tf`}</inlineCode>{` that we can commit and one git ignored file `}<inlineCode parentName="p">{`terraform.tfvars`}</inlineCode>{` to store these private variables in.`}</p>
    <pre><code parentName="pre" {...{
        "className": "language-kotlin"
      }}>{`# provider.tf
provider "aws" {
    access_key="\${var.AWS_ACCESS_KEY}"
    secret_key="\${var.AWS_SECRET_KEY}"
    region="\${var.AWS_REGION}"
}

# vars.tf
variable "AWS_ACCESS_KEY" {}
variable "AWS_SECRET_KEY" {}
variable "AWS_REGION" {
    default = "ap-southeast-2"
}

# terraform.tfvars
AWS_ACCESS_KEY=""
AWS_SECRET_KEY=""
AWS_REGION=""
`}</code></pre>
    <p>{`For env vars: Terraform will read environment variables in the form of `}<inlineCode parentName="p">{`TF_VAR_name`}</inlineCode>{` to find the value for a variable. For example, the `}<inlineCode parentName="p">{`TF_VAR_access_key`}</inlineCode>{` variable can be set to set the `}<inlineCode parentName="p">{`access_key`}</inlineCode>{` variable.`}</p>
    <h3 {...{
      "id": "lookup-examples"
    }}>{`Lookup examples`}</h3>
    <pre><code parentName="pre" {...{
        "className": "language-kotlin"
      }}>{`# instance.tf
resource "aws_instance" "example" {
  ami           = "\${lookup(var.AMIS, var.AWS_REGION)}"
  instance_type = "t2.micro"
}

# provider.tf
provider "aws" {
    access_key = "\${var.AWS_ACCESS_KEY}"
    secret_key = "\${var.AWS_SECRET_KEY}"
    region = "\${var.AWS_REGION}"
}

# vars.tf
variable "AWS_ACCESS_KEY" {}
variable "AWS_SECRET_KEY" {}
variable "AWS_REGION" {
  default = "eu-west-1"
}
variable "AMIS" {
  type = "map"
  default = {
    us-east-1 = "ami-13be557e"
    us-west-2 = "ami-06b94666"
    eu-west-1 = "ami-0d729a60"
  }
}

# terraform.tfvars
AWS_ACCESS_KEY=""
AWS_SECRET_KEY=""
AWS_REGION=""
`}</code></pre>
    <p>{`For info on which ami results from where, checkout `}<a parentName="p" {...{
        "href": "https://cloud-images.ubuntu.com/locator/ec2/"
      }}>{`https://cloud-images.ubuntu.com/locator/ec2/`}</a>{`.`}</p>
    <h2 {...{
      "id": "software-provisioning"
    }}>{`Software Provisioning`}</h2>
    <p>{`There are 2 ways to provision software:`}</p>
    <ol>
      <li parentName="ol">
        <p parentName="li">{`Build your own custom AMI (ie Packer).`}</p>
      </li>
      <li parentName="ol">
        <p parentName="li">{`Another way is to be standardized AMIs and then install the software you need on it: `}</p>
        <ul parentName="li">
          <li parentName="ul">
            <p parentName="li">{`Use file uploads`}</p>
          </li>
          <li parentName="ul">
            <p parentName="li">{`Use remote exec`}</p>
          </li>
          <li parentName="ul">
            <p parentName="li">{`Use automation tools like chef, puppet, ansible`}</p>
          </li>
        </ul>
      </li>
    </ol>
    <h3 {...{
      "id": "file-uploads"
    }}>{`File Uploads`}</h3>
    <pre><code parentName="pre" {...{
        "className": "language-kotlin"
      }}>{`# instance.tf
resource "aws_instance" "example" {
    ami           = "\${lookup(var.AMIS, var.AWS_REGION)}"
    instance_type = "t2.micro"

    provisioner "file" {
        source = "app.conf"
        destination = "/etc/myapp.conf"
    }
}
`}</code></pre>
    <p>{`The provisioner will have to use SSH etc.`}</p>
    <p>{`Another example with the connection:`}</p>
    <pre><code parentName="pre" {...{
        "className": "language-kotlin"
      }}>{`# instance.tf
resource "aws_instance" "example" {
    ami           = "\${lookup(var.AMIS, var.AWS_REGION)}"
    instance_type = "t2.micro"
    # IF SSH
    key_name = "\${aws_key_pem.mykey.key_name}"
    # ENDIF

    provisioner "file" {
        source = "app.conf"
        destination = "/etc/myapp.conf"
        # ! FOR PASSWORD
        connection {
            user = "\${var.instance_username}"
            password = "\${var.instance_password}"
        }

        connection {
            user = "\${var.instance_username}"
            private_key = "\${file(\${var.path_to_private_key})}"
        }
    }

    # IF YOU UPLOAD A .sh FILE AND WANT TO RUN IT
    provisioner "remote-exec" {
        inline = [
            "chmod +x /path/to/script.sh",
            "/path/to/script.sh arguments"
        ]
    }
}
`}</code></pre>
    <h2 {...{
      "id": "attributes"
    }}>{`Attributes`}</h2>
    <h2 {...{
      "id": "state"
    }}>{`State`}</h2>
    <p>{`Terraform keeps state in `}<inlineCode parentName="p">{`terraform.tfstate`}</inlineCode>{`.`}</p>
    <p>{`To configure, you add a file `}<inlineCode parentName="p">{`backend.tf`}</inlineCode>{`:`}</p>
    <pre><code parentName="pre" {...{
        "className": "language-kotlin"
      }}>{`terraform {
    # IF Consul
    backend "consul" {
        address = "demo.consul.io" # host name of consul cluster
        path = "terraform/myproject"
    }

    # ELSEIF S3
    backend "s3" {
        bucket = "bucket"
        key = "terraform/myproject"
        region = "ap-southeast-2"
    }

    #ENDIF
}
`}</code></pre>
    <p><inlineCode parentName="p">{`$ terraform init`}</inlineCode>{` will do what you require for it all to work.`}</p>
    <h2 {...{
      "id": "data-sources"
    }}>{`Data Sources`}</h2>
    <p>{`Provide you with dynamic information.`}</p>
    <ul>
      <li parentName="ul">{`A lot of data is available by AWS in a structured format using their API`}</li>
      <li parentName="ul">{`Terraform also exposes this info using data sources.`}</li>
    </ul>
    <p>{`Examples include the list of AMIs, list of AZs etc.`}</p>
    <pre><code parentName="pre" {...{
        "className": "language-kotlin"
      }}>{`# provider.tf
provider "aws" { 
    region = "\${var.AWS_REGION}"
}

# securitygroup.tf
data "aws_ip_ranges" "european_ec2" {
  regions = [ "eu-west-1", "eu-central-1" ]
  services = [ "ec2" ]
}

resource "aws_security_group" "from_europe" {
 name = "from_europe"

  ingress {
    from_port = "443"
    to_port = "443"
    protocol = "tcp"
    cidr_blocks = [ "\${data.aws_ip_ranges.european_ec2.cidr_blocks}" ]
  }
  tags {
    CreateDate = "\${data.aws_ip_ranges.european_ec2.create_date}"
    SyncToken = "\${data.aws_ip_ranges.european_ec2.sync_token}"
  }

}

# vars.tf
variable "AWS_REGION" {
  default = "eu-west-1"
}
variable "AMIS" {
  type = "map"
  default = {
    us-east-1 = "ami-13be557e"
    us-west-2 = "ami-06b94666"
    eu-west-1 = "ami-844e0bf7"
  }
}
`}</code></pre>
    <h2 {...{
      "id": "templates"
    }}>{`Templates`}</h2>
    <p>{`Can help create `}<strong parentName="p">{`customized configuration files`}</strong>{`.`}</p>
    <h2 {...{
      "id": "modules"
    }}>{`Modules`}</h2>
    <p>{`Help to make your terraform more organised. `}</p>
    <p>{`You can also use third party modules (like modules from Github).`}</p>
    <p>{`It also helps to `}<strong parentName="p">{`reuse`}</strong>{` parts of your code (eg set up network in AWS - VPC).`}</p>
    <pre><code parentName="pre" {...{
        "className": "language-kotlin"
      }}>{`# Install form git
module "module-example" {
    source = "github.com/wardviaene/terraform-module-example"
}

# Use module from local folder
module "module-example" {
    source = "./module-example"
}

# A module passing arguments 
module "module-example" {
    source = "./module-example"
    region = "us-west-1"
    ip-range = "10.0.0.0/8"
    cluster-size = "3"
}
`}</code></pre>
    <p>{`Inside of the module folder, you can also have Terraform files:`}</p>
    <pre><code parentName="pre" {...{
        "className": "language-kotlin"
      }}>{`# module-example/vars.tf
variable "region" {}
variable "ip-range" {}
variable "cluster-size" {}

# module-example/cluster.tf
resource "aws_instance" "instance-1" { ... }
resource "aws_instance" "instance-2" { ... }
resource "aws_instance" "instance-3" { ... }

# module-example/output.tf
output "aws-cluster" {
    value = "\${aws_instance.instance-1.publicip}"
}
`}</code></pre>
    <p>{`You use the output from the module in the main part of your code!`}</p>
    <pre><code parentName="pre" {...{
        "className": "language-kotlin"
      }}>{`output "some-output" {
    value = "\${module.module-example.aws-cluster}"
}
`}</code></pre>
    <h3 {...{
      "id": "downloading-a-module"
    }}>{`Downloading a module`}</h3>
    <pre><code parentName="pre" {...{
        "className": "language-kotlin"
      }}>{`terraform get # this will fetch the modules and store it in .terraform/modules/
`}</code></pre>
    <h2 {...{
      "id": "terraform-command-overview"
    }}>{`Terraform Command Overview`}</h2>
    <table>
      <thead parentName="table">
        <tr parentName="thead">
          <th parentName="tr" {...{
            "align": null
          }}>{`Command`}</th>
          <th parentName="tr" {...{
            "align": null
          }}>{`Description`}</th>
        </tr>
      </thead>
      <tbody parentName="table">
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": null
          }}>{`terraform apply`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`Applies state.`}</td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": null
          }}>{`destroy`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`Destroys all Terraform managed state.`}</td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": null
          }}>{`fmt`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`Rewrite terraform config files to canonical format and style.`}</td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": null
          }}>{`get`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`Download and update modules.`}</td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": null
          }}>{`graph`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`Create a visual representation of a configuration or execution plan.`}</td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": null
          }}><inlineCode parentName="td">{`import <options> <ADDRESS> <ID>`}</inlineCode></td>
          <td parentName="tr" {...{
            "align": null
          }}>{`Find infrastructure resource identified with ID and import the state into terraform.tfstate with resource id ADDRESS.`}</td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": null
          }}><inlineCode parentName="td">{`output <options> <NAME>`}</inlineCode></td>
          <td parentName="tr" {...{
            "align": null
          }}>{`Output any of your resources.`}</td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": null
          }}>{`plan`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`Show changes made to infrastructure.`}</td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": null
          }}>{`refresh`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`Refresh the remote state. Can identify differences between state file and remote state.`}</td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": null
          }}>{`remote`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`Configure remote state storage.`}</td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": null
          }}>{`show`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`Show human readable output from a state or a plan.`}</td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": null
          }}>{`state`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`Used for advanced state management eg rename resource`}</td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": null
          }}>{`taint`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`Destroy and recreate resource.`}</td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": null
          }}>{`validate`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`Validate terraform syntax.`}</td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": null
          }}>{`untaint`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`Undo a taint.`}</td>
        </tr>
      </tbody>
    </table>
    <h2 {...{
      "id": ""
    }}></h2>
    </MDXLayout>;
}

MDXContent.isMDXComponent = true;
