  return <MDXLayout {...layoutProps} {...props} components={components} mdxType="MDXLayout">
    <h1 {...{
      "id": "typescript--github-example"
    }}>{`TypeScript + Github Example`}</h1>
    <p>{`In this example, we are going to create a Zeit Now project that we can use to update files on a repo based on what is passed.`}</p>
    <h2 {...{
      "id": "setting-up"
    }}>{`Setting up`}</h2>
    <p>{`Initialise a project in a folder `}<inlineCode parentName="p">{`yarn init -y`}</inlineCode>{`.`}</p>
    <p>{`Prerequisites:`}</p>
    <pre><code parentName="pre" {...{
        "className": "language-shell"
      }}>{`mkdir project
cd project
yarn init -y # or npm
npm i -g now # or yarn
mkdir api
touch api/example.ts tsconfig.json now.json .env .gitignore test.json
yarn add --dev @now/node @types/js-base64
yarn add @octokit/rest js-base64
`}</code></pre>
    <h2 {...{
      "id": "api-setup"
    }}>{`API Setup`}</h2>
    <p>{`Add the following to `}<inlineCode parentName="p">{`api/example.ts`}</inlineCode>{`:`}</p>
    <pre><code parentName="pre" {...{
        "className": "language-typescript"
      }}>{`import { NowRequest, NowResponse } from '@now/node';
import Octokit from '@octokit/rest';
import { Base64 } from 'js-base64';

const gh = new Octokit({
  auth: process.env.GITHUB_TOKEN,
});
const owner = 'your_github_user_name';

// some helper functions for creating a repo
// and writing a file - we won't update in this example
const createOrUpdateFile = async (
  repo: string,
  content: string,
  sha?: string,
) => {
  try {
    return gh.repos.createOrUpdateFile({
      owner,
      repo,
      sha,
      path: 'hello.json',
      message: 'Updating the Hello JSON file',
      content: Base64.encode(JSON.stringify(content)),
    });
  } catch (err) {
    // basic handling
    console.error(err.message);
    return undefined;
  }
};

const createRepo = async (repo: string) => {
  try {
    await gh.repos.createForAuthenticatedUser({
      name: repo,
      private: true,
    });
  } catch (err) {
    // basic handling
    console.error(err.message);
    return undefined;
  }
};

export default async (_req: NowRequest, res: NowResponse) => {
