/* @jsx mdx */
  import React from 'react'
  import { mdx } from '@mdx-js/react'
  /* @jsx mdx */


const makeShortcode = name => function MDXDefaultShortcode(props) {
  console.warn("Component " + name + " was not imported, exported, or provided by MDXProvider as global scope")
  return <div {...props}/>
};

const layoutProps = {

};
const MDXLayout = "wrapper"
export default function MDXContent({
  components,
  ...props
}) {
  return <MDXLayout {...layoutProps} {...props} components={components} mdxType="MDXLayout">
    <h1 {...{
      "id": "introducing-es2015"
    }}>{`Introducing ES2015`}</h1>
    <h2 {...{
      "id": "es6-1-es2015-basics"
    }}>{`ES6-1: ES2015 Basics`}</h2>
    <p>{`6th Edition to ECMA Script`}</p>
    <h3 {...{
      "id": "es6-2-getting-started-with-es2015"
    }}>{`ES6-2: Getting Started with ES2015`}</h3>
    <ul>
      <li parentName="ul">{`biggest change up to JS since its inception`}</li>
      <li parentName="ul">{`JS built in 10 days`}</li>
      <li parentName="ul">{`Microsoft used Jscript`}</li>
      <li parentName="ul">{`ECMAScript and JavaScript are the same (ECMA named 1997)`}</li>
      <li parentName="ul">{`ECMAScript 5 (2009)`}</li>
      <li parentName="ul">{`Most browsers are using the latest`}</li>
      <li parentName="ul">{`Babel can be used to compile for those using older browsers`}</li>
    </ul>
    <p><strong parentName="p">{`Let and Const`}</strong></p>
    <ul>
      <li parentName="ul">{`so far, we need to be on top of scope`}</li>
    </ul>
    <pre><code parentName="pre" {...{
        "className": "language-javascript"
      }}>{`'use strict';

var hello = 'hello'

// ecma2015

A block can be either a loop, if statement or function

'use strict'

(function initLoop() {
    function doLoop(x) {
        console.log('loop: ', x);
    }

    for (var i=0; i < 10; i++) {
        doLoop(i + 1);
    }
})
`}</code></pre>
    <p>{`...what can go wrong? Dirty Read`}</p>
    <pre><code parentName="pre" {...{
        "className": "language-javascript"
      }}>{`'use strict'

(function initLoop() {
    function doLoop(x) {
        i=3;
        console.log('loop: ', x);
    }

    for (var i=0; i < 10; i++) {
        doLoop(i + 1);
    }
})

We can fix this with the let keyword

'use strict'

(function initLoop() {
    function doLoop(x) {
        // i=3; -> this won't be allowed
        console.log('loop: ', x);
    }

    for (let i=0; i < 10; i++) {
        doLoop(i + 1);
    }
})
`}</code></pre>
    <p><strong parentName="p">{`this will throw an error at us!`}</strong></p>
    <h3 {...{
      "id": "es6-3-duplicate-values"
    }}>{`ES6-3: Duplicate values`}</h3>
    <pre><code parentName="pre" {...{
        "className": "language-javascript"
      }}>{`'use strict';

var student = { name: 'Ken' };
var student = { name: 'James' };

console.log(student);

// student become James

'use strict';

const student = { name: 'Ken' };
var student = { name: 'James' };

console.log(student);

// we will now get an exception (happens for let or const)

- you can have different const variables of the same name for different constants

**not allowed**

... {
    const student = 'test';
    ... {
        student = 'test'
    }
}

**allowed**

... {
    let student = 'test';
    ... {
        student = 'test'
    }
}
`}</code></pre>
    <p><strong parentName="p">{` Use let when you need to reassign, use const when you do not want the value to change. `}</strong></p>
    <h3 {...{
      "id": "es6-4-template-strings"
    }}>{`ES6-4: Template Strings`}</h3>
    <p><strong parentName="p">{` ES5 Strings `}</strong></p>
    <p>{`let str = 'My favourite name is';`}</p>
    <p>{`console.log(str, 5);`}</p>
    <p><strong parentName="p">{` ES2015 Strings `}</strong></p>
    <p>{`// interpolation`}</p>
    <p>{`const student = { name: 'James', followerCount: 34 }`}</p>
    <p>{`\${student.name}`}</p>
    <p><inlineCode parentName="p">{`<this stuff is neatly formatted>`}</inlineCode></p>
    <h3 {...{
      "id": "es6-5-string-search-methods"
    }}>{`ES6-5: String search methods`}</h3>
    <p>{`new functions:`}</p>
    <p>{`startsWith
endsWith
includes`}</p>
    <p>{`console.log(strToSearch.startsWith('example-at-start'));
console.log(strToSearch.endsWith('example-at-end'));
console.log(strToSearch.includes('example-in-the-middle'));`}</p>
    <p><strong parentName="p">{` all take an optional parameter `}</strong></p>
    <p>{`startsWith('example', 5); //start search from 5th index
endsWith('example', 21); //searches the first 21 characters`}</p>
    <hr></hr>
    <h2 {...{
      "id": "es6-6-the-cooler-parts-of-es2015"
    }}>{`ES6-6: The Cooler Parts of ES2015`}</h2>
    <h3 {...{
      "id": "es6-7-arrow-functions"
    }}>{`ES6-7: Arrow Functions`}</h3>
    <ul>
      <li parentName="ul">{`Bound to its parent scope`}</li>
    </ul>
    <p>{`New syntax for writing functions`}</p>
    <ul>
      <li parentName="ul">{`referred to as Lamda functions in other languages`}</li>
    </ul>
    <p><strong parentName="p">{` ES5 `}</strong></p>
    <pre><code parentName="pre" {...{
        "className": "language-javascript"
      }}>{`'use strict';

var Person = function (data) {
  for (var key in data) {
    this[key] = data[key];  
  }
  this.getKeys = function () {
    return Object.keys(this);
  }
}

var Alena = new Person({ name: 'Alena', role: 'Teacher' });

console.log('Alena\\'s Keys:', Alena.getKeys()); // 'this' refers to 'Alena'

var getKeys = Alena.getKeys;

console.log(getKeys()); // 'this' refers to the node process

// throws error get getKeys()
`}</code></pre>
    <p><strong parentName="p">{`ES2015`}</strong></p>
    <pre><code parentName="pre" {...{
        "className": "language-javascript"
      }}>{`'use strict';

var Person = function (data) {
  for (var key in data) {
    this[key] = data[key];  
  }
  this.getKeys = () => {
    return Object.keys(this);
  }
}

var Alena = new Person({ name: 'Alena', role: 'Teacher' });

console.log('Alena\\'s Keys:', Alena.getKeys()); // 'this' refers to 'Alena'

var getKeys = Alena.getKeys;

console.log(getKeys()); // 'this' refers to the node process

// this fixes it!
`}</code></pre>
    <p>{`The lamda function binds the function to the instance of the person no matter where it was called`}</p>
    <p><strong parentName="p">{` Promises `}</strong></p>
    <pre><code parentName="pre" {...{
        "className": "language-javascript"
      }}>{`'use strict';

var Teacher = function (data) {
  this.name = data.name;
  this.greet = function (studentCnt) {
    let promise = new Promise(function (resolve, reject) {
      if (studentCnt === 0) {
        reject('Zero students in class');
      } else {
        resolve(\`Hello to \${studentCnt} of my favorite students!\`);
      }
    });
    return promise;
  }
}

var Classroom = function (data) {
  this.subject = data.name;
  this.teacher = data.teacher;
  this.students = [];
  this.addStudent = function (data) {
    this.students.push(data);
    this.greet();
  }
  this.greet = () => {
    this.teacher.greet(this.students.length).then(
      (function (classroom) {
        return function (greeting) {
          console.log(\`\${classroom.teacher.name} says: \`, greeting);
        }
      })(this),
      function (err) {
        console.log(err);
      })
  }
}

var myTeacher = new Teacher({ name: 'James' });
var myClassroom = new Classroom({ name: 'The Future of JavaScript', teacher: myTeacher });

myClassroom.addStudent({ name: 'Dave' });
`}</code></pre>
    <p><strong parentName="p">{`ES 2015 Promises`}</strong></p>
    <pre><code parentName="pre" {...{
        "className": "language-javascript"
      }}>{`'use strict';

var Teacher = function (data) {
  this.name = data.name;
  this.greet = function (studentCnt) {
    let promise = new Promise(function (resolve, reject) {
      if (studentCnt === 0) {
        reject('Zero students in class');
      } else {
        resolve(\`Hello to \${studentCnt} of my favorite students!\`);
      }
    });
    return promise;
  }
}

var Classroom = function (data) {
  this.subject = data.name;
  this.teacher = data.teacher;
  this.students = [];
  this.addStudent = function (data) {
    this.students.push(data);
    this.greet();
  }
  this.greet = () => {
    this.teacher.greet(this.students.length).then(
      greeting => console.log(\`\${this.classroom.teacher.name} says: \`, greeting);
      error => console.log(err);
  }
}

var myTeacher = new Teacher({ name: 'James' });
var myClassroom = new Classroom({ name: 'The Future of JavaScript', teacher: myTeacher });

myClassroom.addStudent({ name: 'Dave' });
`}</code></pre>
    <h3 {...{
      "id": "es6-8-default-parameters"
    }}>{`ES6-8: Default Parameters`}</h3>
    <p>{`Set default parameters for a function -> similar to other languages`}</p>
    <p><strong parentName="p">{`ES5`}</strong></p>
    <pre><code parentName="pre" {...{
        "className": "language-javascript"
      }}>{`'use strict'

function greet(name, timeOfDay) {
    name = name || 'Guil';
    timeofDay = timeOfDay || 'Day';
    ...
}

greet();

**ES 2015**

'use strict'

function greet(name = 'Guil', timeOfDay = 'Day') {
    ...
}

greet(undefined, 'Afternoon');  // defaults are optional, need to undefine
`}</code></pre>
    <h3 {...{
      "id": "es6-9-rest-parameters-and-spread-operator"
    }}>{`ES6-9: Rest Parameters and Spread Operator`}</h3>
    <p><strong parentName="p">{`How to use the rest parameters`}</strong></p>
    <ul>
      <li parentName="ul">{`rest must be the last parameter`}</li>
    </ul>
    <pre><code parentName="pre" {...{
        "className": "language-javascript"
      }}>{`function myFunction(name, ...params) {
    console.log(name, params);
}

myFunction('Andrew', 1, 2, 3);

// output Andrew [1, 2, 3]
`}</code></pre>
    <p><strong parentName="p">{`Spread parameters`}</strong></p>
    <pre><code parentName="pre" {...{
        "className": "language-javascript"
      }}>{`const originalFlavors = ['Chocolate', 'Vanilla'];

const newFlavors = ['Strawberry', 'Mint Chocolate Chip'];

const inventory = ['Rocky Road', ...originalFlavors, 'Neopolitan', ...newFlavors];

console.log(inventory);

// spitting an array and them using them as arguments using the spread operator

function myFunction(name, iceCreamFlavor) {
    console.log('\${name} really likes \${iceCreamFlavor} ice cream.')
}

let args = ['Gabe', 'Vanilla'];

myFunction(...args);    // sends as separate arguments
`}</code></pre>
    <h3 {...{
      "id": "es6-10-destructuring"
    }}>{`ES6-10: Destructuring`}</h3>
    <p>{`Relatively simple concept`}</p>
    <p>{`Let's you extract values from arrays or objects`}</p>
    <pre><code parentName="pre" {...{
        "className": "language-javascript"
      }}>{`let toybox = { item1: 'car', item2: 'ball', item3: 'frisbee' };

let { item3: disc} = toybox;

console.log(disc);

// logs item3 value

let widgets = ['wid1','wid2','wid3','wid4','wid5'];

let [a,b,c, ...d] = widgets;
`}</code></pre>
    <hr></hr>
    <h2 {...{
      "id": "es6-11-objects-and-new-collection-types"
    }}>{`ES6-11: Objects and New Collection Types`}</h2>
    <h3 {...{
      "id": "es6-12-object-property-shorthand"
    }}>{`ES6-12: Object Property Shorthand`}</h3>
    <pre><code parentName="pre" {...{
        "className": "language-javascript"
      }}>{`function submit(name, comments, rating = 5) {
    let data = { name, comments, rating };

    for (let key in data) {
        console.log(key + ':', data[key]);
    }

    // ... do ajax request
}

submit('English', 'Great course!');

// without a default name, the key because the parameter for the object
// works with all forms
`}</code></pre>
    <h3 {...{
      "id": "es6-13-forof"
    }}>{`ES6-13: for...of`}</h3>
    <p>{`For iterating over a data set`}</p>
    <pre><code parentName="pre" {...{
        "className": "language-javascript"
      }}>{`// previous for (let ... )
// ES5 gave us for each

let myNumbers = [1,2,3,4,5,6];

for (let number of myNumbers) {
    ...
    if (example) {
        break; // you can break!
    }
}

// still use for...in for iterating over regular objects
`}</code></pre>
    <h3 {...{
      "id": "es6-14-set"
    }}>{`ES6-14: Set`}</h3>
    <pre><code parentName="pre" {...{
        "className": "language-javascript"
      }}>{`let classroom = new Set(); // unique collection of values

let stevenJ = { name: 'Steven', age: 22 },
    sarah = { name: 'Sarah', age: 23 },
    stevenS = { name: 'Steve', age: 22 }

classroom.add(stevenJ);
classroom.add(sarah);
classroom.add(stevenS);

if (classroom.has(stevenJ)) console.log('stevenJ is in the classroom'); //true
if (classroom.has(sarah)) console.log('sarah is in the classroom'); //true
if (classroom.has(stevenS)) console.log('sarah is in the classroom'); //true

BUT IF stevenS = stevenJ

let stevenJ = { name: 'Steven', age: 22 },
    sarah = { name: 'Sarah', age: 23 },
    stevenS = stevenJ;

classroom.add(stevenJ);
classroom.add(sarah);
classroom.add(stevenS);

if (classroom.has(stevenJ)) console.log('stevenJ is in the classroom'); //true
if (classroom.has(sarah)) console.log('sarah is in the classroom'); //true
if (classroom.has(stevenS)) console.log('sarah is in the classroom'); //true

console.log(classroom.size()); //3

classroom.delete(stevenJ)

console.log(classroom.size()); //2

// Create array of students from the classroom set

let arrayOfStrudents = Array.from(classroom);
console.log(arrayOfStudents);

// Create set from set

let alumni = new Set(arrayOfStudents);
`}</code></pre>
    <h3 {...{
      "id": "es6-15-map"
    }}>{`ES6-15: Map`}</h3>
    <pre><code parentName="pre" {...{
        "className": "language-javascript"
      }}>{`'use strict'

let classroom = new Map();

let stevenJ = { name: 'Steven', age: 22 },
    sarah = { name: 'Sarah', age: 23 },
    stevenS = { name: 'Steven', age: 22 };

classroom.set('stevenJ', stevenJ);
classroom.set('sarah',sarah);
classroom.set('stevenS', stevenS);

console.log(classroom.size);

if (classroom.has('stevenJ')) console.log('stevenJ is in the classroom');
if (classroom.has('sarah')) console.log('sarah is in the classroom');
if (classroom.has('stevenS')) console.log('stevenS is in the classroom');

console.log('sarah:', classroom.get('sarah'));

classroom.delete('sarah');
classroom.clear();        //deletes all

for (let student of classroom) {
  console.log('\${student[0]} : \${student[1].name} is \${student[1].age} years old');
}
`}</code></pre>
    <h2 {...{
      "id": "es6-16-classes"
    }}>{`ES6-16: Classes`}</h2>
    <h3 {...{
      "id": "es6-17-structure-of-a-class"
    }}>{`ES6-17: Structure of a Class`}</h3>
    <p>{`A class is a blueprint for those that share similar properties or methods`}</p>
    <pre><code parentName="pre" {...{
        "className": "language-javascript"
      }}>{`// old way

let Student = function(data) {
  this.name = data.name;
  this.age = data.age
}

// new way

class Student {
  constructor({ name, age, interestLevel = 5 } = {}) {
    this.name = name;
    this.age = age;
    this.interestLevel = interestLevel;
    this.grades = new Map();
  }
}

let sarah = new Student('Sarah', 11);

console.log(Array.from(sarah.grades));
`}</code></pre>
    <h3 {...{
      "id": "es6-18-subclasses"
    }}>{`ES6-18: Subclasses`}</h3>
    <ul>
      <li parentName="ul">{`classes can inherit from other classes`}</li>
      <li parentName="ul">{`var hoisting`}</li>
      <li parentName="ul">{`destructuring is one way to set default values for class properties`}</li>
    </ul>
    <pre><code parentName="pre" {...{
        "className": "language-javascript"
      }}>{`bla = 2
var bla;
// ...

// is implicitly understood as:

var bla;
bla = 2;
`}</code></pre>
    <pre><code parentName="pre" {...{
        "className": "language-javascript"
      }}>{`class Person {
  dance() {
    const dances = [
      'waltz',
      'tango',
      'mambo'
    ];

    console.log(\${this.name} is doing the \${dances[Math.floor(Math.random()*dances.length)]}!);
  }
  constructor({ name, age, eyeColor = 'brown' } = {}) {
    this.name = name;
    this.age = age;
    this.eyeColor = eyeColor;
  }
}

class Student extends Person {

  dance(traditional) {
    if (traditional) {
      super.dance();
      return;
    }

    const dances = [
      'lyrical',
      'tap',
      'jazz'
    ];

    console.log(\${this.name} is doing the \${dances[Math.floor(Math.random()*dances.length)]}!);
  }

  constructor({ name, age, interestLevel = 5 } = {} ) {
    super({ name, age });
    this.name = name;
    this.age = age;
    this.interestLevel = interestLevel;
    this.grades = new Map;
  }
}

let stevenJ = new Student({name: 'Steven', age: 22, interestLevel: 3 });
stevenJ.dance();
`}</code></pre>
    <h2 {...{
      "id": "es6-19-static-methods"
    }}>{`ES6-19: Static Methods`}</h2>
    <pre><code parentName="pre" {...{
        "className": "language-javascript"
      }}>{`class Bird {
  static changeColor(color) {
    this.color = color;
  }
  constructor({ color = 'red' } = {}) {
    this.color = color;
  }
}

let redBird = new Bird;
console.log(redBird.color);
redBird.changeColor('blue');
console.log(redBird.color); // would call an error!

// what we could do

Bird.changeColor.call(redBird, 'blue');
console.log(redBird.color);

/* the other option is to change the changeColor function to accept (bird, color) and then change this.color to bird.color
*/
`}</code></pre>
    <h2 {...{
      "id": "es6-20-getter-and-setter-methods-in-a-class"
    }}>{`ES6-20: Getter and Setter methods in a class`}</h2>
    <pre><code parentName="pre" {...{
        "className": "language-javascript"
      }}>{`class Bird {
  changeColor(color) {
    this.color = color;
  }

  set color(color) {
    this.color = color;
  }

  get color() {
    return this.color;
  }

  constructor({ color = 'red' } = {}) {
    this.color = color;
  }
}
`}</code></pre>
    </MDXLayout>;
}

MDXContent.isMDXComponent = true;
