  import React from 'react'
  import { MDXTag } from '@mdx-js/tag'


export default class MDXContent extends React.Component {
  constructor(props) {
    super(props)
    this.layout = null
  }
  render() {
    const { components, ...props } = this.props

    return <MDXTag
             name="wrapper"

             components={components}><MDXTag name="h1" components={components} props={{"id":"introducing-es2015"}}>{`Introducing ES2015`}</MDXTag>
<MDXTag name="h2" components={components} props={{"id":"es6-1-es2015-basics"}}>{`ES6-1: ES2015 Basics`}</MDXTag>
<MDXTag name="p" components={components}>{`6th Edition to ECMA Script`}</MDXTag>
<MDXTag name="h3" components={components} props={{"id":"es6-2-getting-started-with-es2015"}}>{`ES6-2: Getting Started with ES2015`}</MDXTag>
<MDXTag name="ul" components={components}>
<MDXTag name="li" components={components} parentName="ul">{`biggest change up to JS since its inception`}</MDXTag>
<MDXTag name="li" components={components} parentName="ul">{`JS built in 10 days`}</MDXTag>
<MDXTag name="li" components={components} parentName="ul">{`Microsoft used Jscript`}</MDXTag>
<MDXTag name="li" components={components} parentName="ul">{`ECMAScript and JavaScript are the same (ECMA named 1997)`}</MDXTag>
<MDXTag name="li" components={components} parentName="ul">{`ECMAScript 5 (2009)`}</MDXTag>
<MDXTag name="li" components={components} parentName="ul">{`Most browsers are using the latest`}</MDXTag>
<MDXTag name="li" components={components} parentName="ul">{`Babel can be used to compile for those using older browsers`}</MDXTag>
</MDXTag>
<MDXTag name="p" components={components}><MDXTag name="strong" components={components} parentName="p">{`Let and Const`}</MDXTag></MDXTag>
<MDXTag name="ul" components={components}>
