/* @jsx mdx */
  import React from 'react'
  import { mdx } from '@mdx-js/react'
  /* @jsx mdx */


const makeShortcode = name => function MDXDefaultShortcode(props) {
  console.warn("Component " + name + " was not imported, exported, or provided by MDXProvider as global scope")
  return <div {...props}/>
};

const layoutProps = {

};
const MDXLayout = "wrapper"
export default function MDXContent({
  components,
  ...props
}) {
  return <MDXLayout {...layoutProps} {...props} components={components} mdxType="MDXLayout">
    <h1 {...{
      "id": "passkit"
    }}>{`PassKit`}</h1>
    <ul>
      <li parentName="ul"><a parentName="li" {...{
          "href": "#passkit"
        }}>{`PassKit`}</a>{`
- `}<a parentName="li" {...{
          "href": "#adding-the-certificate"
        }}>{`Adding the certificate`}</a>{`
- `}<a parentName="li" {...{
          "href": "#php-server"
        }}>{`PHP Server`}</a>{`
- `}<a parentName="li" {...{
          "href": "#plan-ticket-example"
        }}>{`Plan ticket example`}</a>{`
- `}<a parentName="li" {...{
          "href": "#store-card-example"
        }}>{`Store card example`}</a>{`
- `}<a parentName="li" {...{
          "href": "#more-info"
        }}>{`More info`}</a></li>
    </ul>
    <h2 {...{
      "id": "adding-the-certificate"
    }}>{`Adding the certificate`}</h2>
    <p>{`Head to the iOS dev portal and add the appropriate certificate.`}</p>
    <p>{`You may need to open Keychain access to generate a new key using `}<inlineCode parentName="p">{`Keychain Access > Certificate Assistant > Req Cert from Cert Auth`}</inlineCode>{` and generate a new cert. This cert will be used to generate what you need to import into Keychain Access from the dev portal.`}</p>
    <p>{`Once created, export it to a folder for the server.`}</p>
    {
      /* Once created, download the certificate and export it as .p12 to the server which hosts `node-passbook`. You may need to global install as well `npm install -g passbook` to then use `node-passbook prepare-keys -p keys` to convert .p12 to .pem. Make sure you set the PEM passphrase too - it will be needed. */
    }
    <h2 {...{
      "id": "php-server"
    }}>{`PHP Server`}</h2>
    <p>{`Using `}<inlineCode parentName="p">{`composer`}</inlineCode>{`, you can build out a scaffold using the Slim Framework:`}</p>
    <p><inlineCode parentName="p">{`composer create-project slim/slim-skeleton [project-name]`}</inlineCode>{`.`}</p>
    <p>{`To install the PHP PassKit helper, run `}<inlineCode parentName="p">{`composer require pkpass/pkpass`}</inlineCode>{`.`}</p>
    <p>{`Once built, you can then hit the routes and just require the pkpass and the routes file may end up looking like this:`}</p>
    <h3 {...{
      "id": "plan-ticket-example"
    }}>{`Plan ticket example`}</h3>
    <pre><code parentName="pre" {...{
        "className": "language-php"
      }}>{`<?php
// Routes
use PKPass\\PKPass;

$app->get('/', function ($request, $response, $args) {
    // Sample log message
    $this->logger->info("Slim-Skeleton '/' route");

    // Render index view
    return $this->renderer->render($response, 'sample.phtml', $args);
});

$app->get('/pass', function ($request, $response, $args) {
    // Sample log message
    $this->logger->info("Slim-Skeleton '/pass' route");

    $pass = new PKPass('<name>.p12', '<insert-password>');

    // Pass content
    $data = [
        'description' => 'Demo pass',
        'formatVersion' => 1,
        'organizationName' => 'Flight Express',
        'passTypeIdentifier' => 'pass.com.<teamname>.<passname>', // Change this!
        'serialNumber' => '12345678',
        'teamIdentifier' => '<insert-team-id>', // Change this! Found on Apple Dev Portal
        'boardingPass' => [
            'primaryFields' => [
                [
                    'key' => 'origin',
                    'label' => 'San Francisco',
                    'value' => 'SFO',
                ],
                [
                    'key' => 'destination',
                    'label' => 'London',
                    'value' => 'LHR',
                ],
            ],
            'secondaryFields' => [
                [
                    'key' => 'gate',
                    'label' => 'Gate',
                    'value' => 'F12',
                ],
                [
                    'key' => 'date',
                    'label' => 'Departure date',
                    'value' => '07/11/2012 10:22',
                ],
            ],
            'backFields' => [
                [
                    'key' => 'passenger-name',
                    'label' => 'Passenger',
                    'value' => 'John Appleseed',
                ],
            ],
            'transitType' => 'PKTransitTypeAir',
        ],
        'barcode' => [
            'format' => 'PKBarcodeFormatQR',
            'message' => 'Flight-GateF12-ID6643679AH7B',
            'messageEncoding' => 'iso-8859-1',
        ],
        'backgroundColor' => 'rgb(32,110,247)',
        'logoText' => 'Flight info',
        'relevantDate' => date('Y-m-d\\TH:i:sP')
    ];

    $pass->setWWDRcertPath('<path to wwdr.pem>');
    $pass->setData($data);

    // Add files to the pass package
    $pass->addFile('public/icon.png');
    $pass->addFile('public/icon@2x.png');
    $pass->addFile('public/logo.png');

    if($pass->checkError($error) == true) {
        // echo $error;
        // exit('An error occured: ' . $error);
    }

    // echo 'Here';

    $result = $pass->create(true);
    if($result == false) {
        echo $pass->getError();
    }
    // Render index view
    return $result;
});
`}</code></pre>
    <h3 {...{
      "id": "store-card-example"
    }}>{`Store card example`}</h3>
    <pre><code parentName="pre" {...{
        "className": "language-php"
      }}>{`<?php
// Routes
use PKPass\\PKPass;

$app->get('/', function ($request, $response, $args) {
    // Sample log message
    $this->logger->info("Slim-Skeleton '/' route");

    // Render index view
    return $this->renderer->render($response, 'sample.phtml', $args);
});

$app->get('/pass', function ($request, $response, $args) {
    // Sample log message
    $this->logger->info("Slim-Skeleton '/pass' route");

    $pass = new PKPass('<key-path>', 'password');

    // Pass content
    $data = [
        'formatVersion' => 1,
        'organizationName' => 'Testing',
        'passTypeIdentifier' => 'pass.com.<id>.<app>', // Change this!
        'serialNumber' => '12345678',
        'teamIdentifier' => '<team-id>', // Change this!
        'logoText' => 'Loyalty',
        'description' => 'Rewards card',
        'storeCard' => [
            'secondaryFields' => [
                [
                    'key' => 'balance',
                    'label' => 'BALANCE',
                    'value' => '$250.00'
                ],
                [
                    'key' => 'name',
                    'label' => 'NICKNAME',
                    'value' => 'Denno'
                ],
            ],
            'backFields' => [
                [
                    'key' => 'owner-name',
                    'label' => 'Rewards Card',
                    'value' => 'John Appleseed'
                ],
            ]
        ],
        'barcode' => [
            'format' => 'PKBarcodeFormatPDF417',
            'message' => 'ID6643679AH7B',
            'messageEncoding' => 'iso-8859-1',
            'altText' => 'ID6643679AH7B'
        ],
        'backgroundColor' => 'rgb(32,110,247)',
        'logoText' => 'Dummy card info'
    ];

    $pass->setWWDRcertPath('<wwdr-key>');
    $pass->setData($data);

    // Add files to the pass package
    $pass->addFile('public/icon.png');
    $pass->addFile('public/icon@2x.png');
    $pass->addFile('public/logo.png');

    if($pass->checkError($error) == true) {
        // echo $error;
        // exit('An error occured: ' . $error);
    }

    // echo 'Here';

    $result = $pass->create(true);
    if($result == false) {
        echo $pass->getError();
    }
    // Render index view
    return $result;
});

?>
`}</code></pre>
    <h2 {...{
      "id": "more-info"
    }}>{`More info`}</h2>
    <p>{`If you need to generate a wwdr cert, you can globally install `}<inlineCode parentName="p">{`passbook`}</inlineCode>{` eg. `}<inlineCode parentName="p">{`node install -g passbook`}</inlineCode>{` and run a command like `}<inlineCode parentName="p">{`node-passbook prepare-keys -p <folder-hosting-exported-keys>`}</inlineCode>{` and it can do the conversion for you. Make sure you don't forget the password, though.`}</p>
    <p>{`Some changes you may need to make are passing `}<inlineCode parentName="p">{`POST`}</inlineCode>{` variables to change it for each user.`}</p>
    <p>{`If you need to change the JSON structure above, refer to `}<a parentName="p" {...{
        "href": "https://developer.apple.com/library/content/documentation/UserExperience/Reference/PassKit_Bundle/Chapters/Introduction.html"
      }}>{`https://developer.apple.com/library/content/documentation/UserExperience/Reference/PassKit_Bundle/Chapters/Introduction.html`}</a>{` for more info.`}</p>
    </MDXLayout>;
}

MDXContent.isMDXComponent = true;
