  import React from 'react'
  import { MDXTag } from '@mdx-js/tag'


export default class MDXContent extends React.Component {
  constructor(props) {
    super(props)
    this.layout = null
  }
  render() {
    const { components, ...props } = this.props

    return <MDXTag
             name="wrapper"

             components={components}><div id="ordering"></div>
<MDXTag name="hr" components={components}></MDXTag>
<MDXTag name="h2" components={components} props={{"id":"css-ordering"}}>{`CSS Ordering`}</MDXTag>
<MDXTag name="pre" components={components}><MDXTag name="code" components={components} parentName="pre" props={{"className":"language-css"}}>{`.selector {
  /* Positioning */
  position: absolute;
  z-index: 10;
  top: 0;
  right: 0;

  /* Display & Box Model */
  display: inline-block;
  overflow: hidden;
  box-sizing: border-box;
  width: 100px;
  height: 100px;
  padding: 10px;
  border: 10px solid #333;
  margin: 10px;

  /* Color */
  background: #000;
  color: #fff
  
  /* Text */
  font-family: sans-serif;
  font-size: 16px;
  line-height: 1.4;
  text-align: right;

  /* Other */
  cursor: pointer;
}
`}</MDXTag></MDXTag>
<div id="structure"></div>
<MDXTag name="hr" components={components}></MDXTag>
<MDXTag name="h2" components={components} props={{"id":"css-structure"}}>{`CSS Structure`}</MDXTag>
<div id="newSection"></div>
<MDXTag name="h3" components={components} props={{"id":"-----one-component-per-file"}}>{`---- One Component per file`}</MDXTag>
<MDXTag name="p" components={components}>{`For each component, place them in their own file.`}</MDXTag>
<MDXTag name="pre" components={components}><MDXTag name="code" components={components} parentName="pre" props={{"className":"language-css"}}>{`/* css/components/search-form.scss */
.search-form {
  > .button { /* ... */ }
  > .field { /* ... */ }
  > .label { /* ... */ }

  // variants
  &.-small { /* ... */ }
  &.-wide { /* ... */ }
}
`}</MDXTag></MDXTag>
<div id="newSection"></div>
<MDXTag name="h3" components={components} props={{"id":"-----nesting"}}>{`---- Nesting`}</MDXTag>
<MDXTag name="p" components={components}>{`Use no more than 1 level of nesting. It's easy to get lost with too much nesting.`}</MDXTag>
<MDXTag name="pre" components={components}><MDXTag name="code" components={components} parentName="pre" props={{}}>{`/* ✗ Avoid: 3 levels of nesting */
.image-frame {
  > .description {
    /* ... */

    > .icon {
      /* ... */
    }
  }
}

/* ✓ Better: 2 levels */
.image-frame {
  > .description { /* ... */ }
  > .description > .icon { /* ... */ }
}
`}</MDXTag></MDXTag>
           </MDXTag>
  }
}
