  import React from 'react'
  import { MDXTag } from '@mdx-js/tag'


export default class MDXContent extends React.Component {
  constructor(props) {
    super(props)
    this.layout = null
  }
  render() {
    const { components, ...props } = this.props

    return <MDXTag
             name="wrapper"

             components={components}><MDXTag name="h1" components={components} props={{"id":"css-syntax"}}>{`CSS Syntax`}</MDXTag>
<MDXTag name="pre" components={components}><MDXTag name="code" components={components} parentName="pre" props={{"className":"language-css"}}>{`selector {property: value;}

External Style Sheet
<link rel="stylesheet" type="text/css" href="style.css" />
Internal Style
<style type="text/css">
selector {property: value;}
</style>
Inline Style
<tag style="property: value">
`}</MDXTag></MDXTag>
<MDXTag name="h3" components={components} props={{"id":"antialiasing"}}>{`Antialiasing`}</MDXTag>
<MDXTag name="pre" components={components}><MDXTag name="code" components={components} parentName="pre" props={{"className":"language-css"}}>{`-webkit-font-smoothing: antialiased;
`}</MDXTag></MDXTag>
<MDXTag name="h3" components={components} props={{"id":"css-ordering"}}>{`CSS Ordering`}</MDXTag>
<MDXTag name="pre" components={components}><MDXTag name="code" components={components} parentName="pre" props={{"className":"language-css"}}>{`.selector {
  /* Positioning */
  position: absolute;
  z-index: 10;
  top: 0;
  right: 0;

  /* Display & Box Model */
  display: inline-block;
  overflow: hidden;
  box-sizing: border-box;
  width: 100px;
  height: 100px;
  padding: 10px;
  border: 10px solid #333;
  margin: 10px;

  /* Color */
  background: #000;
  color: #fff
  
  /* Text */
  font-family: sans-serif;
  font-size: 16px;
  line-height: 1.4;
  text-align: right;

  /* Other */
  cursor: pointer;
}
`}</MDXTag></MDXTag>
<MDXTag name="h3" components={components} props={{"id":"css-outline"}}>{`CSS Outline`}</MDXTag>
<MDXTag name="pre" components={components}><MDXTag name="code" components={components} parentName="pre" props={{"className":"language-css"}}>{`outline: outline-color outline-style outline-width|initial|inherit;
`}</MDXTag></MDXTag>
<MDXTag name="h3" components={components} props={{"id":"css-find-active-element"}}>{`CSS find active element`}</MDXTag>
<MDXTag name="pre" components={components}><MDXTag name="code" components={components} parentName="pre" props={{"className":"language-javascript"}}>{`document.activeElement
`}</MDXTag></MDXTag>
<MDXTag name="h3" components={components} props={{"id":"css-general"}}>{`CSS General`}</MDXTag>
<MDXTag name="p" components={components}>{`Class				String preceded by a period
ID					String preceded by a hash mark
div					Formats structure or block of text
span				Inline formatting
color				Foreground color
cursor				Appearance of the cursor
display				block; inline; list-item; none
overflow			How content overflowing its box is handled
visible, hidden, scroll, auto
visibility			visible, hidden`}</MDXTag>
<MDXTag name="h3" components={components} props={{"id":"css-position"}}>{`CSS Position`}</MDXTag>
<MDXTag name="p" components={components}>{`clear			Any floating elements around the element?
both, left, right, none
float			Floats to a specified side
left, right, none
left			The left position of an element
auto, length values (pt, in, cm, px)
top				The top position of an element
auto, length values (pt, in, cm, px)
position		static, relative, absolute
z-index			Element above or below overlapping elements?
auto, integer (higher numbers on top)`}</MDXTag>
<MDXTag name="h3" components={components} props={{"id":"css-font"}}>{`CSS Font`}</MDXTag>
<MDXTag name="p" components={components}>{`font-style			Italic, normal
font-variant		normal, small-caps
font-weight			bold, normal, lighter, bolder, integer (100-900)
font-size			Size of the font
font-family			Specific font(s) to be used`}</MDXTag>
<MDXTag name="h3" components={components} props={{"id":"css-background"}}>{`CSS Background`}</MDXTag>
<MDXTag name="p" components={components}>{`background-color		Background color
background-image		Background image
background-repeat		repeat, no-repeat, repeat-x, repeat-y
background-attachment	Background image scroll with the element?
scroll, fixed
background-position		(x y), top, center, bottom, left, right`}</MDXTag>
<MDXTag name="h3" components={components} props={{"id":"css-list"}}>{`CSS List`}</MDXTag>
<MDXTag name="p" components={components}>{`list-style-type			Type of bullet or numbering in the list
disc; circle; square; decimal; lower-roman; upper-roman; lower-alpha; upper-alpha; none
list-style-position		Position of the bullet or number in a list
inside; outside
list-style-image		Image to be used as the bullet in a list`}</MDXTag>
<MDXTag name="h3" components={components} props={{"id":"css-text"}}>{`CSS Text`}</MDXTag>
<MDXTag name="p" components={components}>{`letter-spacing		Space between letters
line-height			Vertical distance between baselines
text-align			Horizontal alignment
text-decoration		blink, line-through, none, overline, underline
text-indent			First line indentation
text-transform		capitalize, lowercase, uppercase
vertical-align		Vertical alignment
word-spacing		Spacing between words`}</MDXTag>
<MDXTag name="h3" components={components} props={{"id":"box-model"}}>{`Box Model`}</MDXTag>
<MDXTag name="p" components={components}>{`height;
width;
margin-top;
margin-right;
margin-bottom;
margin-left;
padding-top;
padding-right;
padding-bottom;
padding-left;`}</MDXTag>
<MDXTag name="h3" components={components} props={{"id":"border"}}>{`Border`}</MDXTag>
<MDXTag name="p" components={components}>{`border-width	Width of the border
border-style	dashed; dotted; double; groove; inset; outset; ridge; solid; none
border-color	Color of the border`}</MDXTag>
<MDXTag name="h3" components={components} props={{"id":"setting-a-default-browser-outline"}}>{`Setting a default browser outline`}</MDXTag>
<MDXTag name="p" components={components}>{`outline`}</MDXTag>
<MDXTag name="h3" components={components} props={{"id":"css-hiding-an-element"}}>{`CSS Hiding an element`}</MDXTag>
<MDXTag name="pre" components={components}><MDXTag name="code" components={components} parentName="pre" props={{"className":"language-css"}}>{`.hidden {
    position:absolute;
    left:-10000px;
    top:auto;
    width:1px;
    height:1px;
    overflow:hidden;
}
`}</MDXTag></MDXTag>
           </MDXTag>
  }
}
