/* @jsx mdx */
  import React from 'react'
  import { mdx } from '@mdx-js/react'
  /* @jsx mdx */


const makeShortcode = name => function MDXDefaultShortcode(props) {
  console.warn("Component " + name + " was not imported, exported, or provided by MDXProvider as global scope")
  return <div {...props}/>
};

const layoutProps = {

};
const MDXLayout = "wrapper"
export default function MDXContent({
  components,
  ...props
}) {
  return <MDXLayout {...layoutProps} {...props} components={components} mdxType="MDXLayout">
    <h1 {...{
      "id": "webmasters-elm"
    }}>{`Webmasters Elm`}</h1>
    <h2 {...{
      "id": "costs"
    }}>{`Costs`}</h2>
    <ol>
      <li parentName="ol">{`Learning a new language`}</li>
      <li parentName="ol">{`Smaller ecosystem`}</li>
      <li parentName="ol">{`Fewer Web APIs have first-class support`}</li>
    </ol>
    <h2 {...{
      "id": "benefits"
    }}>{`Benefits`}</h2>
    <ol>
      <li parentName="ol">{`Bundle size small`}</li>
      <li parentName="ol">{`Production Elm in the first week`}</li>
    </ol>
    <h2 {...{
      "id": "elm"
    }}>{`Elm`}</h2>
    <table>
      <thead parentName="table">
        <tr parentName="thead">
          <th parentName="tr" {...{
            "align": null
          }}>{`JS Equivalent`}</th>
          <th parentName="tr" {...{
            "align": null
          }}>{`Elm`}</th>
        </tr>
      </thead>
      <tbody parentName="table">
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": null
          }}>{`UI`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`view`}</td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": null
          }}>{`State`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`model`}</td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": null
          }}>{`Async`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`update`}</td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": null
          }}>{`Utilities`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`core`}</td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": null
          }}>{`Packages`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`elm install`}</td>
        </tr>
      </tbody>
    </table>
    <h2 {...{
      "id": "rendering-a-page"
    }}>{`Rendering a page`}</h2>
    <h3 {...{
      "id": "compiling"
    }}>{`Compiling`}</h3>
    <p>{`Compiles to JS.`}</p>
    <pre><code parentName="pre" {...{
        "className": "language-shell"
      }}>{`elm make Main.elm --output elm.js
`}</code></pre>
    <h2 {...{
      "id": "user-interface-example"
    }}>{`User Interface Example`}</h2>
    <p>{`Converting this to Elm:`}</p>
    <pre><code parentName="pre" {...{
        "className": "language-javascript"
      }}>{`let pluralize = (singular, plural, quantity) =>
  quantity === 1 ? singular : plural;
`}</code></pre>
    <pre><code parentName="pre" {...{
        "className": "language-elm"
      }}>{`pluralize singular plural quantity =
    if quantity == 1 then
        singular
    else
        plural

main =
    text (pluralize "leaf" "leaves" 1) -- parentheses are to disambiguate
`}</code></pre>
    <h2 {...{
      "id": "part-1---example"
    }}>{`Part 1 - Example`}</h2>
    <p>{`This basic example gives a short look at actually creating a working piece of code.`}</p>
    <pre><code parentName="pre" {...{
        "className": "language-elm"
      }}>{`module Main exposing (main)

import Html exposing (..)
import Html.Attributes exposing (..)


banner =
    div [ class "banner" ]
        [ div [ class "container" ]
            [ h1 [] [ text "conduit"]
            , p [] [ text "A place to share your knowledge."]
            ]
        ]


feed =
    div [ class "feed-toggle" ] [ text "(In the future we’ll display a feed of articles here!)" ]


main =
    div [ class "home-page" ]
        [ banner
        , div [ class "container page" ]
            [ div [ class "row" ]
                [ div [ class "col-md-9" ] [ feed ]
                , div [ class "col-md-3" ] []
                ]
            ]
        ]
`}</code></pre>
    <h2 {...{
      "id": "the-elm-architecture"
    }}>{`The Elm Architecture`}</h2>
    <p>{`Update, Model, View.`}</p>
    <p>{`View is function that takes a `}<inlineCode parentName="p">{`model`}</inlineCode>{` as a function.`}</p>
    <p>{`Note that whatever message you use, you need to be consistent for `}<inlineCode parentName="p">{`Update`}</inlineCode>{` and `}<inlineCode parentName="p">{`View`}</inlineCode>{`.`}</p>
    <pre><code parentName="pre" {...{
        "className": "language-elm"
      }}>{`type alias Msg =
    { description: String
    , data: String
    }

view : Model -> Html Msg
view model =

--
update : Msg -> Model -> Model
update msg model =
`}</code></pre>
    <h2 {...{
      "id": "type-annotations"
    }}>{`Type Annotations`}</h2>
    <pre><code parentName="pre" {...{
        "className": "language-elm"
      }}>{`username = "okeeffed"

-- typed example (Elm uses inference either way)
username : String
username = "okeeffed"

-- alt example
String.length : String -> Int

-- name list
names : List String
names = ["Sam", "Bob", "Bill"]
`}</code></pre>
    <h2 {...{
      "id": "functions"
    }}>{`Functions`}</h2>
    <pre><code parentName="pre" {...{
        "className": "language-elm"
      }}>{`pluralize : String -> String -> Int -> String

-- equivocal to
pluralize : String -> (String -> (Int -> String))
`}</code></pre>
    <h2 {...{
      "id": "case-expressions"
    }}>{`Case Expressions`}</h2>
    <pre><code parentName="pre" {...{
        "className": "language-elm"
      }}>{`case model.tab of
    "YourFeed" ->
        -- show feed
    "GlobalFeed" ->
        -- show Global Feed
    _ ->
        -- show Tag Feed
`}</code></pre>
    <h2 {...{
      "id": "custom-types"
    }}>{`Custom Types`}</h2>
    <pre><code parentName="pre" {...{
        "className": "language-elm"
      }}>{`-- creates completely distinct values
type Tab =
    YourFeed
    | GlobalFeed
    | TagFeed

yours : Tab
yours =
    YourFeed

-- Turning types into functions

type Tab =
    YourFeed
    | GlobalFeed
    | TagFeed String

-- in the repl
> TagFeed "Happiness"
TagFeed "Happiness" : Tab
> TagFeed
<function> : String -> Tab
`}</code></pre>
    <p>{`Using Custom Types with updates:`}</p>
    <pre><code parentName="pre" {...{
        "className": "language-elm"
      }}>{`-- allows us to pass different data types
type Msg
    = ClickedTag String
    | ClickedPage Int

update msg model =
    case msg of
        ClickedTag selectedTag ->
            -- use tag here
        ClickedPage page ->
            -- use page here
`}</code></pre>
    <p>{`Summing it all up`}</p>
    <p>{`Type | In Elm
case-expressions | case msg of
Enumerations | type Bool = True | False
Containers | type Msg = ClickedPage Int | ...
Variant Functions | onClick (ClickedPage pageNumber)`}</p>
    <h2 {...{
      "id": "maybe-overview"
    }}>{`Maybe Overview`}</h2>
    <pre><code parentName="pre" {...{
        "className": "language-elm"
      }}>{`-- List.head : List elem -> Maybe elem
first users =
    List.head users

-- example case
case first newUsers of
    Just user ->
        String.length user
    Nothing ->
        0
`}</code></pre>
    <h2 {...{
      "id": "pipelines"
    }}>{`Pipelines`}</h2>
    <pre><code parentName="pre" {...{
        "className": "language-elm"
      }}>{`List.head (List.map (List.reverse (List.filter (\\x -> x < 5) [2, 4, 6])) negate)

-- As pipeline
[2, 4, 6]
    |> List.filter (\\x -> x < 5)
    |> List.reverse
    |> List.map negate
    |> List.head
`}</code></pre>
    <h2 {...{
      "id": "decoding-json"
    }}>{`Decoding JSON`}</h2>
    <pre><code parentName="pre" {...{
        "className": "language-elm"
      }}>{`-- Import to understand custom type
type Result okVal errVal
    = Ok okVal
    | Err errVal

-- similar to String.toint()
case decodeString Json.Decode.int "42" of
    Ok num ->
        -- Do something with Int
    Err error ->
        -- Do something with the error

type alias User =
    { id : Int
    , firstName : String
    , lastName : String
    }

user : Decoder User
user =
    Json.Decode.succeed User
        |> required "user_id" int
        |> required "first_name" string
        |> required "last_name" string

-- Alternatively
users : Decoder (List User)
users =
    list user
`}</code></pre>
    <h2 {...{
      "id": "optional-and-nullable"
    }}>{`Optional and Nullable`}</h2>
    <pre><code parentName="pre" {...{
        "className": "language-elm"
      }}>{`type alias User =
    { id : Int
    , name : Maybe String
    , email : String
    }

user : Decoder User
user =
    Json.Decode.succeed User
        |> required "user_id" int
        |> required "name" (nullable string)
        |> required "email" string
`}</code></pre>
    <p>{`For an example that requires a list:`}</p>
    <pre><code parentName="pre" {...{
        "className": "language-elm"
      }}>{`Decode.succeed Metadata
   |> required "description" string
   |> required "title" string
   |> required "tagList" (list string)
   |> required "favorited" bool
   |> required "favoritesCound" int
   |> required "createdAt" Timestamp.iso8601Decoder
`}</code></pre>
    <h2 {...{
      "id": "tuples"
    }}>{`Tuples`}</h2>
    <p>{`Serve the same purpose as records but with field `}<inlineCode parentName="p">{`positions`}</inlineCode>{` instead of names.`}</p>
    <p>{`There is a 3 limit max!`}</p>
    <pre><code parentName="pre" {...{
        "className": "language-elm"
      }}>{`x = Tuple.first (5, 7)
(name, x, y) = ("foo", 5, 7)

x = { x = 5, y = 7 }.x
{ name, x, y } = { name = "foo", x = 5, y = 7 }
`}</code></pre>
    <h2 {...{
      "id": "http-and-interacting-with-servers"
    }}>{`Http and interacting with servers`}</h2>
    <pre><code parentName="pre" {...{
        "className": "language-elm"
      }}>{`Http
`}</code></pre>
    </MDXLayout>;
}

MDXContent.isMDXComponent = true;
