/* @jsx mdx */
  import React from 'react'
  import { mdx } from '@mdx-js/react'
  /* @jsx mdx */


const makeShortcode = name => function MDXDefaultShortcode(props) {
  console.warn("Component " + name + " was not imported, exported, or provided by MDXProvider as global scope")
  return <div {...props}/>
};

const layoutProps = {

};
const MDXLayout = "wrapper"
export default function MDXContent({
  components,
  ...props
}) {
  return <MDXLayout {...layoutProps} {...props} components={components} mdxType="MDXLayout">
    <h1 {...{
      "id": "comparing-design-system-components"
    }}>{`Comparing Design System Components`}</h1>
    <p>{`Use this resource to find resources and code for a variety of components to help write your own.`}</p>
    <p>{`Each component has its own section.`}</p>
    <p>{`For each section, there is table with the design system name, the type (ie link to code, guide, playground etc) and the link.`}</p>
    <h2 {...{
      "id": "table-of-contents"
    }}>{`Table of Contents`}</h2>
    <ul>
      <li parentName="ul"><a parentName="li" {...{
          "href": "#comparing-design-system-components"
        }}>{`Comparing Design System Components`}</a><ul parentName="li">
          <li parentName="ul"><a parentName="li" {...{
              "href": "#table-of-contents"
            }}>{`Table of Contents`}</a></li>
          <li parentName="ul"><a parentName="li" {...{
              "href": "#button"
            }}>{`Button`}</a></li>
        </ul></li>
    </ul>
    <h2 {...{
      "id": "button"
    }}>{`Button`}</h2>
    <table>
      <thead parentName="table">
        <tr parentName="thead">
          <th parentName="tr" {...{
            "align": null
          }}>{`Design System`}</th>
          <th parentName="tr" {...{
            "align": null
          }}>{`Type`}</th>
          <th parentName="tr" {...{
            "align": null
          }}>{`Link`}</th>
        </tr>
      </thead>
      <tbody parentName="table">
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": null
          }}>{`Carbon`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`source`}</td>
          <td parentName="tr" {...{
            "align": null
          }}><a parentName="td" {...{
              "href": "https://github.com/carbon-design-system/carbon-components-react/tree/master/src/components/Button"
            }}>{`https://github.com/carbon-design-system/carbon-components-react/tree/master/src/components/Button`}</a></td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": null
          }}>{`Carbon`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`example`}</td>
          <td parentName="tr" {...{
            "align": null
          }}><a parentName="td" {...{
              "href": "https://www.carbondesignsystem.com/components/button/code/"
            }}>{`https://www.carbondesignsystem.com/components/button/code/`}</a></td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": null
          }}>{`AtlasKit`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`example`}</td>
          <td parentName="tr" {...{
            "align": null
          }}><a parentName="td" {...{
              "href": "https://atlaskit.atlassian.com/packages/core/button"
            }}>{`https://atlaskit.atlassian.com/packages/core/button`}</a></td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": null
          }}>{`AtlasKit`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`source`}</td>
          <td parentName="tr" {...{
            "align": null
          }}><a parentName="td" {...{
              "href": "https://bitbucket.org/atlassian/atlaskit-mk-2/src/master/packages/core/button"
            }}>{`https://bitbucket.org/atlassian/atlaskit-mk-2/src/master/packages/core/button`}</a></td>
        </tr>
      </tbody>
    </table>
    <p>{`AtlasKit button uses the following method to determine the element tag:`}</p>
    <pre><code parentName="pre" {...{
        "className": "language-javascript"
      }}>{`getElement = () => {
  const { href, isDisabled } = this.props;
  if (href) {
    return isDisabled ? 'span' : 'a';
  }
  return 'button';
};
`}</code></pre>
    </MDXLayout>;
}

MDXContent.isMDXComponent = true;
