/* @jsx mdx */
  import React from 'react'
  import { mdx } from '@mdx-js/react'
  /* @jsx mdx */


const makeShortcode = name => function MDXDefaultShortcode(props) {
  console.warn("Component " + name + " was not imported, exported, or provided by MDXProvider as global scope")
  return <div {...props}/>
};

const layoutProps = {

};
const MDXLayout = "wrapper"
export default function MDXContent({
  components,
  ...props
}) {
  return <MDXLayout {...layoutProps} {...props} components={components} mdxType="MDXLayout">
    <h1 {...{
      "id": "go-cheat-sheet"
    }}>{`Go Cheat Sheet`}</h1>
    <p>{`Good refs:`}</p>
    <p><a parentName="p" {...{
        "href": "https://github.com/a8m/go-lang-cheat-sheet"
      }}>{`https://github.com/a8m/go-lang-cheat-sheet`}</a></p>
    <h2 {...{
      "id": "in-a-nutshell"
    }}>{`In a nutshell`}</h2>
    <ul>
      <li parentName="ul">{`Imperative language`}</li>
      <li parentName="ul">{`Statically typed`}</li>
      <li parentName="ul">{`Syntax tokens similar to C (but less parentheses and no semicolons) and the structure to Oberon-2`}</li>
      <li parentName="ul">{`Compiles to native code (no JVM)`}</li>
      <li parentName="ul">{`No classes, but structs with methods`}</li>
      <li parentName="ul">{`Interfaces`}</li>
      <li parentName="ul">{`No implementation inheritance. There's type embedding, though.`}</li>
      <li parentName="ul">{`Functions are first class citizens`}</li>
      <li parentName="ul">{`Functions can return multiple values`}</li>
      <li parentName="ul">{`Has closures`}</li>
      <li parentName="ul">{`Pointers, but not pointer arithmetic`}</li>
      <li parentName="ul">{`Built-in concurrency primitives: Goroutines and Channels`}</li>
    </ul>
    <h2 {...{
      "id": "basics"
    }}>{`Basics`}</h2>
    <pre><code parentName="pre" {...{
        "className": "language-go"
      }}>{`// hello.go
package main

import "fmt"

func main() {
    fmt.Println("Hello Go")
}
`}</code></pre>
    <pre><code parentName="pre" {...{
        "className": "language-shell"
      }}>{`go run hello.go
`}</code></pre>
    <h2 {...{
      "id": "declarations"
    }}>{`Declarations`}</h2>
    <pre><code parentName="pre" {...{
        "className": "language-go"
      }}>{`var foo int // declaration without initialization
var foo int = 42 // declaration with initialization
var foo, bar int = 42, 1302 // declare and init multiple vars at once
var foo = 42 // type omitted, will be inferred
foo := 42 // shorthand, only in func bodies, omit var keyword, type is always implicit
const constant = "This is a constant"
`}</code></pre>
    <h2 {...{
      "id": "functions"
    }}>{`Functions`}</h2>
    <pre><code parentName="pre" {...{
        "className": "language-go"
      }}>{`// a simple function
func functionName() {}

// function with parameters (again, types go after identifiers)
func functionName(param1 string, param2 int) {}

// multiple parameters of the same type
func functionName(param1, param2 int) {}

// return type declaration
func functionName() int {
    return 42
}

// Can return multiple values at once
func returnMulti() (int, string) {
    return 42, "foobar"
}
var x, str = returnMulti()

// Return multiple named results simply by return
func returnMulti2() (n int, s string) {
    n = 42
    s = "foobar"
    // n and s will be returned
    return
}
var x, str = returnMulti2()
`}</code></pre>
    <h2 {...{
      "id": "funcs-as-values-and-closures"
    }}>{`Funcs as values and closures`}</h2>
    <pre><code parentName="pre" {...{
        "className": "language-go"
      }}>{`func main() {
    // assign a function to a name
    add := func(a, b int) int {
        return a + b
    }
    // use the name to call the function
    fmt.Println(add(3, 4))
}

// Closures, lexically scoped: Functions can access values that were
// in scope when defining the function
func scope() func() int {
    outer_var := 2
    foo := func() int { return outer_var}
    return foo
}


// Closures
func outer() (func() int, int) {
    outer_var := 2
    inner := func() int {
        outer_var += 99 // outer_var from outer scope is mutated.
        return outer_var
    }
    inner()
    return inner, outer_var // return inner func and mutated outer_var 101
}
`}</code></pre>
    </MDXLayout>;
}

MDXContent.isMDXComponent = true;
