<MDXTag name="p" components={components}>{`We now need to create a `}<MDXTag name="inlineCode" components={components} parentName="p">{`Nav Mesh`}</MDXTag>{`. The `}<MDXTag name="inlineCode" components={components} parentName="p">{`NavMeshAgent`}</MDXTag>{` allows us to set a destination target that the "enemy" can follow. It is a component that can be attached to a game object so that it can interact with the `}<MDXTag name="inlineCode" components={components} parentName="p">{`NavMesh`}</MDXTag>{`.`}</MDXTag>
<MDXTag name="pre" components={components}><MDXTag name="code" components={components} parentName="pre" props={{"className":"language-csharp"}}>{`// BirdMovement.cs
using System.Collections;
using System.Collections.Generic;
using UnityEngine;

public class BirdMovement : MonoBehaviour {

    [SerializeField]
    private Transform target;
    private UnityEngine.AI.NavMeshAgent birdAgent;
    private Animator birdAnimator;

    // Use this for initialization
    void Start () {
        birdAgent = GetComponent<UnityEngine.AI.NavMeshAgent> ();
        birdAnimator = GetComponent<Animator> ();
    }

    // Update is called once per frame
    void Update () {
        // Set the bird's destination
        birdAgent.SetDestination (target.position);

        // Measure the magnitude of the NavMeshAgent's velocity
        float speed = birdAgent.velocity.magnitude;

        // Pass the velocity to the animator component
        birdAnimator.SetFloat("Speed", speed);
    }
}
`}</MDXTag></MDXTag>
