<MDXTag name="h3" components={components} props={{"id":"managing-the-game-state"}}>{`Managing the game state`}</MDXTag>
<MDXTag name="p" components={components}>{`How do we know when the game has started and when we need to restart?`}</MDXTag>
<MDXTag name="pre" components={components}><MDXTag name="code" components={components} parentName="pre" props={{"className":"language-csharp"}}>{`using System.Collections;
using System.Collections.Generic;
using UnityEngine;
using UnityEngine.UI;
using UnityEngine.SceneManagement;

public class GameState : MonoBehaviour {
    private bool gameStarted = false;
    [SerializeField]
    private Text gameStateText;
    [SerializeField]
    private GameObject player;
    [SerializeField]
    private BirdMovement birdMovement;
    [SerializeField]
    private FollowCamera followCamera;
    private float restartDelay = 3f;
    private float restartTimer;
    private PlayerMovement playerMovement;
    private PlayerHealth playerHealth;

    // Use this for initialization
    void Start () {
        Cursor.visible = false;

        playerMovement = player.GetComponent<PlayerMovement> ();
        playerHealth = player.GetComponent<PlayerHealth> ();

        // do not allow player to move before the game
        playerMovement.enabled = false;
        // prevent bird
        birdMovement.enabled = false;
        // prevent follow camear
        followCamera.enabled = false;
    }

    // Update is called once per frame
    void Update () {
        // If the game is not sarted and the player presses the space bar...
        if (gameStarted == false && Input.GetKeyUp(KeyCode.Space)) {
            // ... start the game
            StartGame();
        }

        // If player is no longer alive ...
        if (playerHealth.alive == false) {
            // ...end the game
            EndGame();

            // ... increment timer to count up to restarting...
            restartTimer = restartTimer + Time.deltaTime;

            // ...and if it reaches the restart delay...
            if (restartTimer >= restartDelay) {
                // reload scene
                SceneManager.LoadScene(SceneManager.GetActiveScene().buildIndex);
            }
        }
    }

    private void StartGame() {
        gameStarted = true;

        // set main text to see through
        gameStateText.color = Color.clear;

        // allow player to move
        playerMovement.enabled = true;
        birdMovement.enabled = true;
        followCamera.enabled = true;
    }

    private void EndGame() {
        gameStarted = false;

        gameStateText.text = "Game Over";
        // set main text to see through
        gameStateText.color = Color.white;

        // remove player from game
        player.SetActive (false);
    }
}
`}</MDXTag></MDXTag>
