  import React from 'react'
  import { MDXTag } from '@mdx-js/tag'


export default class MDXContent extends React.Component {
  constructor(props) {
    super(props)
    this.layout = null
  }
  render() {
    const { components, ...props } = this.props

    return <MDXTag
             name="wrapper"

             components={components}><MDXTag name="h1" components={components} props={{"id":"angular-basics"}}>{`ANGULAR BASICS`}</MDXTag>
<MDXTag name="ul" components={components}>
<MDXTag name="li" components={components} parentName="ul">{`name file the same as directive`}</MDXTag>
</MDXTag>
<MDXTag name="p" components={components}>{`Create templates/todos.html`}</MDXTag>
<MDXTag name="p" components={components}>{`Create scripts/directives/todos.js`}</MDXTag>
<MDXTag name="pre" components={components}><MDXTag name="code" components={components} parentName="pre" props={{"className":"language-javascript"}}>{`angular.module("todoListApp")
.directive('todos' function() {
    return {
        templateUrl: 'templates/todos.html',
        controller: 'mainCtrl' //can define the controller too,
        replace: true
    }
});
`}</MDXTag></MDXTag>
<MDXTag name="p" components={components}>{`To get rid of directive tags, use the replace key.`}</MDXTag>
<MDXTag name="h4" components={components} props={{"id":"-----jsang-54-finalising-the-app"}}>{`---- JSANG-5.4: Finalising the App`}</MDXTag>
<MDXTag name="ul" components={components}>
<MDXTag name="li" components={components} parentName="ul">{`adding a save all function`}</MDXTag>
<MDXTag name="li" components={components} parentName="ul">{`ng-click and start editing`}</MDXTag>
</MDXTag>
<MDXTag name="p" components={components}>{`for the save in main.js...`}</MDXTag>
<MDXTag name="pre" components={components}><MDXTag name="code" components={components} parentName="pre" props={{"className":"language-javascript"}}>{`.controller(... function() {

    ...

    \$scope.saveTodos = function() {
        var filteredTodos = \$scope.todos.filter(function(todo) {
            if (todo.edited) {
                return todo;
            };
        })
        dataService.saveTodos(filteredTodos);
    }

})
`}</MDXTag></MDXTag>
<MDXTag name="p" components={components}>{`QUESTIONS`}</MDXTag>
<MDXTag name="ol" components={components}>
<MDXTag name="li" components={components} parentName="ol">{`In the object a directive returns, `}<MDXTag name="strong" components={components} parentName="li"><MDXTag name="strong" components={components} parentName="strong">{`_`}</MDXTag></MDXTag>{` loads an html file as a directive template.`}</MDXTag>
</MDXTag>
<MDXTag name="p" components={components}>{`A: 'templateUrl'`}</MDXTag>
<MDXTag name="ol" components={components} props={{"start":2}}>
<MDXTag name="li" components={components} parentName="ol">{`The Array's `}<MDXTag name="strong" components={components} parentName="li"><MDXTag name="strong" components={components} parentName="strong">{`_`}</MDXTag></MDXTag>{` method returns a subset of the array based on logic in the callback. For example, `}<MDXTag name="inlineCode" components={components} parentName="li">{`['foo', 'bar', 'yes', 'no'].someMethod(callback)`}</MDXTag>{`.`}</MDXTag>
</MDXTag>
<MDXTag name="p" components={components}>{`A: filter`}</MDXTag>
<MDXTag name="ol" components={components} props={{"start":3}}>
<MDXTag name="li" components={components} parentName="ol">{`In the object that a directive returns, the `}<MDXTag name="strong" components={components} parentName="li"><MDXTag name="strong" components={components} parentName="strong">{`_`}</MDXTag></MDXTag>{` key defines a controller to be used.`}</MDXTag>
</MDXTag>
<MDXTag name="p" components={components}>{`A: 'controller'`}</MDXTag>
<MDXTag name="ol" components={components} props={{"start":4}}>
<MDXTag name="li" components={components} parentName="ol">{`The first parameter of angular's `}<MDXTag name="inlineCode" components={components} parentName="li">{`directive`}</MDXTag>{` method is `}<MDXTag name="strong" components={components} parentName="li"><MDXTag name="strong" components={components} parentName="strong">{`__`}</MDXTag></MDXTag>{`.`}</MDXTag>
</MDXTag>
<MDXTag name="p" components={components}>{`A: The name of the directive`}</MDXTag>
           </MDXTag>
  }
}
