<MDXTag name="pre" components={components}><MDXTag name="code" components={components} parentName="pre" props={{"className":"language-go"}}>{`// Example
package main

import "fmt"

// Not using the pointer
func main() {
    num := 8.2
    halve(num)
    fmt.Println(num)
}

func halve(number float64) {
    number = number / 2;
    fmt.Println(number)
}

// Using the pointer
package main

import "fmt"

// Not using the pointer
func main() {
    num := 8.2
    halve(&num)
    fmt.Println(num)
}

func halve(number *float64) {
    *number = *number / 2
    fmt.Println(*number)
}
`}</MDXTag></MDXTag>
