  import React from 'react'
  import { MDXTag } from '@mdx-js/tag'


export default class MDXContent extends React.Component {
  constructor(props) {
    super(props)
    this.layout = null
  }
  render() {
    const { components, ...props } = this.props

    return <MDXTag
             name="wrapper"

             components={components}><div id="section"></div>
<MDXTag name="hr" components={components}></MDXTag>
<MDXTag name="h2" components={components} props={{"id":"getting-started"}}>{`Getting Started`}</MDXTag>
<MDXTag name="p" components={components}>{`BDD - Behaviour Driven Development `}</MDXTag>
<MDXTag name="p" components={components}>{`We want to put the test code in a file separate from the other code.`}</MDXTag>
<MDXTag name="p" components={components}>{`After install the dev dependency of mocha and chai, you can write a test.js file and then run \`mocha test.js' and it will simply run the test!`}</MDXTag>
<MDXTag name="p" components={components}>{`To start making these tests, we `}<MDXTag name="strong" components={components} parentName="p">{`NEED`}</MDXTag>{` to make a `}<MDXTag name="inlineCode" components={components} parentName="p">{`test`}</MDXTag>{` file in the same directory as `}<MDXTag name="inlineCode" components={components} parentName="p">{`package.json`}</MDXTag></MDXTag>
<div id="testSuite"></div>
<MDXTag name="h3" components={components} props={{"id":"-----building-a-test-suite"}}>{`---- Building a test suite`}</MDXTag>
<MDXTag name="p" components={components}>{`Write a test that will run the test using the npm task `}<MDXTag name="inlineCode" components={components} parentName="p">{`test`}</MDXTag>{` and just reply an assertion to be true.`}</MDXTag>
<MDXTag name="pre" components={components}><MDXTag name="code" components={components} parentName="pre" props={{}}>{`const expect = require('chai').expect;

// Test suite
describe('Mocha', () => {

    // Test spec (unit test)
    it('should run our test using npm', () => {
        expect(true).to.be.ok;
    });

});
`}</MDXTag></MDXTag>
<MDXTag name="p" components={components}>{`A `}<MDXTag name="inlineCode" components={components} parentName="p">{`spec`}</MDXTag>{` looks very similar to a `}<MDXTag name="inlineCode" components={components} parentName="p">{`suite`}</MDXTag>{`. It takes 2 arguments.`}</MDXTag>
<MDXTag name="ol" components={components}>
<MDXTag name="li" components={components} parentName="ol">{`A string describing the desired behaviour.`}</MDXTag>
<MDXTag name="li" components={components} parentName="ol">{`A function that wraps all the expectations together.`}</MDXTag>
</MDXTag>
<MDXTag name="p" components={components}>{`It's important to start guessing what the function might expect etc.`}</MDXTag>
<MDXTag name="p" components={components}>{`We do a lot of the guess work for setting the spec itself. It may feel awkward at the start, but it will help get some bearings.`}</MDXTag>
<MDXTag name="p" components={components}>{`Once the spec is written, if we actually get `}<MDXTag name="inlineCode" components={components} parentName="p">{`undefined`}</MDXTag>{` returned, that is likely because the function hasn't been written and/or does not return anything.`}</MDXTag>
<MDXTag name="pre" components={components}><MDXTag name="code" components={components} parentName="pre" props={{}}>{`let expect = require('chai').expect;

describe('checkForShip', () => {
    const checkForShip = require('../path/to/src').checkForShip;

    // this is the test spec
    it('should correctly report no ship at a given player's coordinate',  () => { 

        player = {
            ships: [
                {
                    locations: [[0,0]]
                }
            ]
        }

        expect(checkForShip(player, [9, 9])).to.be.false;
    });

    it('should handle ships located at more than one coordinate',  () => { 

        player = {
            ships: [
                {
                    locations: [[0,0], [0,1]]
                }
            ]
        }

        expect(checkForShip(player, [0, 1])).to.be.true;
        expect(checkForShip(player, [9, 9])).to.be.false;
    });

    it('should handle ships located at more than one coordinate',  () => { 

        player = {
            ships: [
                {
                    locations: [[0,0], [0,1]]
                }
            ]
        }

        expect(checkForShip(player, [0, 1])).to.be.true;
        expect(checkForShip(player, [9, 9])).to.be.false;
    });
});
`}</MDXTag></MDXTag>
<MDXTag name="p" components={components}>{`// example in the /src/js/ship_methods.js`}</MDXTag>
<MDXTag name="p" components={components}>{`function checkForShip (player, coordinates) {`}</MDXTag>
<MDXTag name="pre" components={components}><MDXTag name="code" components={components} parentName="pre" props={{}}>{`var shipPresent, ship;

for (var i = 0; i < player.ships.length; i++) {
    ship = player.ships[i];

    shipPresent = ship.locations.filter( (actualCoordinate) => {
        return (actualCoordinate[0] === coordinates[0]) && (actualCoordinate[1] === coordinates[1]);
    })[0];

    if (!shipPresent) {
        return false;
    }
}
`}</MDXTag></MDXTag>
<MDXTag name="p" components={components}>{`}`}</MDXTag>
           </MDXTag>
  }
