  import React from 'react'
  import { MDXTag } from '@mdx-js/tag'


export default class MDXContent extends React.Component {
  constructor(props) {
    super(props)
    this.layout = null
  }
  render() {
    const { components, ...props } = this.props

    return <MDXTag
             name="wrapper"

             components={components}><MDXTag name="h1" components={components} props={{"id":"react-native-style-guide"}}>{`React-Native Style Guide`}</MDXTag>
<MDXTag name="h3" components={components} props={{"id":"react-native-style-guide-1"}}>{`React Native Style Guide`}</MDXTag>
<MDXTag name="h4" components={components} props={{"id":"general-guidelines"}}>{`General Guidelines`}</MDXTag>
<MDXTag name="ul" components={components}>
<MDXTag name="li" components={components} parentName="ul">{`Every component should be treated as an API.`}</MDXTag>
<MDXTag name="li" components={components} parentName="ul">{`When in doubt, break up a component or function into smaller useful parts.`}</MDXTag>
</MDXTag>
<MDXTag name="h4" components={components} props={{"id":"es6-and-es7"}}>{`ES6 and ES7`}</MDXTag>
<MDXTag name="p" components={components}>{`The flavor of JS we use is based on ES5 but with ES6 and ES7 transforms. This means, that a large part of the syntax is based on the current version of JS, but is flavored with capabilities that come with ES6 and ES7. For example:`}</MDXTag>
<MDXTag name="ul" components={components}>
<MDXTag name="li" components={components} parentName="ul">{`Object destructing (ES6)`}</MDXTag>
<MDXTag name="li" components={components} parentName="ul">{`Object spread (ES7)`}</MDXTag>
<MDXTag name="li" components={components} parentName="ul">{`Rest and spread params (ES6)`}</MDXTag>
<MDXTag name="li" components={components} parentName="ul">{`Promises (ES6)`}</MDXTag>
<MDXTag name="li" components={components} parentName="ul">{`Arrow Functions (ES6)`}</MDXTag>
<MDXTag name="li" components={components} parentName="ul">{`Object concise method (ES6)`}</MDXTag>
<MDXTag name="li" components={components} parentName="ul">{`Object short notation (ES6)`}</MDXTag>
<MDXTag name="li" components={components} parentName="ul">{`Classes (ES6)`}</MDXTag>
</MDXTag>
<MDXTag name="p" components={components}>{`For more information see `}<MDXTag name="a" components={components} parentName="p" props={{"href":"https://facebook.github.io/react-native/docs/javascript-environment.html#content"}}>{`React Native JavaScript Environment`}</MDXTag></MDXTag>
<MDXTag name="h4" components={components} props={{"id":"component-creation"}}>{`Component Creation`}</MDXTag>
<MDXTag name="p" components={components}>{`Components should be created using ES6 classes and Object Short Notation.`}</MDXTag>
<MDXTag name="pre" components={components}><MDXTag name="code" components={components} parentName="pre" props={{"className":"language-javascript"}}>{`// Bad

var ChatBox = React.createClass({

  getInitialState: function(){
    return {toggle: 'on'};
  },

  render: function(){
    return (
      <Text> Example </Text>
    );
  }

})

// Good

class ChatBox extends React.Component{

  constructor(){
    this.state = {toggle: 'on'}
  }

  render(){
    return (
      <Text> Example </Text>
    );
  }

}
`}</MDXTag></MDXTag>
<MDXTag name="p" components={components}>{`Component functions that are not life-cycle functions should be created with an underscore at the beginning of the function name.`}</MDXTag>
<MDXTag name="pre" components={components}><MDXTag name="code" components={components} parentName="pre" props={{"className":"language-javascript"}}>{`_fetchmessages(){
  fetch(requestURL)
    .then((response)=> response.json() )
    .then((responseData)=> {
      this.setState({messages: responseData})
    })
    .done();
}

_turnEditOn(){
  this.setState({edit: true});
}
`}</MDXTag></MDXTag>
<MDXTag name="p" components={components}>{`Keep life-cycle functions at the top of the object constructor in logical order.`}</MDXTag>
<MDXTag name="ol" components={components}>
<MDXTag name="li" components={components} parentName="ol"><MDXTag name="inlineCode" components={components} parentName="li">{`constructor`}</MDXTag></MDXTag>
<MDXTag name="li" components={components} parentName="ol"><MDXTag name="inlineCode" components={components} parentName="li">{`componentWillMount`}</MDXTag></MDXTag>
<MDXTag name="li" components={components} parentName="ol"><MDXTag name="inlineCode" components={components} parentName="li">{`componentDidMount`}</MDXTag></MDXTag>
<MDXTag name="li" components={components} parentName="ol"><MDXTag name="inlineCode" components={components} parentName="li">{`componentWillReceiveProps`}</MDXTag></MDXTag>
<MDXTag name="li" components={components} parentName="ol"><MDXTag name="inlineCode" components={components} parentName="li">{`render`}</MDXTag></MDXTag>
</MDXTag>
<MDXTag name="p" components={components}>{`For more information see `}<MDXTag name="a" components={components} parentName="p" props={{"href":"https://facebook.github.io/react/docs/component-specs.html"}}>{`Component Specs and Lifecycle`}</MDXTag></MDXTag>
<MDXTag name="p" components={components}>{`Every component function should be as parsimonious as possible. If you find yourself creating a function that is doing more than two things at once, then break up the function into smaller functions.`}</MDXTag>
<MDXTag name="h4" components={components} props={{"id":"using-bind"}}>{`Using Bind`}</MDXTag>
<MDXTag name="p" components={components}>{`When passing a function as prop to a child component, the use of bind is not necessary. React Native automatically binds `}<MDXTag name="inlineCode" components={components} parentName="p">{`this`}</MDXTag>{` to that function.`}</MDXTag>
<MDXTag name="pre" components={components}><MDXTag name="code" components={components} parentName="pre" props={{"className":"language-javascript"}}>{`// Bad

render(){
  return(
    <CameraButton
      selectImage={this.selectImage.bind(this)}
    />
  );
}

// Good

render(){
  return(
    <CameraButton
      selectImage={this.selectImage}
    />
  );
}
`}</MDXTag></MDXTag>
<MDXTag name="p" components={components}>{`It necessary however to use bind, when passing a component function to an event prop. For example:`}</MDXTag>
<MDXTag name="pre" components={components}><MDXTag name="code" components={components} parentName="pre" props={{"className":"language-javascript"}}>{`<ListView
  renderRow={this.renderMessage.bind(this)}
/>

...

renderMessage(message) {
  return (
    <Message
      message={message}
      navigator={this.props.navigator}
      fetchMessages={this.fetchMessages.bind}
    />
  );
}
`}</MDXTag></MDXTag>
<MDXTag name="h4" components={components} props={{"id":"passing-props"}}>{`Passing Props`}</MDXTag>
<MDXTag name="p" components={components}>{`Explicitly pass props if the number of props is small or if the prop is important enough that you want to be explicit about it.`}</MDXTag>
<MDXTag name="p" components={components}>{`If there are a large number of props, pass in the whole thing or use destructuring to break it apart and pass them correctly.`}</MDXTag>
<MDXTag name="pre" components={components}><MDXTag name="code" components={components} parentName="pre" props={{"className":"language-javascript"}}>{`//Bad

<MessageBody
  content=this.props.message.content
/>
<MessageFooter
  replies=this.props.message.replies
  upvotes=this.props.message.upvotes
  timestamp=this.props.message.timestamp
  distance=this.props.message.distance
/>

// Good

var {content, ...footer} = this.props.message

<MessageBody {content} />
<MessageFooter {..footer} />
`}</MDXTag></MDXTag>
<MDXTag name="p" components={components}>{`Sometimes you want to combine objects and pass them as props. In that case use `}<MDXTag name="inlineCode" components={components} parentName="p">{`Object.assign`}</MDXTag>{`.`}</MDXTag>
<MDXTag name="pre" components={components}><MDXTag name="code" components={components} parentName="pre" props={{"className":"language-javascript"}}>{`_onPressMessage() {

  var {message, ...props} = this.props;
  var {votes, ...message} = this.props.message;
  var fetchMessages = this._updateHearts;

  this.props.navigator({
    component: Comments,
    passProps: Object.assign(
      {..props},
      {..message},
      {fetchMessages}
    ),
  })
}
`}</MDXTag></MDXTag>
<MDXTag name="p" components={components}>{`For more information on passing props, see React Native `}<MDXTag name="a" components={components} parentName="p" props={{"href":"https://facebook.github.io/react/docs/transferring-props.html"}}>{`Transferring Props`}</MDXTag></MDXTag>
<MDXTag name="h4" components={components} props={{"id":"state-and-props"}}>{`State and Props`}</MDXTag>
<MDXTag name="p" components={components}>{`Props are immutable. Do not mutate them.`}</MDXTag>
<MDXTag name="pre" components={components}><MDXTag name="code" components={components} parentName="pre" props={{"className":"language-javascript"}}>{`// Bad

var component = <Component />;
component.props.foo = x;
component.props.bar = y;
`}</MDXTag></MDXTag>
<MDXTag name="p" components={components}>{`State is mutable.`}</MDXTag>
<MDXTag name="pre" components={components}><MDXTag name="code" components={components} parentName="pre" props={{"className":"language-javascript"}}>{`constructor(){
  this.state = {messages:[]};
}

...

_addMessages(data){
  var messages = data.messages;
  this.setState({
    messages: this.state.messages.push(messages)
  })
}
`}</MDXTag></MDXTag>
<MDXTag name="p" components={components}>{`Props can be passed into state, but be explicit that they are only 'initial values'`}</MDXTag>
<MDXTag name="pre" components={components}><MDXTag name="code" components={components} parentName="pre" props={{"className":"language-javascript"}}>{`constructor(props){
  var initialNum = this.props.numHearts;
  this.state = {numHearts: initialNum};
}

...

_handleClick(){
  this.setState({numHearts: this.state.numHearts + 1});
}
`}</MDXTag></MDXTag>
<MDXTag name="p" components={components}>{`Be sure to  make use of `}<MDXTag name="inlineCode" components={components} parentName="p">{`componentWillReceiveProps`}</MDXTag>{` to sync state and props, if props are passed to state.`}</MDXTag>
<MDXTag name="pre" components={components}><MDXTag name="code" components={components} parentName="pre" props={{"className":"language-javascript"}}>{`componentWillReceiveProps(props){
  this.setState({numHearts: props.numHearts});
}
`}</MDXTag></MDXTag>
<MDXTag name="h1" components={components} props={{"id":"react-style-guide-cheet-sheet"}}>{`React Style Guide Cheet Sheet`}</MDXTag>
<MDXTag name="h2" components={components} props={{"id":"custom-classes"}}>{`Custom Classes`}</MDXTag>
<MDXTag name="ul" components={components}>
<MDXTag name="li" components={components} parentName="ul">{`place custom functions above the render function.`}</MDXTag>
</MDXTag>
<MDXTag name="pre" components={components}><MDXTag name="code" components={components} parentName="pre" props={{"className":"language-javascript"}}>{`React.createClass({
  displayName : '',
  propTypes: {},
  mixins : [],
  getInitialState : function() {},
  componentWillMount : function() {},
  componentWillUnmount : function() {},
  _onChange : function() {},
  _onCreate : function() {},
  render : function() {}
});
`}</MDXTag></MDXTag>
<MDXTag name="h2" components={components} props={{"id":"conditional-html"}}>{`Conditional html`}</MDXTag>
