  import React from 'react'
  import { MDXTag } from '@mdx-js/tag'


export default class MDXContent extends React.Component {
  constructor(props) {
    super(props)
    this.layout = null
  }
  render() {
    const { components, ...props } = this.props

    return <MDXTag
             name="wrapper"

             components={components}><MDXTag name="h1" components={components} props={{"id":"lists-and-adapters-in-android"}}>{`Lists and Adapters in Android`}</MDXTag>
<MDXTag name="p" components={components}><MDXTag name="strong" components={components} parentName="p">{`What is an adapter?`}</MDXTag></MDXTag>
<MDXTag name="p" components={components}>{`If we have our data and a list view, we can then use an adapter to adapt data for the view.`}</MDXTag>
<MDXTag name="p" components={components}>{`Android provides a number of different adapters.`}</MDXTag>
<MDXTag name="p" components={components}>{`Example with the `}<MDXTag name="inlineCode" components={components} parentName="p">{`ArrayAdapter`}</MDXTag>{`, it is an extension of the `}<MDXTag name="inlineCode" components={components} parentName="p">{`BaseAdapter`}</MDXTag>{`.`}</MDXTag>
<MDXTag name="p" components={components}>{`Declaring a string adapter would be `}<MDXTag name="inlineCode" components={components} parentName="p">{`ArrayAdapter<String>`}</MDXTag>{` that can deal with generic types. We specify the type within the angle brackets.`}</MDXTag>
<MDXTag name="pre" components={components}><MDXTag name="code" components={components} parentName="pre" props={{"className":"language-java"}}>{`package teamtreehouse.com.stormy.ui;

import ...

static class DailyForecastActivity extends ListActivity {
    @Override
    protected void onCreate(Bundle savedInstanceState) {
        super.onCreate(savedInstanceState);
        setContentView(R.layout.activity_daily_forecast);

        String[] daysOfTheWeek = {
            "Sunday",
            "Monday",
            ...
        }
        ArrayAdapter<String> adapter = new ArrayAdapter<String>(this, android.R.layout.simple_list_item_1,
            daysOfTheWeek);

        setListAdapter(adapter);
    }
}
`}</MDXTag></MDXTag>
           </MDXTag>
