  import React from 'react'
  import { MDXTag } from '@mdx-js/tag'


export default class MDXContent extends React.Component {
  constructor(props) {
    super(props)
    this.layout = null
  }
  render() {
    const { components, ...props } = this.props

    return <MDXTag
             name="wrapper"

             components={components}><MDXTag name="h1" components={components} props={{"id":"flexbox-help-sheet"}}>{`Flexbox Help Sheet`}</MDXTag>
<MDXTag name="p" components={components}>{`Sources:`}</MDXTag>
<MDXTag name="p" components={components}><MDXTag name="a" components={components} parentName="p" props={{"href":"https://css-tricks.com/snippets/css/a-guide-to-flexbox/"}}>{`CSS Tricks`}</MDXTag></MDXTag>
<MDXTag name="h2" components={components} props={{"id":"terminology"}}>{`Terminology`}</MDXTag>
<MDXTag name="h4" components={components} props={{"id":"flex-container-parent"}}>{`Flex Container (parent)`}</MDXTag>
<MDXTag name="p" components={components}>{`Properties of the parent:`}</MDXTag>
<MDXTag name="pre" components={components}><MDXTag name="code" components={components} parentName="pre" props={{"className":"language-css"}}>{`.container {
  display: flex; /* or inline-flex */
  flex-direction: row | row-reverse | column | column-reverse;
  flex-wrap: nowrap | wrap | wrap-reverse;
  flex-flow: <‘flex-direction’> || <‘flex-wrap’>; //shorthand code
  justify-content: flex-start | flex-end | center | space-between | space-around;
  align-items: flex-start | flex-end | center | baseline | stretch;
  align-content: flex-start | flex-end | center | space-between | space-around | stretch;
}
`}</MDXTag></MDXTag>
<MDXTag name="h4" components={components} props={{"id":"flex-items-child"}}>{`Flex Items (child)`}</MDXTag>
<MDXTag name="p" components={components}>{`Properties of the child:`}</MDXTag>
<MDXTag name="pre" components={components}><MDXTag name="code" components={components} parentName="pre" props={{"className":"language-css"}}>{`.item {
    order: <integer>;
    flex-grow: <number>; /* default 0 */
    flex-shrink: <number>; /* default 1 */
    flex-basis: <length> | auto; /* default auto */
    flex: none | [ <'flex-grow'> <'flex-shrink'>? || <'flex-basis'> ];
    align-self: auto | flex-start | flex-end | center | baseline | stretch;
}
`}</MDXTag></MDXTag>
<MDXTag name="h2" components={components} props={{"id":"example"}}>{`Example`}</MDXTag>
<MDXTag name="pre" components={components}><MDXTag name="code" components={components} parentName="pre" props={{"className":"language-css"}}>{`.parent {
  display: flex;
  height: 300px; /* Or whatever */
}

.child {
  width: 100px;  /* Or whatever */
  height: 100px; /* Or whatever */
  margin: auto;  /* Magic! */
}

.flex-container {
  /* We first create a flex layout context */
  display: flex;

  /* Then we define the flow direction and if we allow the items to wrap
   * Remember this is the same as:
   * flex-direction: row;
   * flex-wrap: wrap;
   */
  flex-flow: row wrap;

  /* Then we define how is distributed the remaining space */
  justify-content: space-around;
}
`}</MDXTag></MDXTag>
           </MDXTag>
  }
}
