/* @jsx mdx */
  import React from 'react'
  import { mdx } from '@mdx-js/react'
  /* @jsx mdx */


const makeShortcode = name => function MDXDefaultShortcode(props) {
  console.warn("Component " + name + " was not imported, exported, or provided by MDXProvider as global scope")
  return <div {...props}/>
};

const layoutProps = {

};
const MDXLayout = "wrapper"
export default function MDXContent({
  components,
  ...props
}) {
  return <MDXLayout {...layoutProps} {...props} components={components} mdxType="MDXLayout">
    <h1 {...{
      "id": "verbes-auxiliaires"
    }}>{`Verbes Auxiliaires`}</h1>
    <h2 {...{
      "id": "resources"
    }}>{`Resources`}</h2>
    <ol>
      <li parentName="ol"><a parentName="li" {...{
          "href": "https://www.lawlessfrench.com/grammar/auxiliary-verbs/"
        }}>{`Auxiliary verbs - Lawless French`}</a></li>
      <li parentName="ol"><a parentName="li" {...{
          "href": "https://www.lawlessfrench.com/grammar/pronominal-verbs/"
        }}>{`Prominal verbs`}</a></li>
      <li parentName="ol"><a parentName="li" {...{
          "href": "https://www.lawlessfrench.com/grammar/auxiliary-verbs-2/"
        }}>{`More on auxiliary verbs - Lawless French`}</a></li>
    </ol>
    <h2 {...{
      "id": "what-are-auxiliary-verbs"
    }}>{`What are Auxiliary Verbs`}</h2>
    <p>{`Auxiliary verbs are also known as helping verbs, because they help form compound conjugations, meaning compound tenses like the passé composé as well as compound moods like the past subjunctive.`}</p>
    <p>{`With compound conjugations there are two verbs:`}</p>
    <ol>
      <li parentName="ol">{`The auxiliary verb, which conjugates for the required tense or mood; and`}</li>
      <li parentName="ol">{`The main verb, which is always in the form of the past participle.`}</li>
    </ol>
    <h2 {...{
      "id": "avoir-vs-être"
    }}>{`Avoir vs Être`}</h2>
    <p>{`French has only two auxiliary verbs.`}</p>
    <ol>
      <li parentName="ol">{`Avoir is the auxiliary for most French verbs.`}</li>
      <li parentName="ol">{`There are notable exceptions for which être is the auxiliary.`}</li>
    </ol>
    <blockquote>
      <p parentName="blockquote">{`Whichever verb it is, the auxiliary is consistent for all compound conjugations: avoir verbs always take avoir as their auxiliary verb for all compound tenses and moods, while être verbs always take être. However, there are a few verbs that can take either auxiliary, depending on how they’re used: learn more.`}</p>
    </blockquote>
    <h2 {...{
      "id": "être-verbs"
    }}>{`Être verbs`}</h2>
    <ol>
      <li parentName="ol">{`All pronominal verbs`}</li>
      <li parentName="ol">{`Intransitive verbs of movement`}</li>
    </ol>
    <h2 {...{
      "id": "tips-for-learning-la-maison-dêtre"
    }}>{`Tips for learning: La Maison d’être`}</h2>
    <p>{`Native French-speaking teachers teach être verbs by having students draw a house with a door, windows, at least two rooms, and a staircase, and then label it with the être verbs. For example, draw a figure passing in front of the house (passer), another entering it (entrer), and a third going upstairs (monter).`}</p>
    <h2 {...{
      "id": "other-learning-mnemonics"
    }}>{`Other learning mnemonics`}</h2>
    <ul>
      <li parentName="ul">{`DR & MRS VANDERTRAMP`}</li>
      <li parentName="ul">{`ADVENT`}</li>
      <li parentName="ul">{`DRAPERS VAN MMT13`}</li>
    </ul>
    <p>{`Check online for the verbs that come from the mneumonics.`}</p>
    </MDXLayout>;
}

MDXContent.isMDXComponent = true;
