  import React from 'react'
  import { MDXTag } from '@mdx-js/tag'


export default class MDXContent extends React.Component {
  constructor(props) {
    super(props)
    this.layout = null
  }
  render() {
    const { components, ...props } = this.props

    return <MDXTag
             name="wrapper"

             components={components}><MDXTag name="h1" components={components} props={{"id":"setting-up-a-lambdacloudfront-setup-to-fetch-subdirectories-in-s3"}}>{`Setting up a Lambda/Cloudfront setup to fetch subdirectories in S3`}</MDXTag>
<MDXTag name="h2" components={components} props={{"id":"links"}}>{`Links`}</MDXTag>
<MDXTag name="ul" components={components}>
<MDXTag name="li" components={components} parentName="ul"><MDXTag name="a" components={components} parentName="li" props={{"href":"https://aws.amazon.com/blogs/compute/implementing-default-directory-indexes-in-amazon-s3-backed-amazon-cloudfront-origins-using-lambdaedge/"}}>{`Useful blog guide`}</MDXTag></MDXTag>
<MDXTag name="li" components={components} parentName="ul"><MDXTag name="a" components={components} parentName="li" props={{"href":"https://medium.com/@svinkle/how-to-deploy-a-react-app-to-a-subdirectory-f694d46427c1"}}>{`Serving CRA from subdirectories`}</MDXTag></MDXTag>
</MDXTag>
<MDXTag name="h2" components={components} props={{"id":"setup"}}>{`Setup`}</MDXTag>
<MDXTag name="h3" components={components} props={{"id":"1-s3-bucket"}}>{`1. S3 Bucket`}</MDXTag>
<MDXTag name="p" components={components}>{`Set up a restricted S3 bucket. Within this bucket, have subdirectories that house their own index.html file.`}</MDXTag>
<MDXTag name="h3" components={components} props={{"id":"2-set-up-cloudfront"}}>{`2. Set up CloudFront`}</MDXTag>
<MDXTag name="p" components={components}>{`Follow the permissions setup in the above article to know how to set up the correct event.`}</MDXTag>
<MDXTag name="h3" components={components} props={{"id":"3-this-is-the-setup-for-a-javascript-lambda-call"}}>{`3. This is the setup for a JavaScript Lambda call.`}</MDXTag>
<MDXTag name="p" components={components}>{`This must be done in a region that supports Lambda@next.`}</MDXTag>
<MDXTag name="pre" components={components}><MDXTag name="code" components={components} parentName="pre" props={{"className":"language-javascript"}}>{`'use strict';
exports.handler = (event, context, callback) => {
 // Extract the request from the CloudFront event that is sent to Lambda@Edge
 var request = event.Records[0].cf.request;

 // Extract the URI from the request
 var olduri = request.uri;

 // Match any '/' that occurs at the end of a URI. Replace it with a default index
 var newuri = olduri.replace(/\/\$/, '/index.html');

 // Log the URI as received by CloudFront and the new URI to be used to fetch from origin
 console.log('Old URI: ' + olduri);
 console.log('New URI: ' + newuri);

 // Replace the received URI with the URI that includes the index page
 request.uri = newuri;

 // Return to CloudFront
 return callback(null, request);
};
`}</MDXTag></MDXTag>
<MDXTag name="h3" components={components} props={{"id":"deploying-cra-app"}}>{`Deploying CRA App`}</MDXTag>
<MDXTag name="h3" components={components} props={{"id":"1-update-packagejson"}}>{`1. Update package.json`}</MDXTag>
<MDXTag name="pre" components={components}><MDXTag name="code" components={components} parentName="pre" props={{"className":"language-json"}}>{`"homepage": ".",
`}</MDXTag></MDXTag>
<MDXTag name="h3" components={components} props={{"id":"2-update-router"}}>{`2. Update router`}</MDXTag>
<MDXTag name="pre" components={components}><MDXTag name="code" components={components} parentName="pre" props={{"className":"language-javascript"}}>{`export const devRouter = () => (
 <Router history={browserHistory}>
  <Route path={'/page-profile'} component={PageProfile} />
  <Route path="*" exact={true} component={PageHome} />
 </Router>
);

export const prodRouter = () => (
 <Router history={browserHistory}>
  <Route path={'/page-profile'} component={PageProfile} />
  <Route path={'/'} component={PageHome} />
  <Route path={'*'} exact={true} component={PageHome} />
 </Router>
);

export const router = () =>
 process.env.NODE_ENV !== 'development' ? prodRouter() : devRouter();
`}</MDXTag></MDXTag>
<MDXTag name="h3" components={components} props={{"id":"3-update-routerpush-calls"}}>{`3. Update router.push calls`}</MDXTag>
<MDXTag name="pre" components={components}><MDXTag name="code" components={components} parentName="pre" props={{"className":"language-javascript"}}>{`import Config from 'src/app.json';
router.push(process.env.PUBLIC_URL + '/test');

// In use
function handleLink(e, d) {
 if (d.href[0] === '/') {
  e.preventDefault();
  const { router } = this.props;
  router.push(Config.baseUrl + d.href);
 }
}
`}</MDXTag></MDXTag>
<MDXTag name="h3" components={components} props={{"id":"4-set-a-baseurl-in-appjson"}}>{`4. Set a baseUrl in app.json`}</MDXTag>
<MDXTag name="pre" components={components}><MDXTag name="code" components={components} parentName="pre" props={{"className":"language-json"}}>{`{
 "baseUrl": "/dato-cms-test"
}
`}</MDXTag></MDXTag>
           </MDXTag>
  }
}
