/* @jsx mdx */
  import React from 'react'
  import { mdx } from '@mdx-js/react'
  /* @jsx mdx */


const makeShortcode = name => function MDXDefaultShortcode(props) {
  console.warn("Component " + name + " was not imported, exported, or provided by MDXProvider as global scope")
  return <div {...props}/>
};

const layoutProps = {

};
const MDXLayout = "wrapper"
export default function MDXContent({
  components,
  ...props
}) {
  return <MDXLayout {...layoutProps} {...props} components={components} mdxType="MDXLayout">
    <h1 {...{
      "id": "developers-guide-to-typescript"
    }}>{`Developer's Guide to TypeScript`}</h1>
    <h2 {...{
      "id": "types"
    }}>{`Types`}</h2>
    <p>{`For types, we have all our basic types and we can also have custom types.`}</p>
    <p>{`There are two types, `}<inlineCode parentName="p">{`primitive`}</inlineCode>{` and `}<inlineCode parentName="p">{`object`}</inlineCode>{` - the `}<inlineCode parentName="p">{`object`}</inlineCode>{` types itself can be what we define.`}</p>
    <p>{`Why do we care? The compiler uses it to search for errors and it allows other engineers to understand what type of data is flowing around.`}</p>
    <pre><code parentName="pre" {...{
        "className": "language-javascript"
      }}>{`interface Todo {
  id: number;
  completed: boolean;
};

const todo = obj.data as Todo;
`}</code></pre>
    <h2 {...{
      "id": "declaring-types-examples"
    }}>{`Declaring types examples`}</h2>
    <pre><code parentName="pre" {...{
        "className": "language-javascript"
      }}>{`// array
let colors: string[] = ['a', 'b', 'c'];

// class
class Car {}
let car: Car = new Car();

// object literal
let point: { x: number, y: number } = {
  x: 10,
  y: 20
};

// functions
const logNumber: (i: number) => void = (i: number) => {
  console.log(i);
};
`}</code></pre>
    <p>{`The object literal gets a bit nasty, but we have ways around this using `}<inlineCode parentName="p">{`types`}</inlineCode>{` and `}<inlineCode parentName="p">{`interfaces`}</inlineCode>{`.`}</p>
    <h2 {...{
      "id": "the-any-type"
    }}>{`The 'any' type`}</h2>
    <pre><code parentName="pre" {...{
        "className": "language-javascript"
      }}>{`const json = '{"x":10, "y": 20}';
const coordinates = JSON.parse(json); // by default gets any time

// this is the better
const coor: { x: number, y: number } = JSON.parse(json);
`}</code></pre>
    <h2 {...{
      "id": "the-void-and-never-return-types"
    }}>{`The 'void' and 'never' return types`}</h2>
    <pre><code parentName="pre" {...{
        "className": "language-javascript"
      }}>{`// Returns nothing
const logger = (message: string): void => console.log(message);

// Never returns
const throwError = (message: string): never => throw new Error(message);
`}</code></pre>
    <h2 {...{
      "id": "interfaces"
    }}>{`Interfaces`}</h2>
    <pre><code parentName="pre" {...{
        "className": "language-javascript"
      }}>{`interface Vehicle {
  name: string;
  year: number;
  broken: boolean;
}

const oldCivic = {
  name: 'civic',
  year: 2000,
  broken: true
};

const printVehicle = (vehicle: Vehicle): void => {};
`}</code></pre>
    <h2 {...{
      "id": "abstract-classes"
    }}>{`Abstract Classes`}</h2>
    <p>{`Good for classes that we only want to use to extend. Think expectation vs reality.`}</p>
    <p>{`Abstract classes:`}</p>
    <ol>
      <li parentName="ol">{`Cannot create objects directly`}</li>
      <li parentName="ol">{`Only used as a parent class`}</li>
      <li parentName="ol">{`Can contain real implementations of some methods`}</li>
      <li parentName="ol">{`Can refer to methods that don't exist yet`}</li>
    </ol>
    <pre><code parentName="pre" {...{
        "className": "language-javascript"
      }}>{`abstract class Examlpe {
  // promises we will create this func later
  abstract requireFunc(argA: number): void;

  swap = (a:number) => requireFunc(a);
}
`}</code></pre>
    <h2 {...{
      "id": "abstract-classes-vs-interfaces"
    }}>{`Abstract classes vs Interfaces`}</h2>
    <ul>
      <li parentName="ul">{`Interfaces promot loose coupling, Abstract/Inheritance is for strong coupling`}</li>
      <li parentName="ul">{`Interfaces for very different objects we want to work together, Abstract/Inheritance for when we want to build up a definition of an object`}</li>
    </ul>
    <h2 {...{
      "id": "enums"
    }}>{`Enums`}</h2>
    <pre><code parentName="pre" {...{
        "className": "language-javascript"
      }}>{`enum Result {
  Win = 'W',
  Loss = 'L',
  Draw = 'D'
}
`}</code></pre>
    <h2 {...{
      "id": "generics"
    }}>{`Generics`}</h2>
    <ul>
      <li parentName="ul">{`Like function args, but for types in class/function definitions`}</li>
      <li parentName="ul">{`Allow us to define the type of a property/argument/return value at a future point`}</li>
      <li parentName="ul">{`Used heavily when writing useable code`}</li>
    </ul>
    <pre><code parentName="pre" {...{
        "className": "language-javascript"
      }}>{`class HoldAnything<TypeOfData> {
  data: TypeOfData;
}

const holdNumber = new HolderAnything<number>();
const holdString = new HolderAnything<string>();

interface Coordinates {
  x: number;
  y: number;
}

const holdCoordinates = new HolderAnything<Coordinates>();

// TypeOfData can actually be called anything
// Convention is normally T, but then we go alphabetical order if we need multiple generics

class holdMultipleAnything<T,U,V> {
  firstThing: T;
  secondThing: U;
  thirdThing: V;
}

const holdAllTheThings = new holdMultipleAnything<string, number, Coordinates>();
`}</code></pre>
    <h3 {...{
      "id": "constraints-with-generics"
    }}>{`Constraints with Generics`}</h3>
    <ul>
      <li parentName="ul">{`Cannot call base methods on generics since there is no guarantee without a interface`}</li>
      <li parentName="ul"></li>
    </ul>
    <pre><code parentName="pre" {...{
        "className": "language-javascript"
      }}>{`class Car {
  print() {
    console.log('Car');
  }
}

class House {
  print() {
    console.log('House');
  }
}

interface Printable {
  print(): void;
}

function printWhatever<T extends Printable>(el: T) {
  el.print();
}

printWhatever(new Car());
printWhatever(new House());
`}</code></pre>
    <pre><code parentName="pre" {...{
        "className": "language-javascript"
      }}>{`// what you have to do without method overloading
class Utility {
  static function addNumbers(a:number, b: number): number {
    return a + b;
  }

  static function addStrings(a:string, b: string): string {
    return a + " " + b;
  }
}
Utility.addNumbers(1,2) // return 2
Utility.addStrings('Hello','World') // return "Hello World"

// method overloading in other languages
class Utility {
  static function add(a:number, b: number): number {
    return a + b;
  }

  static function add(a:string, b: string): string {
    return a + " " + b;
  }
}
Utility.add(1,2) // return 2
Utility.add('Hello','World') // return "Hello World"

// method overloading in other typescript
class Utility {
  static function add(a:number, b: number): number;
  static function add(a:string, b: string): string;

  static function add(a, b) {
    return a + b;
  }
}
Utility.add(1,2) // return 2
Utility.add('Hello','World') // return "HelloWorld"
`}</code></pre>
    </MDXLayout>;
}

MDXContent.isMDXComponent = true;
