/* @jsx mdx */
  import React from 'react'
  import { mdx } from '@mdx-js/react'
  /* @jsx mdx */


const makeShortcode = name => function MDXDefaultShortcode(props) {
  console.warn("Component " + name + " was not imported, exported, or provided by MDXProvider as global scope")
  return <div {...props}/>
};

const layoutProps = {

};
const MDXLayout = "wrapper"
export default function MDXContent({
  components,
  ...props
}) {
  return <MDXLayout {...layoutProps} {...props} components={components} mdxType="MDXLayout">
    <h1 {...{
      "id": "nodejs-saas-with-stripe"
    }}>{`Node.js SaaS with Stripe`}</h1>
    <p>{`The basics plan is to:`}</p>
    <ol>
      <li parentName="ol">{`Create a test customer for the subscription.`}</li>
      <li parentName="ol">{`Create a product.`}</li>
      <li parentName="ol">{`Create a plan for that product.`}</li>
      <li parentName="ol">{`Create a subscription for that plan.`}</li>
      <li parentName="ol">{`Repeating for a few products.`}</li>
      <li parentName="ol">{`Writing a test script to do all this.`}</li>
      <li parentName="ol">{`Fetching back a list of subscriptions for that particular user.`}</li>
      <li parentName="ol">{`Writing out an example using Express servers.`}</li>
    </ol>
    <h2 {...{
      "id": "building-out-the-base-functions-to-use-for-the-express-app"
    }}>{`Building out the base functions to use for the Express app`}</h2>
    <pre><code parentName="pre" {...{
        "className": "language-javascript"
      }}>{`// controllers/stripe/index.js
const stripe = require('stripe')(process.env.STRIPE_SECRET_KEY);

/**
 * Create a new customer based off an email address.
 *
 * @param {string} email
 * @returns {Promise} Result of attempting to create a customer.
 */
function customerCreate(email) {
  return new Promise(async (resolve, reject) => {
    try {
      const customer = await stripe.customers.create({ email: email });
      resolve(customer);
    } catch (err) {
      reject(err);
    }
  });
}

/**
 * Register a new product.
 * @example
 * {
 * name: 'My SaaS Platform',
 * type: 'service'
 * }
 *
 * @param {Object} data Name and type of product
 * @returns {Promise} Request to create new product
 */
function productCreate(data) {
  return new Promise(async (resolve, reject) => {
    try {
      const res = await stripe.products.create(data);
      resolve(res);
    } catch (err) {
      reject(err);
    }
  });
}

/**
 * Create a new plan with a product.
 * @example
 * {
 * currency: 'usd',
 * interval: 'month',
 * product: 'prod_id',
 * nickname: 'Pro Plan',
 * amount: 12
 * }
 *
 * @param {*} data
 * @returns
 */
function planCreate(data) {
  return new Promise(async (resolve, reject) => {
    try {
      const res = await stripe.plans.create(data);
      resolve(res);
    } catch (err) {
      reject(err);
    }
  });
}

/**
 * Create a new subscription for a user based on * a plan.
 * @example
 * {
 * customer: 'customer_id',
 * items: [
 *   {
 *     plan: 'plan_abc',
 *     quantity: 2
 *   }
 * ]
 * }
 *
 * @param {*} data
 * @returns
 */
function subscriptionCreate(data) {
  return new Promise(async (resolve, reject) => {
    try {
      const res = await stripe.subscriptions.create(data);
      resolve(res);
    } catch (err) {
      reject(err);
    }
  });
}

/**
 * Fetch the subscription list.
 * @example
 * {
 * { limit: 99 },
 * customer: 'cus_id'
 * }
 *
 * @param {*} data
 * @returns
 */
function subscriptionList(data) {
  return new Promise(async (resolve, reject) => {
    try {
      const res = await stripe.subscriptions.list(data);
      resolve(res);
    } catch (err) {
      reject(err);
    }
  });
}
`}</code></pre>
    </MDXLayout>;
}

MDXContent.isMDXComponent = true;
