<MDXTag name="pre" components={components}><MDXTag name="code" components={components} parentName="pre" props={{"className":"language-javascript"}}>{`import React, { Component } from 'react';
import { connect } from 'react-redux';

class BookDetail extends Component {
    render() {
        if (!this.props.book) {
            return <div>Select a book to begin</div>
        }

        return (
            <div>
                <h3>Details for:</h3>
                <div>{this.props.book.titile}</div>
            </div>
        );
    }
}

function mapStateToProps(state) {
    return {
        // activeBook from our activeBook pierce of state
