  import React from 'react'
  import { MDXTag } from '@mdx-js/tag'


export default class MDXContent extends React.Component {
  constructor(props) {
    super(props)
    this.layout = null
  }
  render() {
    const { components, ...props } = this.props

    return <MDXTag
             name="wrapper"

             components={components}><MDXTag name="h1" components={components} props={{"id":"th-react-basics-notes"}}>{`TH React Basics Notes`}</MDXTag>
<MDXTag name="h2" components={components} props={{"id":"threact-1-first-steps-in-react"}}>{`THREACT-1: First Steps in React`}</MDXTag>
<MDXTag name="h4" components={components} props={{"id":"threact-2-why-react"}}>{`THREACT-2: Why React`}</MDXTag>
<MDXTag name="p" components={components}>{`React is a library for creating UIs.`}</MDXTag>
<MDXTag name="p" components={components}>{`Two huge benefits:`}</MDXTag>
<MDXTag name="ol" components={components}>
<MDXTag name="li" components={components} parentName="ol">{`It's declarative`}</MDXTag>
<MDXTag name="li" components={components} parentName="ol">{`It's component based`}</MDXTag>
</MDXTag>
<MDXTag name="p" components={components}><MDXTag name="strong" components={components} parentName="p">{`Components`}</MDXTag></MDXTag>
<MDXTag name="p" components={components}>{`UI Composed of divs, spans, inputs and others.`}</MDXTag>
<MDXTag name="p" components={components}>{`React allows us to build our own components and use them as built in HTML elements.`}</MDXTag>
<MDXTag name="p" components={components}>{`Tested once, reused everywhere.`}</MDXTag>
<MDXTag name="p" components={components}><MDXTag name="strong" components={components} parentName="p">{`Declarative`}</MDXTag></MDXTag>
<MDXTag name="p" components={components}>{`This means our program describes what we are doing.`}</MDXTag>
<MDXTag name="p" components={components}>{`JS is normally imperative, whereas something like HTML is declarative.`}</MDXTag>
<MDXTag name="p" components={components}>{`Normally, we would have a data model with JS and then the view made up with HTML. This is hard to keep in sync.`}</MDXTag>
<MDXTag name="p" components={components}>{`Example, if the second item in the data model is removed, then we would also need to remove a second list item in HTML.`}</MDXTag>
<MDXTag name="p" components={components}>{`React makes an efficient and fast way to "remove a DOM and reload". This is done by using a `}<MDXTag name="em" components={components} parentName="p">{`Virtual DOM`}</MDXTag></MDXTag>
<MDXTag name="h4" components={components} props={{"id":"threact-3-state-and-the-virtual-dom"}}>{`THREACT-3: State and the Virtual DOM`}</MDXTag>
<MDXTag name="p" components={components}>{`We describe the application entirely in the .js files.`}</MDXTag>
<MDXTag name="p" components={components}>{`The JS representation of the DOM is referred to as the Virtual DOM. This is cheap and fast to use.`}</MDXTag>
<MDXTag name="p" components={components}>{`Interacting with the DOM is a lot slower than manipulating JavaScript.`}</MDXTag>
<MDXTag name="p" components={components}>{`When we write our React markup code, React takes on the responsibility of rendering the real DOM element on the Virtual DOM we define.`}</MDXTag>
<MDXTag name="p" components={components}>{`It's smart to remember the previous and current DOM and makes the minimum changes needed.`}</MDXTag>
<MDXTag name="h4" components={components} props={{"id":"threact-3-understanding-jsx"}}>{`THREACT-3: Understanding JSX`}</MDXTag>
<MDXTag name="p" components={components}>{`DOM Elements have:`}</MDXTag>
<MDXTag name="ol" components={components}>
<MDXTag name="li" components={components} parentName="ol">{`The name eg. div, span`}</MDXTag>
