  import React from 'react'
  import { MDXTag } from '@mdx-js/tag'


export default class MDXContent extends React.Component {
  constructor(props) {
    super(props)
    this.layout = null
  }
  render() {
    const { components, ...props } = this.props

    return <MDXTag
             name="wrapper"

             components={components}><MDXTag name="h1" components={components} props={{"id":"mongo-cheat-sheet"}}>{`Mongo Cheat Sheet`}</MDXTag>
<MDXTag name="h2" components={components} props={{"id":"mongo-1-mongo-install"}}>{`MONGO-1: Mongo Install`}</MDXTag>
<MDXTag name="pre" components={components}><MDXTag name="code" components={components} parentName="pre" props={{}}>{`npm install mongoose --save

// to run

mongod // starts the daemon
mongo // check out the documents
`}</MDXTag></MDXTag>
<MDXTag name="h2" components={components} props={{"id":"mongo-2-mongo-shell"}}>{`MONGO-2: Mongo Shell`}</MDXTag>
<MDXTag name="pre" components={components}><MDXTag name="code" components={components} parentName="pre" props={{}}>{`show dbs — // display the databases
use bookworm — // specify the database you're going to work with
show collections — // shows the document collections for the selected database
db.users.find() — // display all the documents in the users collection
db.users.find().pretty() — // nicer format for output documents within the shell
db.users.drop() — // remove the users collection from the current database
`}</MDXTag></MDXTag>
<MDXTag name="h2" components={components} props={{"id":"mongo-3-mongo-sample-usage"}}>{`MONGO-3: Mongo Sample Usage`}</MDXTag>
<MDXTag name="pre" components={components}><MDXTag name="code" components={components} parentName="pre" props={{"className":"language-javascript"}}>{`// models > user.js

var mongoose = require('mongoose');

var UserSchema = new mongoose.Schema({
  email: {
    type: String,
    required: true,
    trim: true,
    unique: true,
  },
  name: {
    type: String,
    required: true,
    trim: true,
  },
  favoriteBook: {
    type: String,
    required: true,
    trim: true
  },
  password: {
    type: String,
    required: true
  }
});

var User = mongoose.model('User', UserSchema);
module.exports = User;

// in app.js

var User = require('../models/user');
`}</MDXTag></MDXTag>
<MDXTag name="h2" components={components} props={{"id":"mongo-4-building-an-example-schema-in-the-mock-folder"}}>{`MONGO-4: Building an Example Schema in the `}<MDXTag name="inlineCode" components={components} parentName="h2">{`mock`}</MDXTag>{` folder`}</MDXTag>
<MDXTag name="pre" components={components}><MDXTag name="code" components={components} parentName="pre" props={{}}>{`// mock/example.js

import mongoose from 'mongoose';

const schema = new mongoose.Schema({
  question: String,
  answer: String,
  completed: Boolean
});

export const model = mongoose.model('Layout', schema);
`}</MDXTag></MDXTag>
<MDXTag name="h2" components={components} props={{"id":"mongo-5-accessing-the-schema-results"}}>{`MONGO-5: Accessing the Schema results`}</MDXTag>
<MDXTag name="pre" components={components}><MDXTag name="code" components={components} parentName="pre" props={{}}>{`// in a route file
var express = require('express');
var router = express.Router();

import {ExampleModel} from '../models/example';

/* GET users listing. */
router.get('/', (req, res) => {
  console.log(ExampleModel);
  ExampleModel.find({}, "test string", (err, results) => {
    if (err) {
      return res.status(500).json({message: err.message});  
    }
    res.json({ results: results });
  });
  // res.json({ todos: "todos" });
    // res.send('respond with a resource');
});

module.exports = router;
`}</MDXTag></MDXTag>
           </MDXTag>
  }
}
