  import React from 'react'
  import { MDXTag } from '@mdx-js/tag'


export default class MDXContent extends React.Component {
  constructor(props) {
    super(props)
    this.layout = null
  }
  render() {
    const { components, ...props } = this.props

    return <MDXTag
             name="wrapper"

             components={components}><MDXTag name="h1" components={components} props={{"id":"intro-to-detox-and-expo"}}>{`Intro to Detox and Expo`}</MDXTag>
<MDXTag name="h2" components={components} props={{"id":"links"}}>{`Links`}</MDXTag>
<MDXTag name="p" components={components}><MDXTag name="a" components={components} parentName="p" props={{"href":"https://github.com/wix/detox/blob/master/docs/Guide.Expo.md"}}>{`Setup guide can be found here`}</MDXTag></MDXTag>
<MDXTag name="h2" components={components} props={{"id":"prereqs"}}>{`Prereqs`}</MDXTag>
<MDXTag name="p" components={components}>{`Ensure Node is 8.3.0 or higher.`}</MDXTag>
<MDXTag name="p" components={components}>{`Install `}<MDXTag name="inlineCode" components={components} parentName="p">{`applesimutils`}</MDXTag>{`:`}</MDXTag>
<MDXTag name="pre" components={components}><MDXTag name="code" components={components} parentName="pre" props={{"className":"language-shell"}}>{`brew tap wix/brew
brew install applesimutils
`}</MDXTag></MDXTag>
<MDXTag name="p" components={components}>{`Install detox-cli:`}</MDXTag>
<MDXTag name="pre" components={components}><MDXTag name="code" components={components} parentName="pre" props={{"className":"language-shell"}}>{`npm install -g detox-cli
`}</MDXTag></MDXTag>
<MDXTag name="h2" components={components} props={{"id":"getting-started"}}>{`Getting Started`}</MDXTag>
<MDXTag name="h3" components={components} props={{"id":"1-yarn-add"}}>{`1. Yarn add`}</MDXTag>
<MDXTag name="pre" components={components}><MDXTag name="code" components={components} parentName="pre" props={{}}>{`yarn add detox detox-expo-helpers expo-detox-hook
`}</MDXTag></MDXTag>
<MDXTag name="h3" components={components} props={{"id":"2-add-the-following-to-packagejson"}}>{`2. Add the following to package.json`}</MDXTag>
<MDXTag name="pre" components={components}><MDXTag name="code" components={components} parentName="pre" props={{"className":"language-json"}}>{`"detox": {
  "configurations": {
    "ios.sim": {
      "binaryPath": "bin/Exponent.app",
      "type": "ios.simulator",
      "name": "iPhone 7"
    }
  }
}
`}</MDXTag></MDXTag>
<MDXTag name="p" components={components}>{`…and to the scripts object:`}</MDXTag>
<MDXTag name="pre" components={components}><MDXTag name="code" components={components} parentName="pre" props={{"className":"language-json"}}>{`"scripts": {
    "e2e": "detox test --configuration ios.sim"
}
`}</MDXTag></MDXTag>
<MDXTag name="h3" components={components} props={{"id":"3-download-ipa"}}>{`3. Download IPA`}</MDXTag>
<MDXTag name="p" components={components}>{`Fetch the IPA file `}<MDXTag name="a" components={components} parentName="p" props={{"href":"https://expo.io/tools#client"}}>{`from here`}</MDXTag></MDXTag>
<MDXTag name="h3" components={components} props={{"id":"4-copy-e2e-files"}}>{`4. Copy e2e files`}</MDXTag>
<MDXTag name="p" components={components}>{`Fetch/copy files `}<MDXTag name="a" components={components} parentName="p" props={{"href":"https://github.com/expo/with-detox-tests/tree/master/e2e"}}>{`from Github`}</MDXTag></MDXTag>
<MDXTag name="h2" components={components} props={{"id":"using-detox"}}>{`Using Detox`}</MDXTag>
<MDXTag name="p" components={components}>{`A basic example of incorporating some useful detox calls:`}</MDXTag>
<MDXTag name="pre" components={components}><MDXTag name="code" components={components} parentName="pre" props={{"className":"language-js"}}>{`const { reloadApp } = require('detox-expo-helpers');

describe('Login flow', () => {
  beforeEach(async () => {
    await reloadApp();
  });

  it('should login successfully', async () => {
    await device.reloadReactNative();
    await expect(element(by.id('email'))).toBeVisible();

    await element(by.id('email')).typeText('john@example.com');
    await element(by.id('password')).typeText('123456');
    await element(by.text('Login')).tap();

    await expect(element(by.text('Welcome'))).toBeVisible();
    await expect(element(by.id('email'))).toNotExist();
  });
});
`}</MDXTag></MDXTag>
<MDXTag name="p" components={components}>{`The element can be selected by making the most of using the `}<MDXTag name="inlineCode" components={components} parentName="p">{`testId`}</MDXTag>{` — for example, the following test button has ID `}<MDXTag name="inlineCode" components={components} parentName="p">{`hello_button`}</MDXTag>{`:`}</MDXTag>
<MDXTag name="pre" components={components}><MDXTag name="code" components={components} parentName="pre" props={{"className":"language-js"}}>{`<TouchableOpacity
  testID="hello_button"
  onPress={this.onButtonPress.bind(this, 'Hello')}
>
  <Text style={{ color: 'blue', marginBottom: 20 }}>Say Hello</Text>
</TouchableOpacity>
`}</MDXTag></MDXTag>
<MDXTag name="h2" components={components} props={{"id":"running-the-tests"}}>{`Running the tests`}</MDXTag>
<MDXTag name="p" components={components}>{`Finally, to run the test we can run `}<MDXTag name="inlineCode" components={components} parentName="p">{`yarn start`}</MDXTag>{` in one terminal and `}<MDXTag name="inlineCode" components={components} parentName="p">{`yarn e2e`}</MDXTag>{` on the other to run the `}<MDXTag name="inlineCode" components={components} parentName="p">{`e2e`}</MDXTag>{` script. Note that you need to have live reload disabled, so better to also put the app into production mode before running the e2e tests. This can be done from the terminal following the instructions to set production mode.`}</MDXTag>
<MDXTag name="h2" components={components} props={{"id":"notes"}}>{`Notes`}</MDXTag>
<MDXTag name="p" components={components}>{`You may need to add the `}<MDXTag name="inlineCode" components={components} parentName="p">{`--reuse`}</MDXTag>{` flag to the `}<MDXTag name="inlineCode" components={components} parentName="p">{`e2e`}</MDXTag>{` script. At the time of writing, the current version 0.55 is having issues on iOS with hanging. The requirement at the moment is that you have to open and close the Expo window. `}<MDXTag name="a" components={components} parentName="p" props={{"href":"https://github.com/wix/Detox/issues/917#issuecomment-422396875"}}>{`See this GitHub issue for more info`}</MDXTag>{`.`}</MDXTag>
           </MDXTag>
  }
}
