/* @jsx mdx */
  import React from 'react'
  import { mdx } from '@mdx-js/react'
  /* @jsx mdx */


const makeShortcode = name => function MDXDefaultShortcode(props) {
  console.warn("Component " + name + " was not imported, exported, or provided by MDXProvider as global scope")
  return <div {...props}/>
};

const layoutProps = {

};
const MDXLayout = "wrapper"
export default function MDXContent({
  components,
  ...props
}) {
  return <MDXLayout {...layoutProps} {...props} components={components} mdxType="MDXLayout">
    <h1 {...{
      "id": "webpack-optimisation"
    }}>{`Webpack Optimisation`}</h1>
    <h2 {...{
      "id": "loader-targeting"
    }}>{`Loader targeting`}</h2>
    <p>{`Apply loaders to the minimal number of modules necessary.`}</p>
    <pre><code parentName="pre" {...{
        "className": "language-javascript"
      }}>{`// instead of...
module.exports = {
  //...
  module: {
    rules: [
      {
        test: /\\.js$/,
        loader: 'babel-loader'
      }
    ]
  }
};

// try using the include target
module.exports = {
  //...
  module: {
    rules: [
      {
        test: /\\.js$/,
        include: path.resolve(__dirname, 'src'),
        loader: 'babel-loader'
      }
    ]
  }
};
`}</code></pre>
    <h2 {...{
      "id": "bootstrap"
    }}>{`Bootstrap`}</h2>
    <p>{`Each additional loader/plugin has a bootup time. Try to use as few tools as possible.`}</p>
    <h2 {...{
      "id": "resolving"
    }}>{`Resolving`}</h2>
    <p>{`The following can increase resolving speed:`}</p>
    <ul>
      <li parentName="ul">{`Minimize the number of items in `}<inlineCode parentName="li">{`resolve.modules`}</inlineCode>{`, `}<inlineCode parentName="li">{`resolve.extensions`}</inlineCode>{`, `}<inlineCode parentName="li">{`resolve.mainFiles`}</inlineCode>{`, `}<inlineCode parentName="li">{`resolve.descriptionFiles`}</inlineCode>{`, as they increase the number of filesystem calls.`}</li>
      <li parentName="ul">{`Set `}<inlineCode parentName="li">{`resolve.symlinks: false`}</inlineCode>{` if you don't use symlinks (eg `}<inlineCode parentName="li">{`npm link`}</inlineCode>{` or `}<inlineCode parentName="li">{`yarn link`}</inlineCode>{`).`}</li>
      <li parentName="ul">{`Set `}<inlineCode parentName="li">{`resolve.cacheWithContext: false`}</inlineCode>{` if you use custom resolving plugins, that are not context specific.`}</li>
    </ul>
    <h2 {...{
      "id": "dlls"
    }}>{`Dlls`}</h2>
    <p>{`Use the `}<inlineCode parentName="p">{`DllPlugin`}</inlineCode>{` to move code that is changes less frequently into a separate compilation. This will improve app compilation speed, although it does increase complexity of the build process.`}</p>
    <h2 {...{
      "id": "smaller--faster"
    }}>{`Smaller = Faster`}</h2>
    <p>{`Decrease the total size of the compilation to increase build performance. Try to keep chunks small.`}</p>
    <ul>
      <li parentName="ul">{`Use fewer/smaller libs`}</li>
      <li parentName="ul">{`Use the `}<inlineCode parentName="li">{`SplitChunksPlugin`}</inlineCode>{` in Multi-Page Applications`}</li>
      <li parentName="ul">{`Use the `}<inlineCode parentName="li">{`SplitChunksPlugin`}</inlineCode>{` in `}<inlineCode parentName="li">{`async`}</inlineCode>{` mode in Multi-Page Applications`}</li>
      <li parentName="ul">{`Remove unused code`}</li>
      <li parentName="ul">{`Only compile the part of the code you are currently developing on`}</li>
    </ul>
    <h2 {...{
      "id": "worker-pool"
    }}>{`Worker Pool`}</h2>
    <p>{`The `}<inlineCode parentName="p">{`thread-loader`}</inlineCode>{` can be used to offload expensive loaders to a worker pool.`}</p>
    <blockquote>
      <p parentName="blockquote">{`Don't use too many loaders. There is a boot overhead for the Node.js runtime and the loader. Minimize the module transfers between worker and main process. IPC is expensive.`}</p>
    </blockquote>
    <h2 {...{
      "id": "persistent-cache"
    }}>{`Persistent Cache`}</h2>
    <p>{`Enable persistent caching with the `}<inlineCode parentName="p">{`cache-loader`}</inlineCode>{`. Clear cache directory on "postinstall" in `}<inlineCode parentName="p">{`package.json`}</inlineCode>{`.`}</p>
    <h2 {...{
      "id": "custom-pluginsloaders"
    }}>{`Custom plugins/loaders`}</h2>
    <p>{`Profile them to not introduce a performance problem here.`}</p>
    <h2 {...{
      "id": "incremental-builds"
    }}>{`Incremental Builds`}</h2>
    <p>{`Use watch mode. Specifically Webpack's.`}</p>
    <p>{`If CPU overloads due to poling mode, you can increase the polling interval with `}<inlineCode parentName="p">{`watchOptions.poll`}</inlineCode>{`.`}</p>
    <h2 {...{
      "id": "compile-in-memory"
    }}>{`Compile in Memory`}</h2>
    <p>{`Following utils improve performance by compilin and serving assets in memory rather than writing to disk:`}</p>
    <ul>
      <li parentName="ul"><inlineCode parentName="li">{`webpack-dev-server`}</inlineCode></li>
      <li parentName="ul"><inlineCode parentName="li">{`webpack-hot-middleware`}</inlineCode></li>
      <li parentName="ul"><inlineCode parentName="li">{`webpack-dev-middleware`}</inlineCode></li>
    </ul>
    <h2 {...{
      "id": "devtool"
    }}>{`Devtool`}</h2>
    <p>{`Be aware of the performance differences between the different devtool settings.`}</p>
    <ul>
      <li parentName="ul">{`"eval" has the best performance, but doesn't assist you for transpiled code.`}</li>
      <li parentName="ul">{`The `}<inlineCode parentName="li">{`cheap-source-map`}</inlineCode>{` variants are more performant if you can live with the slightly worse mapping quality.`}</li>
      <li parentName="ul">{`Use a `}<inlineCode parentName="li">{`eval-source-map`}</inlineCode>{` variant for incremental builds.
=> In most cases, `}<inlineCode parentName="li">{`cheap-module-eval-source-map`}</inlineCode>{` is the best option.`}</li>
    </ul>
    <p>{`[TO FINISH => up to https://webpack.js.org/guides/build-performance/#minimal-entry-chunk]`}</p>
    </MDXLayout>;
}

MDXContent.isMDXComponent = true;
