  import React from 'react'
  import { MDXTag } from '@mdx-js/tag'


export default class MDXContent extends React.Component {
  constructor(props) {
    super(props)
    this.layout = null
  }
  render() {
    const { components, ...props } = this.props

    return <MDXTag
             name="wrapper"

             components={components}><MDXTag name="h1" components={components} props={{"id":"styles--base--mixins"}}>{`Styles > Base > Mixins`}</MDXTag>
<MDXTag name="p" components={components}>{`The mixins file is the place to store all of the functional Sass mixins used throughout the project.`}</MDXTag>
<MDXTag name="h2" components={components} props={{"id":"inline-block-list"}}>{`inline-block-list`}</MDXTag>
<MDXTag name="pre" components={components}><MDXTag name="code" components={components} parentName="pre" props={{"className":"language-css"}}>{`// inline-block list
@mixin inline-block-list(\$padding: false) {
    margin: 0;
    padding: 0;
    border: 0;
    @include clearfix;

    li {
        list-style-type: none;
        @include inline-block();
        white-space: nowrap;
        @if \$padding {
            padding: {
                left: \$padding;
                right: \$padding;
            }
        }
    }
}
`}</MDXTag></MDXTag>
<MDXTag name="h2" components={components} props={{"id":"omega-reset"}}>{`omega-reset`}</MDXTag>
<MDXTag name="pre" components={components}><MDXTag name="code" components={components} parentName="pre" props={{"className":"language-css"}}>{`@mixin omega-reset(\$nth) {
    &:nth-child(#{\$nth}) {
        margin-right: flex-gutter();
    }
    &:nth-child(#{\$nth} + 1) {
        clear: none;
    }
}
`}</MDXTag></MDXTag>
<MDXTag name="h2" components={components} props={{"id":"font-size"}}>{`font-size`}</MDXTag>
<MDXTag name="pre" components={components}><MDXTag name="code" components={components} parentName="pre" props={{"className":"language-css"}}>{`@mixin font-size(\$fontSize, \$lineHeight: null) {
    font-size: \$fontSize;
    font-size: rem(\$fontSize) / 0.625;

    @if (\$lineHeight) {
        line-height: em(\$lineHeight, \$fontSize);
    } @else {
        line-height: inherit;
    }
}
`}</MDXTag></MDXTag>
<MDXTag name="h2" components={components} props={{"id":"inline-block"}}>{`inline-block`}</MDXTag>
<MDXTag name="pre" components={components}><MDXTag name="code" components={components} parentName="pre" props={{"className":"language-css"}}>{`// inline-block for old browsers
@mixin inline-block() {
    display: inline-block;
    *zoom: 1;
    *display: inline;
}
`}</MDXTag></MDXTag>
<MDXTag name="h2" components={components} props={{"id":"reset-box-model"}}>{`reset-box-model`}</MDXTag>
<MDXTag name="pre" components={components}><MDXTag name="code" components={components} parentName="pre" props={{"className":"language-css"}}>{`@mixin reset-box-model {
    margin: 0;
    padding: 0;
    border: 0;
}
`}</MDXTag></MDXTag>
<MDXTag name="h2" components={components} props={{"id":"absolute-fill"}}>{`absolute-fill`}</MDXTag>
<MDXTag name="pre" components={components}><MDXTag name="code" components={components} parentName="pre" props={{"className":"language-css"}}>{`@mixin absolute-fill {
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
}
`}</MDXTag></MDXTag>
<MDXTag name="h2" components={components} props={{"id":"headings"}}>{`headings`}</MDXTag>
<MDXTag name="pre" components={components}><MDXTag name="code" components={components} parentName="pre" props={{"className":"language-css"}}>{`@mixin headings(\$from: 1, \$to: 6) {
    @for \$i from \$from through \$to {
        h#{\$i} {
            @content;
        }
    }
}
`}</MDXTag></MDXTag>
<MDXTag name="h2" components={components} props={{"id":"background-image"}}>{`background-image`}</MDXTag>
<MDXTag name="pre" components={components}><MDXTag name="code" components={components} parentName="pre" props={{"className":"language-css"}}>{`@mixin background-image(\$path) {
    background-image: url(../img/#{\$path}.png);
    background-image: linear-gradient(transparent, transparent), url(../img/#{\$path}.svg);
}
`}</MDXTag></MDXTag>
<MDXTag name="h2" components={components} props={{"id":"reset-ul"}}>{`reset-ul`}</MDXTag>
<MDXTag name="pre" components={components}><MDXTag name="code" components={components} parentName="pre" props={{"className":"language-css"}}>{`/* Reset unordered list style */
@mixin reset-ul {
    list-style-type: none;
    margin: 0;
    padding: 0;
    overflow: auto;
}
`}</MDXTag></MDXTag>
<MDXTag name="h2" components={components} props={{"id":"hide-text"}}>{`hide-text`}</MDXTag>
<MDXTag name="pre" components={components}><MDXTag name="code" components={components} parentName="pre" props={{"className":"language-css"}}>{`@mixin hide-text {
    overflow: hidden;
    text-indent: 101%;
    white-space: nowrap;
}
`}</MDXTag></MDXTag>
<MDXTag name="h2" components={components} props={{"id":"_neat-parse-media"}}>{`_`}{`neat-parse-media`}</MDXTag>
<MDXTag name="p" components={components}>{`Swaps neat 2.0 to desktop first`}</MDXTag>
<MDXTag name="pre" components={components}><MDXTag name="code" components={components} parentName="pre" props={{"className":"language-css"}}>{`@function _neat-parse-media(\$media) {
    @if type-of(\$media) == number {
        @return "only screen and (max-width: #{\$media})";
    } @else if type-of(\$media) == string {
        @return "#{\$media}";
    }
}
`}</MDXTag></MDXTag>
<MDXTag name="h2" components={components} props={{"id":"background-color"}}>{`background-color`}</MDXTag>
<MDXTag name="p" components={components}>{`Set default background-color transition.`}</MDXTag>
<MDXTag name="pre" components={components}><MDXTag name="code" components={components} parentName="pre" props={{"className":"language-css"}}>{`@mixin background-color\$color, \$lighten: false) {
    background-color: \$color;
    transition: background-color 600ms ease-in;

    &:hover,
    &:active {
        @if \$lighten {
            background-color: lighten(\$color, 30%);
        } @else {
            background-color: darken(\$color, 30%);
        }
    }
}
