/* @jsx mdx */
  import React from 'react'
  import { mdx } from '@mdx-js/react'
  /* @jsx mdx */


const makeShortcode = name => function MDXDefaultShortcode(props) {
  console.warn("Component " + name + " was not imported, exported, or provided by MDXProvider as global scope")
  return <div {...props}/>
};

const layoutProps = {

};
const MDXLayout = "wrapper"
export default function MDXContent({
  components,
  ...props
}) {
  return <MDXLayout {...layoutProps} {...props} components={components} mdxType="MDXLayout">
    <div id="web1"></div>
    <hr></hr>
    <h2 {...{
      "id": "web-1-webpack---the-why"
    }}>{`WEB-1: Webpack - The Why.`}</h2>
    <p>{`Build Tasks`}</p>
    <ol>
      <li parentName="ol">{`Module loading`}</li>
    </ol>
    <p>{`Looks for `}<inlineCode parentName="p">{`require`}</inlineCode>{` and `}<inlineCode parentName="p">{`import`}</inlineCode>{` statements. This helps prevents errors.`}</p>
    <ol {...{
      "start": 2
    }}>
      <li parentName="ol">{`Concatenation`}</li>
    </ol>
    <p>{`Combining several files into one for loading performance. Minimal HTTP reqs.`}</p>
    <ol {...{
      "start": 3
    }}>
      <li parentName="ol">{`Minification`}</li>
    </ol>
    <p>{`Compressing/extraction unnecessary lines and characters.`}</p>
    <div id="web2"></div>
    <h2 {...{
      "id": "web-2-webpack-config"
    }}>{`WEB-2: Webpack Config`}</h2>
    <p>{`Webpack is an opinionated tool. If you stick to industry standard, you won't need to do much config.`}</p>
    <p>{`Config is done in `}<inlineCode parentName="p">{`webpack.config.js`}</inlineCode></p>
    <p>{`For now, we'll just look at 3 configuration properties:`}</p>
    <pre><code parentName="pre" {...{}}>{`1. entry
2. output
3. module
`}</code></pre>
    <p>{`The file looks like so:`}</p>
    <pre><code parentName="pre" {...{}}>{`var HtmlWebpackPlugin = require("html-webpack-plugin");

// ensure all the webpack dependencies are installed
var webpackConfig = {
    entry: "./src/app.js",
    output: {
        path: "build",
        filename: "bundle.js"
    },
    module: {
        loaders: [
            {
                // used to ensure js loads
                loader: "babel-loader",
                test: /\\.js$/
            }
        ]
    }
}
`}</code></pre>
    <p><inlineCode parentName="p">{`entry`}</inlineCode>{` can be specified with a string for a single entry point or an object for multiple entry points.`}</p>
    <p><inlineCode parentName="p">{`output`}</inlineCode>{` used for building for production - which folder to place your build in and what file to have the bundled file.`}</p>
    <p><inlineCode parentName="p">{`module`}</inlineCode>{` many properties that are used to config webpack. Loaders will be used for using installed loaders.`}</p>
    <div id="web3"></div>
    <hr></hr>
    <h2 {...{
      "id": "web-3-building-an-app"
    }}>{`WEB-3: Building an app`}</h2>
    <p>{`Webpack has it's own CLI tool.`}</p>
    <p>{`In the example file from the TH lesson, index.js is the entry point and index.ejs is then used to inject JS into a HTML file.`}</p>
    <p>{`We're going to define a npm script for webpack.`}</p>
    <pre><code parentName="pre" {...{}}>{`// in package.json
...
"script": {
        "build": "webpack --optimize-minimize"
}
...
`}</code></pre>
    <div id="web4"></div>
    <hr></hr>
    <h2 {...{
      "id": "web-4-webpack-dev-server"
    }}>{`WEB-4: Webpack Dev Server`}</h2>
    <p><inlineCode parentName="p">{`webpack-dev-server`}</inlineCode>{` is an npm file you can use for serving, however you can just flag `}<inlineCode parentName="p">{`webpack --watch`}</inlineCode>{` and run from MAMP if you want.`}</p>
    <div id="web5"></div>
    <hr></hr>
    <h2 {...{
      "id": "web-5-adding-styles"
    }}>{`WEB-5: Adding Styles`}</h2>
    <p>{`Loaders are extensions that enable more webpack config options.`}</p>
    <div id="web6"></div>
    <hr></hr>
    <h2 {...{
      "id": "web-6-loaders"
    }}>{`WEB-6: Loaders`}</h2>
    <p>{`Source maps, fonts etc.`}</p>
    <p>{`We can pipe loaders.`}</p>
    </MDXLayout>;
}

MDXContent.isMDXComponent = true;
