/* @jsx mdx */
  import React from 'react'
  import { mdx } from '@mdx-js/react'
  /* @jsx mdx */


const makeShortcode = name => function MDXDefaultShortcode(props) {
  console.warn("Component " + name + " was not imported, exported, or provided by MDXProvider as global scope")
  return <div {...props}/>
};

const layoutProps = {

};
const MDXLayout = "wrapper"
export default function MDXContent({
  components,
  ...props
}) {
  return <MDXLayout {...layoutProps} {...props} components={components} mdxType="MDXLayout">
    <h1 {...{
      "id": "java-spring-stripe-configuration"
    }}>{`Java Spring Stripe Configuration`}</h1>
    <p>{`In this short example, we are going to "Hello, World!" a Stripe charge!`}</p>
    <h2 {...{
      "id": "resources"
    }}>{`Resources`}</h2>
    <ol>
      <li parentName="ol"><a parentName="li" {...{
          "href": "https://docs.dennisokeeffe.com/manual-java-gradle-intro"
        }}>{`Gradle Intro Docs`}</a></li>
      <li parentName="ol"><a parentName="li" {...{
          "href": "https://spring.io/guides/gs/rest-service/"
        }}>{`Spring Guide to Rest Services`}</a></li>
      <li parentName="ol"><a parentName="li" {...{
          "href": "https://stripe.com/docs/api"
        }}>{`Stripe API`}</a></li>
      <li parentName="ol"><a parentName="li" {...{
          "href": "https://stackabuse.com/stripe-integration-with-java-spring-for-payment-processing/"
        }}>{`Stripe with Spring`}</a></li>
      <li parentName="ol"><a parentName="li" {...{
          "href": "https://github.com/stripe/stripe-java#usage"
        }}>{`Stripe Github Java Usage`}</a></li>
      <li parentName="ol"><a parentName="li" {...{
          "href": "https://docs.spring.io/spring-framework/docs/current/javadoc-api/org/springframework/http/ResponseEntity.html"
        }}>{`Spring ResponseEntity`}</a></li>
      <li parentName="ol"><a parentName="li" {...{
          "href": "https://www.tutorialspoint.com/spring_boot/spring_boot_service_components.htm"
        }}>{`Tutorials Point: Service Components`}</a></li>
      <li parentName="ol"><a parentName="li" {...{
          "href": "https://gist.github.com/fteychene/1e5c30fc86d7623084d0"
        }}>{`Basic Spring Boot Gitignore file`}</a></li>
    </ol>
    <h2 {...{
      "id": "setup"
    }}>{`Setup`}</h2>
    <pre><code parentName="pre" {...{
        "className": "language-shell"
      }}>{`mkdir hello-spring-stripe && cd hello-spring-stripe
mkdir -p src/main/java/stripe
touch build.gradle src/main/java/stripe/StripeCharge.java src/main/java/stripe/StripeChageController.java src/main/java/stripe/Application.java .env
# init gradle
gradle wrapper
`}</code></pre>
    <h2 {...{
      "id": "setup-buildgradle"
    }}>{`Setup build.gradle`}</h2>
    <p>{`Add the following for Stripe + Spring:`}</p>
    <pre><code parentName="pre" {...{
        "className": "language-java"
      }}>{`buildscript {
    repositories {
        mavenCentral()
    }
    dependencies {
        classpath("org.springframework.boot:spring-boot-gradle-plugin:2.2.1.RELEASE")
    }
}

apply plugin: 'java'
apply plugin: 'eclipse'
apply plugin: 'idea'
apply plugin: 'org.springframework.boot'
apply plugin: 'io.spring.dependency-management'

bootJar {
    baseName = 'hello-spring-jar'
    version =  '0.1.0'
}

repositories {
    mavenCentral()
}

sourceCompatibility = 1.8
targetCompatibility = 1.8

dependencies {
    compile("com.stripe:stripe-java:16.5.1")
    compile("org.springframework.boot:spring-boot-starter-web")
    compile("io.github.cdimascio:java-dotenv:5.1.3")
    testCompile('org.springframework.boot:spring-boot-starter-test')
}
`}</code></pre>
    <h2 {...{
      "id": "set-up-dotenv-file"
    }}>{`Set up dotenv file`}</h2>
    <p>{`We are using a local dotenv file for this "Hello, World!" level example.`}</p>
    <pre><code parentName="pre" {...{
        "className": "language-shell"
      }}>{`# required
SK_TEST_KEY=sk_...
# not required
PK_TEST_KEY=pk_...
`}</code></pre>
    <h2 {...{
      "id": "setting-up-the-main-application"
    }}>{`Setting up the main application`}</h2>
    <p>{`In `}<inlineCode parentName="p">{`src/main/java/stripe/Application.java`}</inlineCode>{`, add the following:`}</p>
    <pre><code parentName="pre" {...{
        "className": "language-java"
      }}>{`package stripecharge;

import org.springframework.boot.SpringApplication;
import org.springframework.boot.autoconfigure.SpringBootApplication;

@SpringBootApplication
public class Application {

  public static void main(String[] args) {
    SpringApplication.run(Application.class, args);
  }
}
`}</code></pre>
    <h2 {...{
      "id": "setting-up-the-model"
    }}>{`Setting up the model`}</h2>
    <p>{`In `}<inlineCode parentName="p">{`src/main/java/stripe/StripeCharge.java`}</inlineCode>{`:`}</p>
    <pre><code parentName="pre" {...{
        "className": "language-java"
      }}>{`package stripecharge;

import java.util.HashMap;
import java.util.Map;

public class StripeCharge {

  private final long amount;
  private final String receiptEmail;
  private final String source;
  private final String currency;

  public StripeCharge(long amount, String receiptEmail) {
    this.amount = amount;
    this.source = "tok_visa";
    this.currency = "usd";
    this.receiptEmail = receiptEmail;
  }

  public Map<String, Object> getCharge() {
    Map<String, Object> params = new HashMap<String, Object>();
    params.put("amount", this.amount);
    params.put("currency", this.currency);
    // source should obtained with Stripe.js
    params.put("source", this.source);
    params.put(
      "description",
      "My First Test Charge (created for API docs)"
    );
    params.put("receipt_email",this.receiptEmail);
    return params;
  }
}
`}</code></pre>
    <h2 {...{
      "id": "setting-up-the-controller"
    }}>{`Setting up the controller`}</h2>
    <p>{`In `}<inlineCode parentName="p">{`src/main/java/stripe/StripeChargeController.java`}</inlineCode>{`:`}</p>
    <pre><code parentName="pre" {...{
        "className": "language-java"
      }}>{`package stripecharge;

// NOTE: RequestMapping + RequestParam not required for this demo
// but kept in for reference purposes if wanted to play around later
import java.util.concurrent.atomic.AtomicLong;
import org.springframework.web.bind.annotation.RequestMapping;
import org.springframework.web.bind.annotation.PostMapping;
import org.springframework.web.bind.annotation.RequestParam;
import org.springframework.web.bind.annotation.RequestBody;
import org.springframework.http.ResponseEntity;
import org.springframework.http.HttpStatus;
import org.springframework.web.bind.annotation.RestController;
import io.github.cdimascio.dotenv.Dotenv;
import com.stripe.Stripe;
import com.stripe.exception.StripeException;
import com.stripe.model.Charge;
import com.stripe.net.RequestOptions;

@RestController
@RequestMapping("/api")
public class StripeChargeController {

  @PostMapping("/charge")
  public ResponseEntity<String> createCharge(@RequestBody StripeCharge stripeCharge) {
    try {
      // for demonstrations sake, using .env file
      Dotenv dotenv = Dotenv.load();

      // creating the charge
      Stripe.apiKey = dotenv.get("SK_TEST_KEY");
      Charge charge = Charge.create(stripeCharge.getCharge());
      System.out.println(charge);
      return new ResponseEntity<String>("Success", HttpStatus.CREATED);
    } catch (StripeException e) {
      e.printStackTrace();
      return new ResponseEntity<String>("Failure", HttpStatus.INTERNAL_SERVER_ERROR);

    }
  }
}
`}</code></pre>
    <h2 {...{
      "id": "build-run-and-ping"
    }}>{`Build, Run and Ping`}</h2>
    <p>{`Run `}<inlineCode parentName="p">{`./gradlew build`}</inlineCode>{` to build to project or `}<inlineCode parentName="p">{`./gradlew bootRun`}</inlineCode>{` to build and run.`}</p>
    <p>{`Once the project is running, we can ping `}<inlineCode parentName="p">{`http POST http://localhost:8080/api/charge amount:=200 receiptEmail=hello@example.com`}</inlineCode>{` (using HTTPie) and we get a response like the following:`}</p>
    <pre><code parentName="pre" {...{
        "className": "language-s"
      }}>{`HTTP/1.1 201
Content-Length: 7
Content-Type: application/json;charset=UTF-8
Date: Sun, 12 Jan 2020 03:33:10 GMT

Success
`}</code></pre>
    <p>{`Check your Stripe Dashboard under `}<inlineCode parentName="p">{`Developer > Events`}</inlineCode>{` and you'll see we have successfully made a payment! Hooray!`}</p>
    </MDXLayout>;
}

MDXContent.isMDXComponent = true;
