  import React from 'react'
  import { mdx } from '@mdx-js/react'
  /* @jsx mdx */


const makeShortcode = name => function MDXDefaultShortcode(props) {
  console.warn("Component " + name + " was not imported, exported, or provided by MDXProvider as global scope")
  return <div {...props}/>
};

const layoutProps = {

};
const MDXLayout = "wrapper"
export default function MDXContent({
  components,
  ...props
}) {
  return <MDXLayout {...layoutProps} {...props} components={components} mdxType="MDXLayout">
    <h1 {...{
      "id": "using-stripe-with-node"
    }}>{`Using Stripe with Node`}</h1>
    {
      /* TOC */
    }
    <ul>
      <li parentName="ul"><a parentName="li" {...{
          "href": "#using-stripe-with-node"
        }}>{`Using Stripe with Node`}</a><ul parentName="li">
          <li parentName="ul"><a parentName="li" {...{
              "href": "#prereqs"
            }}>{`Prereqs`}</a></li>
          <li parentName="ul"><a parentName="li" {...{
              "href": "#testing-with-express"
            }}>{`Testing with Express`}</a></li>
        </ul></li>
    </ul>
    {
      /* /TOC */
    }
    <h2 {...{
      "id": "prereqs"
    }}>{`Prereqs`}</h2>
    <ol>
      <li parentName="ol">{`Sign up`}</li>
      <li parentName="ol"><inlineCode parentName="li">{`yarn add stripe`}</inlineCode>{` to your Node.js Project`}</li>
      <li parentName="ol">{`Build out a server`}</li>
    </ol>
    <h2 {...{
      "id": "testing-with-express"
    }}>{`Testing with Express`}</h2>
    <p>{`Configure the .env file and upload`}</p>
    <p><a parentName="p" {...{
        "href": "https://github.com/stripe/stripe-node"
      }}>{`Node Stripe Github`}</a></p>
    <pre><code parentName="pre" {...{
        "className": "language-javascript"
      }}>{`const stripe = require('stripe')(process.env.STRIPE_SECRET_KEY);

/* GET home page. */
module.exports = function(app) {
    app.get('/', function(req, res) {
        res.send('Server is healthy');
    });

    app.post('/', function(req, res) {
        console.log(req.body);
        // Create a new customer and then a new charge for that customer:
        stripe.customers
            .create({
                email: 'foo-customer@example.com'
            })
            .then(function(customer) {
                console.log(customer);
                return stripe.customers.createSource(customer.id, {
                    source: {
                        object: 'card',
                        exp_month: 10,
                        exp_year: 2018,
                        number: '4242 4242 4242 4242',
                        cvc: 100
                    }
                });
            })
            .then(function(source) {
                return stripe.charges.create({
                    amount: 1600,
                    currency: 'aud',
                    customer: source.customer
                });
            })
            .then(function(charge) {
                // New charge created on a new customer
                console.log(charge);
            })
            .catch(function(err) {
                // Deal with an error
                console.log(err);
            });

        res.send('Post requests are healthy');
    });
};
`}</code></pre>
    </MDXLayout>;
}

MDXContent.isMDXComponent = true;
