  import React from 'react'
  import { MDXTag } from '@mdx-js/tag'


export default class MDXContent extends React.Component {
  constructor(props) {
    super(props)
    this.layout = null
  }
  render() {
    const { components, ...props } = this.props

    return <MDXTag
             name="wrapper"

             components={components}><MDXTag name="hr" components={components}></MDXTag>
<MDXTag name="p" components={components}>{`Author: Dennis O'Keeffe`}</MDXTag>
<MDXTag name="h2" components={components} props={{"id":"section-setup"}}>{`Section: Setup`}</MDXTag>
<MDXTag name="h1" components={components} props={{"id":"adding-sass-to-webpack"}}>{`Adding SASS to Webpack`}</MDXTag>
<MDXTag name="h2" components={components} props={{"id":"steps"}}>{`Steps`}</MDXTag>
<MDXTag name="ol" components={components}>
<MDXTag name="li" components={components} parentName="ol">{`Install the appropraite npm packages`}</MDXTag>
<MDXTag name="li" components={components} parentName="ol">{`Eject create-react-app`}</MDXTag>
<MDXTag name="li" components={components} parentName="ol">{`Update `}<MDXTag name="inlineCode" components={components} parentName="li">{`webpack.config`}</MDXTag>{` (check webpack.config)`}</MDXTag>
</MDXTag>
<MDXTag name="h3" components={components} props={{"id":"step-one"}}>{`Step One`}</MDXTag>
<MDXTag name="pre" components={components}><MDXTag name="code" components={components} parentName="pre" props={{}}>{`yarn add sass-loader node-sass-magic-importer resolve-url-loader node-sass --dev
`}</MDXTag></MDXTag>
<MDXTag name="h3" components={components} props={{"id":"step-two"}}>{`Step Two`}</MDXTag>
<MDXTag name="p" components={components}>{`Ensure you have updated git first.`}</MDXTag>
<MDXTag name="pre" components={components}><MDXTag name="code" components={components} parentName="pre" props={{}}>{`npm run eject
`}</MDXTag></MDXTag>
<MDXTag name="h3" components={components} props={{"id":"step-three"}}>{`Step Three`}</MDXTag>
<MDXTag name="p" components={components}>{`Make sure you exclude .scss from the file-loader.`}</MDXTag>
<MDXTag name="p" components={components}>{`Example for `}<MDXTag name="inlineCode" components={components} parentName="p">{`webpack.config.dev.js`}</MDXTag>{`:`}</MDXTag>
<MDXTag name="pre" components={components}><MDXTag name="code" components={components} parentName="pre" props={{"className":"language-javascript"}}>{`// Top of the file
