  import React from 'react'
  import { MDXTag } from '@mdx-js/tag'


export default class MDXContent extends React.Component {
  constructor(props) {
    super(props)
    this.layout = null
  }
  render() {
    const { components, ...props } = this.props

    return <MDXTag
             name="wrapper"

             components={components}><MDXTag name="h1" components={components} props={{"id":"react-native-with-css-and-scss"}}>{`React Native with CSS and SCSS`}</MDXTag>
<MDXTag name="h2" components={components} props={{"id":"react-native-to-cssscss-node-package"}}>{`React Native to CSS/SCSS Node Package`}</MDXTag>
<MDXTag name="p" components={components}><MDXTag name="a" components={components} parentName="p" props={{"href":"https://github.com/sabeurthabti/react-native-css"}}>{`This is the link to the package`}</MDXTag></MDXTag>
<MDXTag name="p" components={components}>{`This package converts css/scss to a .js file that we can use in the react-native component/container files.`}</MDXTag>
<MDXTag name="p" components={components}><MDXTag name="strong" components={components} parentName="p">{`Instructions`}</MDXTag></MDXTag>
<MDXTag name="p" components={components}>{`Install globally `}<MDXTag name="inlineCode" components={components} parentName="p">{`npm install react-native-css -g`}</MDXTag></MDXTag>
<MDXTag name="p" components={components}>{`Now we can run `}<MDXTag name="inlineCode" components={components} parentName="p">{`react-native-css -i INPUT_CSS_FILE -o OUTPUT_JS_FILE --watch`}</MDXTag>{` - check the .md`}</MDXTag>
<MDXTag name="p" components={components}><MDXTag name="strong" components={components} parentName="p">{`! IMPORTANT: Rules for the SASS File`}</MDXTag></MDXTag>
<MDXTag name="p" components={components}>{`The target classes `}<MDXTag name="em" components={components} parentName="p">{`NEED be written in camel case`}</MDXTag></MDXTag>
<MDXTag name="p" components={components}><MDXTag name="strong" components={components} parentName="p">{`Basic Example: style.css`}</MDXTag></MDXTag>
<MDXTag name="pre" components={components}><MDXTag name="code" components={components} parentName="pre" props={{}}>{`.container {
        margin-top: 75;
        align-items: center;
}

.image {
    width: 107;
    height: 165;
    padding: 10;
}

.descriptionCamelCaseExample {
    padding: 10;
    font-size: 25;
    color: #656565;
}
`}</MDXTag></MDXTag>
<MDXTag name="p" components={components}>{`If we run `}<MDXTag name="inlineCode" components={components} parentName="p">{`react-native-css -i ./style.css -o ./style.js --watch`}</MDXTag></MDXTag>
<MDXTag name="p" components={components}>{`The following will be in style.js`}</MDXTag>
<MDXTag name="pre" components={components}><MDXTag name="code" components={components} parentName="pre" props={{}}>{`module.exports = require('react-native').StyleSheet.create({    
    "container": {
        "marginTop":75,
        "alignItems":"center"
        },
    "image": {
        "width":107,
        "height":165,
        "padding":10
        },
    "descriptionCamelCaseExample": {
        "padding":10,
        "fontSize":25,
        "color":"#656565"}
    });
`}</MDXTag></MDXTag>
<MDXTag name="p" components={components}>{`In the respective component js file, you will now need to import this.`}</MDXTag>
<MDXTag name="pre" components={components}><MDXTag name="code" components={components} parentName="pre" props={{}}>{`'use strict';

// var React = require('react-native');
import React, { Component } from 'react';
import styles from './style.js';

import {
    StyleSheet,
    Text,
    View,
    Image
} from 'react-native';

class ExampleComponentOrContainer extends Component {
    render() {

        // example variable declarations
        // not directly relevant to explanation
        var book = this.props.book;
        var imageURI = (typeof book.volumeInfo.imageLinks !== 'undefined') ? book.volumeInfo.imageLinks.thumbnail : '';
        var description = (typeof book.volumeInfo.description !== 'undefined') ? book.volumeInfo.description : '';

        return (

            // use style = {styles.<class>}
            // to apply the styles

            <View style={styles.container}>
                <Image style={styles.image} source={{uri: imageURI}} />
                <Text style={styles.descriptionCamelCaseExample}>{description}</Text>
            </View>
        );
    }
}

export { BookDetail };
`}</MDXTag></MDXTag>
<MDXTag name="p" components={components}>{`Happy days!`}</MDXTag>
           </MDXTag>
  }
}
