/* @jsx mdx */
  import React from 'react'
  import { mdx } from '@mdx-js/react'
  /* @jsx mdx */


const makeShortcode = name => function MDXDefaultShortcode(props) {
  console.warn("Component " + name + " was not imported, exported, or provided by MDXProvider as global scope")
  return <div {...props}/>
};

const layoutProps = {

};
const MDXLayout = "wrapper"
export default function MDXContent({
  components,
  ...props
}) {
  return <MDXLayout {...layoutProps} {...props} components={components} mdxType="MDXLayout">
    <p>{`########`}</p>
    <p>{`! CHECK THE READ ME BEFORE ANYTHING ELSE`}</p>
    <h3 {...{
      "id": "1-install-bower"
    }}>{`1. Install Bower`}</h3>
    <p>{`bower install`}</p>
    <h3 {...{
      "id": "2-install-node-dependencies"
    }}>{`2. Install Node Dependencies`}</h3>
    <p>{`npm install`}</p>
    <h3 {...{
      "id": "3-install-composer-dependencies"
    }}>{`3. Install Composer Dependencies`}</h3>
    <p>{`composer install	// if others needed, check and manually move into folder in wp-content`}</p>
    <h3 {...{
      "id": "4-copy-across-wordpress-files"
    }}>{`4. Copy Across WordPress Files`}</h3>
    <p>{`Locate the folder in finder, copy the files and paste them at the root of the project`}</p>
    <h3 {...{
      "id": "5-set-mamp-to-the-correct-folder"
    }}>{`5. Set MAMP to the correct folder`}</h3>
    <p>{`Create the Database in Sequel Pro`}</p>
    <h3 {...{
      "id": "6-create-database"
    }}>{`6. Create Database`}</h3>
    <p>{`Create the Database in Sequel Pro`}</p>
    <h3 {...{
      "id": "7-open-the-localhost-and-begin-the-wp-set-up"
    }}>{`7. Open the Localhost and begin the WP set up`}</h3>
    <p>{`! Ensure that the database is created in Sequel Pro`}</p>
    <p>{`Set the database field to the database name
Set username and password to what is set for MAMP
Run the Install`}</p>
    <h3 {...{
      "id": "8-open-the-localhost-and-begin-the-wp-set-up"
    }}>{`8. Open the Localhost and begin the WP set up`}</h3>
    <h3 {...{
      "id": "9-ensure-that-any-other-filesplugins-required-in-the-correct-folder-in-reference-to-3"
    }}>{`9. Ensure that any other files/plugins required in the correct folder (in reference to 3)`}</h3>
    <h3 {...{
      "id": "10-gulp-watch---ensure-that-the-gulpfilejs-is-all-sweet"
    }}>{`10. gulp watch - ensure that the gulpfile.js is all sweet!`}</h3>
    <h3 {...{
      "id": "11-ensure-the-correct-plugins-are-active"
    }}>{`11. Ensure the correct plugins are active`}</h3>
    <h3 {...{
      "id": "12-active-the-appropriate-theme"
    }}>{`12. Active the appropriate theme`}</h3>
    <p>{`// `}<strong parentName="p"><strong parentName="strong"><strong parentName="strong">{`*`}</strong></strong></strong></p>
    <p>{`OTHER FILES`}</p>
    <p>{`/+++++++++++++++++/`}</p>
    <p>{`acf-json files`}</p>
    <ul>
      <li parentName="ul">{`used to create the custom fields in WP admin!`}</li>
    </ul>
    <p>{`/+++++++++++++++++/`}</p>
    <p>{`CPT-UI (custom post types)`}</p>
    <p>{`CPT UI controls the post types themselves.
Can be used to import the data.`}</p>
    <ul>
      <li parentName="ul">{`this will create the acf-json files`}</li>
    </ul>
    <p>{`// `}<strong parentName="p"><strong parentName="strong"><strong parentName="strong">{`*`}</strong></strong></strong></p>
    <p>{`TROUBLESHOOTING`}</p>
    <p>{`/+++++++++++++++++/`}</p>
    <p>{`!ISSUE - check the correct plugins are installed.`}</p>
    <p>{`Steps:`}</p>
    <p>{`Check the wp_options under the Content tab
Alter the template and stylesheet to work with an earlier version`}</p>
    <p>{`/+++++++++++++++++/`}</p>
    <p>{`!ISSUE - admin password/database not showing`}</p>
    <p>{`Steps:`}</p>
    <p>{`Ensure the database has been set up
Ensure gulp watch has compiled the file
Ensure that the wp_config file is correctly set up for the wordpress admin stuff`}</p>
    </MDXLayout>;
}

MDXContent.isMDXComponent = true;
